import React, { useEffect, useState } from 'react';
import { Wifi, Users, Heart, Sparkles } from 'lucide-react';

const SplashScreen = ({ setCurrentView }) => {
  const [progress, setProgress] = useState(0);
  const [currentMessage, setCurrentMessage] = useState(0);

  const loadingMessages = [
    "Φορτώνει...",
    "Προετοιμάζει την οικογενειακή επικοινωνία...",
    "Συνδέει γενιές...",
    "Σχεδόν έτοιμο!"
  ];

  // Roottly Logo Component με το σωστό design από την εικόνα
  const RoottlyLogo = ({ size = 'xlarge', animated = true }) => {
    return (
      <div className="flex flex-col items-center">
        <div className={`${size === 'xlarge' ? 'w-64 h-48' : 'w-32 h-24'} relative mb-8`}>
          {/* Το logo με τα δύο χαρακτήρια που συνδέονται */}
          <div className="relative w-full h-40">
            {/* Παππούς (αριστερά) - μπλε με γυαλιά */}
            <div className={`absolute left-0 top-0 w-24 h-32 ${animated ? 'animate-bounce' : ''}`} 
                 style={animated ? {animationDelay: '0s', animationDuration: '2s'} : {}}>
              <div className="w-full h-full relative">
                {/* Κεφάλι παππού */}
                <div className="w-20 h-20 bg-blue-500 rounded-full relative">
                  {/* Γυαλιά */}
                  <div className="absolute top-6 left-2 w-16 h-8">
                    <div className="w-6 h-6 border-4 border-gray-800 rounded-full bg-white absolute left-0"></div>
                    <div className="w-6 h-6 border-4 border-gray-800 rounded-full bg-white absolute right-0"></div>
                    <div className="w-2 h-1 bg-gray-800 absolute top-2 left-6"></div>
                  </div>
                  {/* Μάτια */}
                  <div className="absolute top-8 left-4 w-2 h-2 bg-gray-800 rounded-full"></div>
                  <div className="absolute top-8 right-4 w-2 h-2 bg-gray-800 rounded-full"></div>
                  {/* Χαμόγελο */}
                  <div className="absolute top-12 left-6 w-8 h-2 border-b-2 border-gray-800 rounded-full"></div>
                </div>
                {/* Σώμα παππού */}
                <div className="w-16 h-12 bg-blue-400 rounded-b-3xl mx-2 relative">
                  {/* Χέρια που απλώνονται */}
                  <div className="absolute -right-2 top-2 w-8 h-3 bg-blue-400 rounded-full transform rotate-12"></div>
                </div>
              </div>
            </div>

            {/* Εγγόνι (δεξιά) - πορτοκαλί */}
            <div className={`absolute right-0 top-0 w-24 h-32 ${animated ? 'animate-bounce' : ''}`}
                 style={animated ? {animationDelay: '0.5s', animationDuration: '2s'} : {}}>
              <div className="w-full h-full relative">
                {/* Κεφάλι εγγονιού */}
                <div className="w-20 h-20 bg-orange-500 rounded-full relative">
                  {/* Μαλλιά */}
                  <div className="absolute -top-2 left-3 w-14 h-6 bg-orange-600 rounded-t-full"></div>
                  {/* Μάτια */}
                  <div className="absolute top-8 left-4 w-2 h-2 bg-gray-800 rounded-full"></div>
                  <div className="absolute top-8 right-4 w-2 h-2 bg-gray-800 rounded-full"></div>
                  {/* Χαμόγελο */}
                  <div className="absolute top-12 left-6 w-8 h-2 border-b-2 border-gray-800 rounded-full"></div>
                </div>
                {/* Σώμα εγγονιού */}
                <div className="w-16 h-12 bg-orange-400 rounded-b-3xl mx-2 relative">
                  {/* Χέρια που απλώνονται */}
                  <div className="absolute -left-2 top-2 w-8 h-3 bg-orange-400 rounded-full transform -rotate-12"></div>
                </div>
              </div>
            </div>

            {/* Κύματα σύνδεσης μεταξύ τους */}
            <div className="absolute top-16 left-20 right-20 h-8">
              <svg className="w-full h-full" viewBox="0 0 100 40">
                <defs>
                  <linearGradient id="connectionGradient" x1="0%" y1="0%" x2="100%" y2="0%">
                    <stop offset="0%" stopColor="#3B82F6" stopOpacity="0.8"/>
                    <stop offset="50%" stopColor="#8B5CF6" stopOpacity="0.9"/>
                    <stop offset="100%" stopColor="#F97316" stopOpacity="0.8"/>
                  </linearGradient>
                </defs>
                
                {/* Κύματα που συνδέουν τις δύο φιγούρες */}
                <path 
                  d="M0 20 Q25 10 50 20 T100 20" 
                  stroke="url(#connectionGradient)" 
                  strokeWidth="3" 
                  fill="none" 
                  className={animated ? 'animate-pulse' : ''}
                  style={animated ? {animationDuration: '2s'} : {}}
                />
                <path 
                  d="M0 25 Q25 35 50 25 T100 25" 
                  stroke="url(#connectionGradient)" 
                  strokeWidth="2" 
                  fill="none" 
                  className={animated ? 'animate-pulse' : ''}
                  style={animated ? {animationDelay: '0.5s', animationDuration: '2s'} : {}}
                />
                <path 
                  d="M0 15 Q25 5 50 15 T100 15" 
                  stroke="url(#connectionGradient)" 
                  strokeWidth="2" 
                  fill="none" 
                  className={animated ? 'animate-pulse' : ''}
                  style={animated ? {animationDelay: '1s', animationDuration: '2s'} : {}}
                />
              </svg>
            </div>

            {/* Καρδιές που πετούν */}
            {animated && (
              <>
                <div className="absolute top-8 left-12 text-red-400 text-lg animate-ping" style={{animationDelay: '1s'}}>💙</div>
                <div className="absolute top-12 right-12 text-red-400 text-lg animate-ping" style={{animationDelay: '1.5s'}}>🧡</div>
                <div className="absolute top-6 left-1/2 transform -translate-x-1/2 text-red-400 text-sm animate-ping" style={{animationDelay: '2s'}}>❤️</div>
              </>
            )}
          </div>
        </div>
        
        {/* App name and tagline */}
        <div className="text-center">
          <div className={`text-6xl font-bold mb-4 ${animated ? 'animate-pulse' : ''}`}>
            <span className="text-blue-600">Root</span><span className="text-orange-600">tly</span>
          </div>
          <div className="text-2xl text-gray-600 font-medium mb-2">
            Συνδέοντας γενιές
          </div>
          <div className="text-lg text-gray-500 flex items-center justify-center gap-2">
            <Heart className="w-5 h-5 text-red-400" />
            Οικογενειακή επικοινωνία
            <Heart className="w-5 h-5 text-red-400" />
          </div>
        </div>
      </div>
    );
  };

  // Progress animation
  useEffect(() => {
    const progressTimer = setInterval(() => {
      setProgress(prev => {
        if (prev >= 100) {
          clearInterval(progressTimer);
          return 100;
        }
        return prev + 2;
      });
    }, 60);

    return () => clearInterval(progressTimer);
  }, []);

  // Message rotation
  useEffect(() => {
    const messageTimer = setInterval(() => {
      setCurrentMessage(prev => (prev + 1) % loadingMessages.length);
    }, 1000);

    return () => clearInterval(messageTimer);
  }, []);

  // Auto advance after animation
  useEffect(() => {
    const timer = setTimeout(() => {
      setCurrentView('onboarding');
    }, 4000);

    return () => clearTimeout(timer);
  }, [setCurrentView]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-orange-50 flex items-center justify-center p-4">
      <div className="text-center w-full max-w-lg">
        {/* Main logo */}
        <RoottlyLogo size="xlarge" animated={true} />
        
        {/* Loading message */}
        <div className="mt-12 mb-8">
          <p className="text-2xl text-gray-600 font-medium animate-fade-in">
            {loadingMessages[currentMessage]}
          </p>
        </div>
        
        {/* Progress bar */}
        <div className="w-full max-w-md mx-auto mb-8">
          <div className="w-full bg-gray-200 rounded-full h-4 overflow-hidden shadow-inner">
            <div 
              className="h-4 bg-gradient-to-r from-blue-500 to-orange-500 rounded-full transition-all duration-300 ease-out shadow-sm"
              style={{ width: `${progress}%` }}
            >
              <div className="h-full bg-white bg-opacity-30 animate-pulse"></div>
            </div>
          </div>
          <div className="text-center mt-3">
            <span className="text-lg font-semibold text-gray-700">{progress}%</span>
          </div>
        </div>
        
        {/* Feature highlights */}
        <div className="grid grid-cols-3 gap-6 mt-12 opacity-60">
          <div className="text-center">
            <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-2">
              <Users className="w-6 h-6 text-blue-600" />
            </div>
            <p className="text-sm text-gray-600">Οικογένεια</p>
          </div>
          
          <div className="text-center">
            <div className="w-12 h-12 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-2">
              <Wifi className="w-6 h-6 text-green-600" />
            </div>
            <p className="text-sm text-gray-600">Σύνδεση</p>
          </div>
          
          <div className="text-center">
            <div className="w-12 h-12 bg-orange-100 rounded-full flex items-center justify-center mx-auto mb-2">
              <Sparkles className="w-6 h-6 text-orange-600" />
            </div>
            <p className="text-sm text-gray-600">Διασκέδαση</p>
          </div>
        </div>
        
        {/* Version info */}
        <div className="mt-8 text-center">
          <p className="text-sm text-gray-400">
            Έκδοση 1.0.0 • Made with ❤️ in Greece
          </p>
        </div>
      </div>

      {/* Background decorative elements */}
      <div className="fixed inset-0 pointer-events-none overflow-hidden">
        <div className="absolute top-20 left-20 w-32 h-32 bg-blue-200 rounded-full opacity-20 animate-float"></div>
        <div className="absolute top-40 right-32 w-24 h-24 bg-orange-200 rounded-full opacity-20 animate-float" style={{animationDelay: '2s'}}></div>
        <div className="absolute bottom-32 left-40 w-20 h-20 bg-green-200 rounded-full opacity-20 animate-float" style={{animationDelay: '4s'}}></div>
        <div className="absolute bottom-20 right-20 w-28 h-28 bg-purple-200 rounded-full opacity-20 animate-float" style={{animationDelay: '1s'}}></div>
      </div>
    </div>
  );
};

export default SplashScreen;
