import React, { useState } from 'react';
import { ChevronLeft, Video, Camera, Monitor, Play, X } from 'lucide-react';

const Lessons = ({ setCurrentView, t, textSizeClasses, buttonSizeClasses, textSize, language }) => {
  const [selectedLesson, setSelectedLesson] = useState(null);
  
  const lessonContent = {
    el: {
      1: {
        title: "Πώς να κάνετε βιντεοκλήση",
        steps: [
          "1. Βρείτε την εφαρμογή βιντεοκλήσης στο τηλέφωνό σας",
          "2. Πατήστε στο όνομα της επαφής που θέλετε να καλέσετε",
          "3. Πατήστε το εικονίδιο της κάμερας 📹",
          "4. Περιμένετε να απαντήσει ο συνομιλητής σας",
          "5. Για να κλείσετε, πατήστε το κόκκινο κουμπί"
        ],
        tips: [
          "💡 Κρατήστε το τηλέφωνο σταθερά μπροστά σας",
          "💡 Βεβαιωθείτε ότι έχετε καλό φωτισμό",
          "💡 Μιλήστε καθαρά και κοντά στο μικρόφωνο"
        ]
      },
      2: {
        title: "Πώς να στείλετε φωτογραφία",
        steps: [
          "1. Ανοίξτε την εφαρμογή μηνυμάτων",
          "2. Επιλέξτε την επαφή",
          "3. Πατήστε το εικονίδιο της κάμερας ή της εικόνας",
          "4. Επιλέξτε φωτογραφία από τη συλλογή σας",
          "5. Πατήστε 'Αποστολή'"
        ],
        tips: [
          "💡 Ελέγξτε την φωτογραφία πριν την στείλετε",
          "💡 Μπορείτε να προσθέσετε μήνυμα μαζί με τη φωτογραφία",
          "💡 Περιμένετε να ολοκληρωθεί η αποστολή"
        ]
      },
      3: {
        title: "Πώς να χρησιμοποιείτε το διαδίκτυο με ασφάλεια",
        steps: [
          "1. Μην δίνετε προσωπικά στοιχεία σε αγνώστους",
          "2. Μην πατάτε σε ύποπτους συνδέσμους",
          "3. Χρησιμοποιείτε ισχυρούς κωδικούς",
          "4. Ενημερώνετε τακτικά τις εφαρμογές σας",
          "5. Ρωτήστε αν έχετε αμφιβολίες"
        ],
        tips: [
          "💡 Αν κάτι φαίνεται πολύ καλό για να είναι αληθινό, μάλλον είναι",
          "💡 Κρατήστε αντίγραφα ασφαλείας των φωτογραφιών σας",
          "💡 Μην ανοίγετε email από αγνώστους"
        ]
      }
    },
    en: {
      1: {
        title: "How to make a video call",
        steps: [
          "1. Find the video call app on your phone",
          "2. Tap on the contact name you want to call",
          "3. Tap the camera icon 📹",
          "4. Wait for the other person to answer",
          "5. To end, tap the red button"
        ],
        tips: [
          "💡 Hold your phone steady in front of you",
          "💡 Make sure you have good lighting",
          "💡 Speak clearly and close to the microphone"
        ]
      },
      2: {
        title: "How to send a photo",
        steps: [
          "1. Open the messages app",
          "2. Select the contact",
          "3. Tap the camera or image icon",
          "4. Choose a photo from your gallery",
          "5. Tap 'Send'"
        ],
        tips: [
          "💡 Check the photo before sending",
          "💡 You can add a message with the photo",
          "💡 Wait for the upload to complete"
        ]
      },
      3: {
        title: "How to use the internet safely",
        steps: [
          "1. Don't give personal information to strangers",
          "2. Don't click on suspicious links",
          "3. Use strong passwords",
          "4. Keep your apps updated",
          "5. Ask if you have doubts"
        ],
        tips: [
          "💡 If something seems too good to be true, it probably is",
          "💡 Keep backups of your photos",
          "💡 Don't open emails from strangers"
        ]
      }
    }
  };
  
  const lessons = [
    { id: 1, title: t.lesson1, icon: <Video className="w-8 h-8" />, color: 'bg-blue-500' },
    { id: 2, title: t.lesson2, icon: <Camera className="w-8 h-8" />, color: 'bg-green-500' },
    { id: 3, title: t.lesson3, icon: <Monitor className="w-8 h-8" />, color: 'bg-purple-500' }
  ];
  
  if (selectedLesson) {
    const content = lessonContent[language][selectedLesson];
    return (
      <div className="min-h-screen bg-gradient-to-br from-indigo-50 to-blue-50">
        <header className="bg-gradient-to-r from-indigo-500 to-blue-500 text-white p-6 shadow-lg">
          <div className="flex items-center gap-4">
            <button
              onClick={() => setSelectedLesson(null)}
              className="text-white hover:bg-white/20 rounded-lg p-2 transition-colors"
            >
              <ChevronLeft className="w-6 h-6" />
            </button>
            <h1 className={`${textSizeClasses[textSize]} font-bold flex-1`}>
              {content.title}
            </h1>
          </div>
        </header>
        
        <main className="p-4">
          <div className="max-w-2xl mx-auto">
            <div className="bg-white rounded-xl shadow-md p-6 mb-4">
              <h2 className={`${textSizeClasses[textSize]} font-bold mb-4 text-gray-800`}>
                Βήματα:
              </h2>
              <div className="space-y-3">
                {content.steps.map((step, index) => (
                  <div key={index} className="flex items-start gap-3">
                    <div className="w-8 h-8 bg-blue-100 rounded-full flex items-center justify-center flex-shrink-0">
                      <span className="text-blue-600 font-bold">{index + 1}</span>
                    </div>
                    <p className={`${textSizeClasses[textSize]} text-gray-700`}>{step}</p>
                  </div>
                ))}
              </div>
            </div>
            
            <div className="bg-yellow-50 rounded-xl shadow-md p-6">
              <h3 className={`${textSizeClasses[textSize]} font-bold mb-4 text-gray-800`}>
                Χρήσιμες συμβουλές:
              </h3>
              <div className="space-y-2">
                {content.tips.map((tip, index) => (
                  <p key={index} className={`${textSizeClasses[textSize]} text-gray-700`}>
                    {tip}
                  </p>
                ))}
              </div>
            </div>
          </div>
        </main>
      </div>
    );
  }
  
  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-50 to-blue-50">
      <header className="bg-gradient-to-r from-indigo-500 to-blue-500 text-white p-6 shadow-lg">
        <div className="flex items-center gap-4">
          <button
            onClick={() => setCurrentView('grandparentHome')}
            className="text-white hover:bg-white/20 rounded-lg p-2 transition-colors"
          >
            <ChevronLeft className="w-6 h-6" />
          </button>
          <h1 className={`${textSizeClasses[textSize]} font-bold flex-1`}>
            {t.digitalLessons}
          </h1>
        </div>
      </header>
      
      <main className="p-4">
        <div className="max-w-2xl mx-auto space-y-4">
          {lessons.map(lesson => (
            <button
              key={lesson.id}
              onClick={() => setSelectedLesson(lesson.id)}
              className="w-full bg-white rounded-xl shadow-md p-6 hover:shadow-lg transition-shadow flex items-center gap-4"
            >
              <div className={`${lesson.color} text-white p-4 rounded-xl`}>
                {lesson.icon}
              </div>
              <div className="flex-1 text-left">
                <h3 className={`${textSizeClasses[textSize]} font-bold text-gray-800`}>
                  {lesson.title}
                </h3>
                <p className="text-gray-600 mt-1">
                  {language === 'el' ? 'Πατήστε για να ξεκινήσετε' : 'Click to start'}
                </p>
              </div>
              <Play className="w-6 h-6 text-gray-400" />
            </button>
          ))}
        </div>
      </main>
    </div>
  );
};

export default Lessons;
