import React, { useState, useEffect, useRef } from 'react';
import { ChevronLeft, Send, Phone, Video, Paperclip, Smile, Camera, Image, Heart, ThumbsUp, Bluetooth, BluetoothConnected } from 'lucide-react';

const Messages = ({ 
  messages, 
  messageInput, 
  setMessageInput, 
  setMessages,
  setCurrentView, 
  userRole, 
  grandparentName,
  grandparentDevice,
  userName,
  grandparentPhone,
  t, 
  textSizeClasses, 
  buttonSizeClasses,
  textSize,
  isConnected = false,
  sendBluetoothMessage = null
}) => {
  
  const [showEmojis, setShowEmojis] = useState(false);
  const [showQuickReplies, setShowQuickReplies] = useState(false);
  const [isTyping, setIsTyping] = useState(false);
  const [isSending, setIsSending] = useState(false);
  const messagesEndRef = useRef(null);
  
  const emojis = ['üòä', '‚ù§Ô∏è', 'üëç', 'üéâ', 'üå∫', '‚òï', 'üç∞', 'üåû', 'üåô', '‚≠ê', 'ü§ó', 'üòò', 'üëã', 'üôè', 'üíï'];
  
  const quickReplies = {
    el: [
      "ŒöŒ±ŒªŒ∑ŒºŒ≠œÅŒ±! ‚òÄÔ∏è",
      "ŒöŒ±ŒªŒ∑ŒΩœçœáœÑŒ±! üåô", 
      "Œ§Œπ Œ∫Œ¨ŒΩŒµŒπœÇ;",
      "Œ£Œµ Œ±Œ≥Œ±œÄœé! ‚ù§Ô∏è",
      "Œ†œéœÇ ŒµŒØœÉŒ±Œπ;",
      "Œ¶ŒπŒªŒπŒ¨! üòò",
      "ŒúœÄœÅŒ¨Œ≤Œø! üëè",
      "ŒïŒΩœÑŒ¨ŒæŒµŒπ! üëç",
      "ŒïœÖœáŒ±œÅŒπœÉœÑœé! üôè",
      "ŒùŒ± ŒµŒØœÉŒ±Œπ Œ∫Œ±ŒªŒ¨! üåü"
    ],
    en: [
      "Good morning! ‚òÄÔ∏è",
      "Good night! üåô",
      "How are you?",
      "Love you! ‚ù§Ô∏è",
      "How are things?",
      "Kisses! üòò",
      "Well done! üëè",
      "Okay! üëç",
      "Thank you! üôè",
      "Take care! üåü"
    ]
  };
  
  const handleSendMessage = async (text = messageInput) => {
    if (!text.trim() || isSending) return;
    
    setIsSending(true);
    const messageText = text.trim();
    
    try {
      if (isConnected && sendBluetoothMessage) {
        // Send via real Bluetooth
        const success = await sendBluetoothMessage(messageText);
        if (success) {
          setMessageInput('');
          setShowQuickReplies(false);
          showDeliveryNotification('Œ£œÑŒ¨ŒªŒ∏Œ∑Œ∫Œµ ŒºŒ≠œÉœâ Bluetooth! üì°');
        } else {
          showDeliveryNotification('ŒëœÄŒøœÑœÖœáŒØŒ± Œ±œÄŒøœÉœÑŒøŒªŒÆœÇ ŒºŒ≠œÉœâ Bluetooth! ‚ùå');
        }
      } else {
        // Fallback mode - show that Bluetooth is needed
        showDeliveryNotification('ŒßœÅŒµŒπŒ¨Œ∂ŒµœÑŒ±Œπ œÉœçŒΩŒ¥ŒµœÉŒ∑ Bluetooth Œ≥ŒπŒ± œÄœÅŒ±Œ≥ŒºŒ±œÑŒπŒ∫Œ¨ ŒºŒ∑ŒΩœçŒºŒ±œÑŒ±! üì±');
        
        // For demo purposes, still add to local messages
        const newMessage = {
          id: Date.now(),
          text: messageText,
          type: grandparentDevice === 'basic' ? 'sms' : 'sent',
          timestamp: new Date().toLocaleTimeString('el-GR', { hour: '2-digit', minute: '2-digit' }),
          status: 'demo',
          sender: userName || (userRole === 'grandparent' ? 'Œ†Œ±œÄœÄŒøœçœÇ/ŒìŒπŒ±Œ≥ŒπŒ¨' : 'Œ†Œ±ŒπŒ¥ŒØ')
        };
        
        setMessages(prev => [...prev, newMessage]);
        setMessageInput('');
        setShowQuickReplies(false);
      }
    } catch (error) {
      console.error('Error sending message:', error);
      showDeliveryNotification('Œ£œÜŒ¨ŒªŒºŒ± Œ±œÄŒøœÉœÑŒøŒªŒÆœÇ ŒºŒ∑ŒΩœçŒºŒ±œÑŒøœÇ! ‚ùå');
    } finally {
      setIsSending(false);
    }
  };
  
  const showDeliveryNotification = (message) => {
    const notification = document.createElement('div');
    notification.className = 'fixed top-20 left-1/2 transform -translate-x-1/2 bg-blue-600 text-white px-6 py-3 rounded-lg shadow-lg z-50 font-semibold';
    notification.textContent = message;
    document.body.appendChild(notification);
    
    setTimeout(() => {
      notification.remove();
    }, 3000);
  };
  
  const addEmoji = (emoji) => {
    setMessageInput(prev => prev + emoji);
    setShowEmojis(false);
  };

  const sendQuickReply = (reply) => {
    handleSendMessage(reply);
  };
  
  const isGrandparentView = userRole === 'grandparent';
  const contactName = isGrandparentView ? userName : grandparentName;
  
  // Auto-scroll to bottom
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, isTyping]);
  
  // Initial messages only if empty
  useEffect(() => {
    if (messages.length === 0) {
      const initialMessages = [
        { id: 1, text: 'ŒìŒµŒπŒ± œÉŒ±œÇ! ŒöŒ±ŒªœéœÇ ŒÆœÅŒ∏Œ±œÑŒµ œÉœÑŒø Roottly! üëã', type: 'received', timestamp: '09:00', status: 'received', sender: 'Œ£œçœÉœÑŒ∑ŒºŒ±' },
        { id: 2, text: 'Œ£œÖŒΩŒ¥ŒµŒ∏ŒµŒØœÑŒµ ŒºŒ≠œÉœâ Bluetooth Œ≥ŒπŒ± œÄœÅŒ±Œ≥ŒºŒ±œÑŒπŒ∫Œ¨ ŒºŒ∑ŒΩœçŒºŒ±œÑŒ±!', type: 'received', timestamp: '09:01', status: 'received', sender: 'Œ£œçœÉœÑŒ∑ŒºŒ±' }
      ];
      
      setMessages(initialMessages);
    }
  }, [messages.length, setMessages]);
  
  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-purple-50 flex flex-col">
      {/* Header */}
      <header className="bg-gradient-to-r from-blue-500 to-purple-500 text-white shadow-lg">
        <div className="p-4">
          <div className="flex items-center gap-4">
            <button
              onClick={() => setCurrentView(userRole === 'grandparent' ? 'grandparentHome' : 'childHome')}
              className="text-white hover:bg-white/20 rounded-lg p-2 transition-colors"
            >
              <ChevronLeft className="w-6 h-6" />
            </button>
            
            <div className="w-12 h-12 bg-white bg-opacity-20 rounded-full flex items-center justify-center text-2xl">
              {isGrandparentView ? 'üë∂' : 'üë®‚Äçü¶≥'}
            </div>
            
            <div className="flex-1">
              <h1 className={`${textSizeClasses[textSize]} font-bold`}>
                {userRole === 'child' ? grandparentName : (t.messages + ' - ' + contactName)}
              </h1>
              {grandparentDevice === 'basic' && userRole === 'child' && (
                <p className="text-sm opacity-80">üì± SMS - {grandparentPhone}</p>
              )}
              <div className="flex items-center gap-2">
                {isConnected ? (
                  <div className="flex items-center gap-1">
                    <BluetoothConnected className="w-4 h-4 text-green-300" />
                    <span className="text-xs text-green-300">Bluetooth œÉœÖŒΩŒ¥ŒµŒ¥ŒµŒºŒ≠ŒΩŒø</span>
                  </div>
                ) : (
                  <div className="flex items-center gap-1">
                    <Bluetooth className="w-4 h-4 text-gray-300" />
                    <span className="text-xs text-gray-300">ŒßœâœÅŒØœÇ œÉœçŒΩŒ¥ŒµœÉŒ∑</span>
                  </div>
                )}
                <p className="text-xs opacity-70 ml-2">
                  {isTyping ? (
                    <span className="flex items-center gap-1">
                      <div className="flex gap-1">
                        <div className="w-1 h-1 bg-white rounded-full animate-bounce"></div>
                        <div className="w-1 h-1 bg-white rounded-full animate-bounce" style={{animationDelay: '0.1s'}}></div>
                        <div className="w-1 h-1 bg-white rounded-full animate-bounce" style={{animationDelay: '0.2s'}}></div>
                      </div>
                      {t.language === 'el' ? 'Œ≥œÅŒ¨œÜŒµŒπ...' : 'typing...'}
                    </span>
                  ) : (
                    isConnected ? 'œÉœÖŒΩŒ¥ŒµŒ¥ŒµŒºŒ≠ŒΩŒøœÇ' : 'demo ŒªŒµŒπœÑŒøœÖœÅŒ≥ŒØŒ±'
                  )}
                </p>
              </div>
            </div>
            
            {grandparentDevice !== 'basic' && (
              <div className="flex gap-2">
                <button className="p-2 hover:bg-white/20 rounded-lg transition-colors">
                  <Phone className="w-5 h-5" />
                </button>
                <button className="p-2 hover:bg-white/20 rounded-lg transition-colors">
                  <Video className="w-5 h-5" />
                </button>
              </div>
            )}
          </div>
        </div>
      </header>
      
      {/* Connection Status Banner */}
      {!isConnected && (
        <div className="bg-yellow-100 border-b-2 border-yellow-300 p-3 text-center">
          <div className="flex items-center justify-center gap-2">
            <Bluetooth className="w-5 h-5 text-yellow-700" />
            <span className="text-yellow-800 font-semibold">
              ŒúŒ∑ŒΩœçŒºŒ±œÑŒ± œÉŒµ ŒªŒµŒπœÑŒøœÖœÅŒ≥ŒØŒ± demo - Œ£œÖŒΩŒ¥ŒµŒ∏ŒµŒØœÑŒµ ŒºŒ≠œÉœâ Bluetooth Œ≥ŒπŒ± œÄœÅŒ±Œ≥ŒºŒ±œÑŒπŒ∫Œ¨ ŒºŒ∑ŒΩœçŒºŒ±œÑŒ±
            </span>
          </div>
        </div>
      )}
      
      {/* Messages Area */}
      <div className="flex-1 p-4 overflow-y-auto">
        <div className="space-y-4 max-w-2xl mx-auto">
          {grandparentDevice === 'basic' && userRole === 'child' && (
            <div className="bg-gradient-to-r from-yellow-100 to-orange-100 border-2 border-yellow-300 rounded-lg p-3 mb-4 text-center">
              <p className="text-yellow-800 font-semibold">
                üì± {t.smsWillBeSent} {grandparentPhone}
              </p>
            </div>
          )}
          
          {messages.map(msg => (
            <div
              key={msg.id}
              className={`flex ${msg.type === 'sent' || msg.type === 'sms' ? 'justify-end' : 'justify-start'}`}
            >
              <div className="max-w-xs sm:max-w-sm">
                <div
                  className={`${buttonSizeClasses[textSize]} rounded-2xl shadow-md ${
                    msg.type === 'sent' || msg.type === 'sms'
                      ? 'bg-gradient-to-r from-blue-500 to-purple-500 text-white'
                      : 'bg-white'
                  }`}
                >
                  {msg.image && <div className="text-4xl mb-2">{msg.image}</div>}
                  <p className="break-words">{msg.text}</p>
                  <div className="flex items-center justify-between mt-2">
                    <p className={`text-xs ${
                      msg.type === 'sent' || msg.type === 'sms' ? 'text-blue-100' : 'text-gray-500'
                    }`}>
                      {msg.timestamp}
                    </p>
                    {msg.type === 'sent' && (
                      <div className="text-xs flex items-center gap-1">
                        {msg.status === 'sending' && '‚è≥'}
                        {msg.status === 'sent' && '‚úì'}
                        {msg.status === 'delivered' && '‚úì‚úì'}
                        {msg.status === 'demo' && 'üîí'}
                        {isConnected && <Bluetooth className="w-3 h-3" />}
                      </div>
                    )}
                  </div>
                </div>
              </div>
            </div>
          ))}
          
          {/* Typing indicator */}
          {isTyping && (
            <div className="flex justify-start">
              <div className="bg-white rounded-2xl p-4 shadow-md">
                <div className="flex gap-1">
                  <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
                  <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{animationDelay: '0.1s'}}></div>
                  <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{animationDelay: '0.2s'}}></div>
                </div>
              </div>
            </div>
          )}
          
          <div ref={messagesEndRef} />
        </div>
      </div>
      
      {/* Quick Replies */}
      {showQuickReplies && (
        <div className="bg-gray-100 border-t p-3">
          <div className="flex flex-wrap gap-2 max-w-2xl mx-auto">
            {quickReplies[t.language || 'el'].map((reply, index) => (
              <button
                key={index}
                onClick={() => sendQuickReply(reply)}
                className="bg-blue-500 text-white px-3 py-1 rounded-full text-sm hover:bg-blue-600 transition-colors"
                disabled={isSending}
              >
                {reply}
              </button>
            ))}
          </div>
        </div>
      )}
      
      {/* Emoji Picker */}
      {showEmojis && (
        <div className="bg-white border-t p-3">
          <div className="flex flex-wrap gap-2 max-w-2xl mx-auto">
            {emojis.map(emoji => (
              <button
                key={emoji}
                onClick={() => addEmoji(emoji)}
                className="text-2xl p-2 hover:bg-gray-100 rounded-lg transition-colors"
              >
                {emoji}
              </button>
            ))}
          </div>
        </div>
      )}
      
      {/* Input Area */}
      <div className="bg-white border-t-2 border-gray-200">
        <div className="p-4">
          <div className="flex gap-2 max-w-2xl mx-auto">
            {/* Quick replies button */}
            <button
              onClick={() => {
                setShowQuickReplies(!showQuickReplies);
                setShowEmojis(false);
              }}
              className={`${buttonSizeClasses[textSize]} text-purple-600 hover:text-purple-800 transition-colors`}
              disabled={isSending}
            >
              <Heart className="w-6 h-6" />
            </button>
            
            {/* Emoji button */}
            <button
              onClick={() => {
                setShowEmojis(!showEmojis);
                setShowQuickReplies(false);
              }}
              className={`${buttonSizeClasses[textSize]} text-gray-600 hover:text-gray-800 transition-colors`}
              disabled={isSending}
            >
              <Smile className="w-6 h-6" />
            </button>
            
            {/* Message input */}
            <input
              type="text"
              value={messageInput}
              onChange={(e) => setMessageInput(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && !isSending && handleSendMessage()}
              placeholder={isConnected ? t.typeMessage : "Œ£œÖŒΩŒ¥ŒµŒ∏ŒµŒØœÑŒµ Œ≥ŒπŒ± œÄœÅŒ±Œ≥ŒºŒ±œÑŒπŒ∫Œ¨ ŒºŒ∑ŒΩœçŒºŒ±œÑŒ±..."}
              disabled={isSending}
              className={`flex-1 ${buttonSizeClasses[textSize]} border-2 border-gray-300 rounded-lg focus:border-blue-500 focus:outline-none disabled:opacity-50`}
            />
            
            {/* Send button */}
            <button
              onClick={() => handleSendMessage()}
              disabled={!messageInput.trim() || isSending}
              className={`${buttonSizeClasses[textSize]} bg-gradient-to-r from-blue-500 to-purple-500 text-white rounded-lg hover:from-blue-600 hover:to-purple-600 disabled:opacity-50 disabled:cursor-not-allowed transition-all px-6 flex items-center gap-2`}
            >
              {isSending ? (
                <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white"></div>
              ) : (
                <>
                  <Send className="w-5 h-5" />
                  {isConnected && <Bluetooth className="w-4 h-4" />}
                </>
              )}
            </button>
          </div>
          
          {/* Bluetooth instruction */}
          {!isConnected && (
            <div className="text-center mt-3">
              <p className="text-sm text-gray-600">
                üí° Œ†Œ∑Œ≥Œ±ŒØŒΩŒµœÑŒµ œÉœÑŒ∑ŒΩ Œ±œÅœáŒπŒ∫ŒÆ œÉŒµŒªŒØŒ¥Œ± Œ≥ŒπŒ± ŒΩŒ± œÉœÖŒΩŒ¥ŒµŒ∏ŒµŒØœÑŒµ ŒºŒ≠œÉœâ Bluetooth
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Messages;
