import React, { useState } from 'react';
import { ChevronLeft, Check, Crown, Glasses, Hat } from 'lucide-react';

const AvatarSelection = ({ 
  setCurrentView, 
  userRole,
  selectedAvatar,
  setSelectedAvatar,
  selectedAccessories,
  setSelectedAccessories,
  gameScore,
  t, 
  textSizeClasses, 
  buttonSizeClasses,
  textSize
}) => {
  const [activeTab, setActiveTab] = useState('avatars');

  const avatars = [
    { id: 'elder_man', emoji: '👨‍🦳', name: 'Παππούς', cost: 0 },
    { id: 'elder_woman', emoji: '👵', name: 'Γιαγιά', cost: 0 },
    { id: 'young_man', emoji: '👦', name: 'Εγγονός', cost: 0 },
    { id: 'young_woman', emoji: '👧', name: 'Εγγονή', cost: 0 },
    { id: 'happy_elder', emoji: '😊👴', name: 'Χαρούμενος Παππούς', cost: 50 },
    { id: 'cool_elder', emoji: '😎👵', name: 'Κουλ Γιαγιά', cost: 50 },
    { id: 'wise_elder', emoji: '🧙‍♂️', name: 'Σοφός Παππούς', cost: 100 },
    { id: 'tech_elder', emoji: '🤓👴', name: 'Tech Παππούς', cost: 100 },
    { id: 'superhero', emoji: '🦸‍♂️', name: 'Σούπερ Παππούς', cost: 200 },
    { id: 'princess', emoji: '👸', name: 'Πριγκίπισσα Γιαγιά', cost: 200 }
  ];

  const accessories = [
    { id: 'hat_basic', emoji: '🧢', name: 'Καπέλο του Μπέιζμπολ', cost: 25 },
    { id: 'hat_formal', emoji: '🎩', name: 'Κλασικό Καπέλο', cost: 30 },
    { id: 'crown', emoji: '👑', name: 'Κορώνα', cost: 75 },
    { id: 'glasses', emoji: '👓', name: 'Γυαλιά', cost: 20 },
    { id: 'sunglasses', emoji: '🕶️', name: 'Γυαλιά Ηλίου', cost: 35 },
    { id: 'bow_tie', emoji: '🎀', name: 'Παπιγιόν', cost: 40 },
    { id: 'flower', emoji: '🌸', name: 'Λουλούδι', cost: 15 },
    { id: 'medal', emoji: '🏅', name: 'Μετάλλιο', cost: 60 },
    { id: 'star', emoji: '⭐', name: 'Αστέρι', cost: 45 },
    { id: 'heart', emoji: '💖', name: 'Καρδιά', cost: 30 }
  ];

  const canAfford = (cost) => gameScore >= cost;
  const isOwned = (id, type) => {
    if (type === 'avatar') {
      return cost === 0 || selectedAvatar === id; // Free avatars or currently selected
    }
    return selectedAccessories.includes(id);
  };

  const handleAvatarSelect = (avatarId, cost) => {
    if (canAfford(cost) || cost === 0) {
      setSelectedAvatar(avatarId);
      // Deduct points if not free
      if (cost > 0) {
        // This would be handled by parent component
      }
    }
  };

  const handleAccessoryToggle = (accessoryId, cost) => {
    if (selectedAccessories.includes(accessoryId)) {
      // Remove accessory
      setSelectedAccessories(prev => prev.filter(id => id !== accessoryId));
    } else if (canAfford(cost)) {
      // Add accessory
      setSelectedAccessories(prev => [...prev, accessoryId]);
      // Deduct points - handled by parent
    }
  };

  const getCurrentAvatar = () => {
    const avatar = avatars.find(a => a.id === selectedAvatar);
    return avatar ? avatar.emoji : '👨‍🦳';
  };

  const getAccessoryEmojis = () => {
    return selectedAccessories.map(id => {
      const accessory = accessories.find(a => a.id === id);
      return accessory ? accessory.emoji : '';
    }).join('');
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-50">
      <header className="bg-gradient-to-r from-purple-500 to-pink-500 text-white p-6 shadow-lg">
        <div className="flex items-center gap-4">
          <button
            onClick={() => setCurrentView(userRole === 'grandparent' ? 'grandparentHome' : 'childHome')}
            className="text-white hover:bg-white/20 rounded-xl p-3 transition-colors"
          >
            <ChevronLeft className="w-8 h-8" />
          </button>
          <div className="flex-1">
            <h1 className={`${textSizeClasses[textSize]} font-bold`}>
              👤 Επιλογή Avatar
            </h1>
            <div className="flex items-center gap-2 mt-1">
              <span className="text-yellow-300 font-semibold">💰 {gameScore} πόντοι</span>
            </div>
          </div>
          <div className="text-right">
            <div className="text-6xl mb-2">
              {getCurrentAvatar()}{getAccessoryEmojis()}
            </div>
            <p className="text-sm opacity-80">Το Avatar σου</p>
          </div>
        </div>
      </header>

      <main className="p-4">
        <div className="max-w-4xl mx-auto">
          {/* Tabs */}
          <div className="flex mb-6 bg-white rounded-xl shadow-md p-2">
            <button
              onClick={() => setActiveTab('avatars')}
              className={`flex-1 ${buttonSizeClasses[textSize]} rounded-lg transition-all ${
                activeTab === 'avatars' 
                ? 'bg-purple-500 text-white' 
                : 'text-purple-600 hover:bg-purple-100'
              }`}
            >
              👤 Avatars
            </button>
            <button
              onClick={() => setActiveTab('accessories')}
              className={`flex-1 ${buttonSizeClasses[textSize]} rounded-lg transition-all ${
                activeTab === 'accessories' 
                ? 'bg-purple-500 text-white' 
                : 'text-purple-600 hover:bg-purple-100'
              }`}
            >
              👑 Αξεσουάρ
            </button>
          </div>

          {/* Avatars Tab */}
          {activeTab === 'avatars' && (
            <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
              {avatars.map(avatar => {
                const isSelected = selectedAvatar === avatar.id;
                const affordable = canAfford(avatar.cost);
                const owned = isOwned(avatar.id, 'avatar');
                
                return (
                  <button
                    key={avatar.id}
                    onClick={() => handleAvatarSelect(avatar.id, avatar.cost)}
                    disabled={!affordable && avatar.cost > 0 && !owned}
                    className={`relative bg-white rounded-2xl p-6 shadow-md transition-all transform hover:scale-105 ${
                      isSelected 
                        ? 'ring-4 ring-purple-500 bg-purple-50' 
                        : !affordable && avatar.cost > 0 && !owned
                        ? 'opacity-50 cursor-not-allowed'
                        : 'hover:shadow-lg'
                    }`}
                  >
                    <div className="text-6xl mb-3">{avatar.emoji}</div>
                    <h3 className={`${textSizeClasses[textSize]} font-semibold text-gray-800 mb-2`}>
                      {avatar.name}
                    </h3>
                    
                    {avatar.cost === 0 ? (
                      <div className="text-green-600 font-bold">ΔΩΡΕΑΝ</div>
                    ) : owned ? (
                      <div className="text-blue-600 font-bold">✓ ΑΓΟΡΑΣΜΕΝΟ</div>
                    ) : (
                      <div className={`font-bold ${affordable ? 'text-purple-600' : 'text-red-500'}`}>
                        💰 {avatar.cost} πόντοι
                      </div>
                    )}

                    {isSelected && (
                      <div className="absolute -top-2 -right-2 bg-green-500 text-white rounded-full p-2">
                        <Check className="w-4 h-4" />
                      </div>
                    )}

                    {!affordable && avatar.cost > 0 && !owned && (
                      <div className="absolute inset-0 bg-gray-500 bg-opacity-50 rounded-2xl flex items-center justify-center">
                        <div className="text-4xl">🔒</div>
                      </div>
                    )}
                  </button>
                );
              })}
            </div>
          )}

          {/* Accessories Tab */}
          {activeTab === 'accessories' && (
            <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
              {accessories.map(accessory => {
                const isSelected = selectedAccessories.includes(accessory.id);
                const affordable = canAfford(accessory.cost);
                const owned = isOwned(accessory.id, 'accessory');
                
                return (
                  <button
                    key={accessory.id}
                    onClick={() => handleAccessoryToggle(accessory.id, accessory.cost)}
                    disabled={!affordable && !owned}
                    className={`relative bg-white rounded-2xl p-6 shadow-md transition-all transform hover:scale-105 ${
                      isSelected 
                        ? 'ring-4 ring-pink-500 bg-pink-50' 
                        : !affordable && !owned
                        ? 'opacity-50 cursor-not-allowed'
                        : 'hover:shadow-lg'
                    }`}
                  >
                    <div className="text-6xl mb-3">{accessory.emoji}</div>
                    <h3 className={`${textSizeClasses[textSize]} font-semibold text-gray-800 mb-2`}>
                      {accessory.name}
                    </h3>
                    
                    {owned ? (
                      <div className="text-blue-600 font-bold">
                        {isSelected ? '✓ ΕΝΕΡΓΟ' : '○ ΑΓΟΡΑΣΜΕΝΟ'}
                      </div>
                    ) : (
                      <div className={`font-bold ${affordable ? 'text-pink-600' : 'text-red-500'}`}>
                        💰 {accessory.cost} πόντοι
                      </div>
                    )}

                    {isSelected && (
                      <div className="absolute -top-2 -right-2 bg-green-500 text-white rounded-full p-2">
                        <Check className="w-4 h-4" />
                      </div>
                    )}

                    {!affordable && !owned && (
                      <div className="absolute inset-0 bg-gray-500 bg-opacity-50 rounded-2xl flex items-center justify-center">
                        <div className="text-4xl">🔒</div>
                      </div>
                    )}
                  </button>
                );
              })}
            </div>
          )}

          {/* Instructions */}
          <div className="mt-8 bg-blue-100 border-2 border-blue-300 rounded-xl p-4 text-center">
            <h3 className={`${textSizeClasses[textSize]} font-bold text-blue-800 mb-2`}>
              💡 Οδηγίες
            </h3>
            <p className="text-blue-700">
              {activeTab === 'avatars' 
                ? 'Επιλέξτε το avatar που σας αρέσει! Κερδίστε πόντους από μαθήματα για να ξεκλειδώσετε νέα avatars.'
                : 'Πατήστε τα αξεσουάρ για να τα φορέσετε ή να τα βγάλετε. Μπορείτε να φορέσετε πολλά μαζί!'
              }
            </p>
          </div>
        </div>
      </main>
    </div>
  );
};

export default AvatarSelection;
