        walking: ["ŒíœåŒªœÑŒ± œÉœÑŒø œÄŒ¨œÅŒ∫Œø", "ŒíœåŒªœÑŒ± œÉœÑŒ∑ Œ≥ŒµŒπœÑŒøŒΩŒπŒ¨", "ŒìœÖŒºŒΩŒ±œÉœÑŒπŒ∫ŒÆ œÉœÑŒø œÉœÄŒØœÑŒπ"],
        photos: ["ŒîŒµŒØœÑŒµ œÄŒ±ŒªŒπŒ≠œÇ œÜœâœÑŒøŒ≥œÅŒ±œÜŒØŒµœÇ", "Œ§œÅŒ±Œ≤ŒÆŒæœÑŒµ ŒΩŒ≠ŒµœÇ œÜœâœÑŒøŒ≥œÅŒ±œÜŒØŒµœÇ", "Œ¶œÑŒπŒ¨ŒæœÑŒµ Œ¨ŒªŒºœÄŒøœÖŒº"],
        games: ["Œ†Œ±ŒØŒæœÑŒµ œÉŒ∫Œ¨Œ∫Œπ", "ŒõœçœÉœÑŒµ œÉœÑŒ±œÖœÅœåŒªŒµŒæŒ±", "Œ†Œ±ŒØŒæœÑŒµ œáŒ±œÅœÑŒπŒ¨"],
        crafts: ["ŒñœâŒ≥œÅŒ±œÜŒØœÉœÑŒµ", "Œ†ŒªŒ≠ŒæœÑŒµ", "Œ¶œÑŒπŒ¨ŒæœÑŒµ œáŒµŒπœÅŒøœÑŒµœáŒΩŒØŒµœÇ"],
        movies: ["ŒîŒµŒØœÑŒµ ŒµŒªŒªŒ∑ŒΩŒπŒ∫ŒÆ œÑŒ±ŒπŒΩŒØŒ±", "ŒîŒµŒØœÑŒµ ŒΩœÑŒøŒ∫ŒπŒºŒ±ŒΩœÑŒ≠œÅ", "ŒîŒµŒØœÑŒµ Œ∫œâŒºœâŒ¥ŒØŒ±"],
        travel: ["ŒîŒµŒØœÑŒµ œÜœâœÑŒøŒ≥œÅŒ±œÜŒØŒµœÇ Œ±œÄœå œÑŒ±ŒæŒØŒ¥ŒπŒ±", "Œ£œáŒµŒ¥ŒπŒ¨œÉœÑŒµ œÑŒ±ŒæŒØŒ¥Œπ", "ŒîŒπŒ±Œ≤Œ¨œÉœÑŒµ Œ≥ŒπŒ± œÄœÅŒøŒøœÅŒπœÉŒºŒøœçœÇ"],
        general: [
          "Œ†ŒπŒµŒØœÑŒµ Œ∫Œ±œÜŒ≠ ŒºŒµ œÜŒØŒªŒøœÖœÇ",
          "ŒöŒ±ŒªŒ≠œÉœÑŒµ Œ≠ŒΩŒ±ŒΩ œÉœÖŒ≥Œ≥ŒµŒΩŒÆ",
          "ŒöŒ¨ŒΩœÑŒµ Œ≤ŒπŒΩœÑŒµŒøŒ∫ŒªŒÆœÉŒ∑",
          "ŒúŒøŒπœÅŒ±œÉœÑŒµŒØœÑŒµ Œ±ŒΩŒ±ŒºŒΩŒÆœÉŒµŒπœÇ",
          "ŒìœÅŒ¨œàœÑŒµ Œ≥œÅŒ¨ŒºŒºŒ±",
          "ŒüœÅŒ≥Œ±ŒΩœéœÉœÑŒµ œÑŒø œÉœÄŒØœÑŒπ",
          "ŒöŒ¨ŒΩœÑŒµ Œ¥ŒπŒ±ŒªŒøŒ≥ŒπœÉŒºœå",
          "ŒëŒ∫ŒøœçœÉœÑŒµ œÑŒπœÇ ŒµŒπŒ¥ŒÆœÉŒµŒπœÇ"
        ]
      },
      en: {
        cooking: ["Cook traditional recipes", "Make desserts", "Prepare Greek coffee"],
        gardening: ["Water the plants", "Plant basil", "Clean the garden"],
        music: ["Listen to favorite songs", "Sing together", "Dance"],
        reading: ["Read newspaper", "Read a book", "Write diary"],
        walking: ["Walk in the park", "Walk in neighborhood", "Exercise at home"],
        photos: ["Look at old photos", "Take new photos", "Make album"],
        games: ["Play chess", "Solve crosswords", "Play cards"],
        crafts: ["Paint", "Knit", "Make crafts"],
        movies: ["Watch Greek movie", "Watch documentary", "Watch comedy"],
        travel: ["Look at travel photos", "Plan a trip", "Read about destinations"],
        general: [
          "Have coffee with friends",
          "Call a relative",
          "Make video call",
          "Share memories",
          "Write a letter",
          "Organize the house",
          "Meditate",
          "Listen to news"
        ]
      }
    };

    const userActivities = userRole === 'grandparent' ? userInterests : grandparentInterests;
    let allActivities = [];
    
    // Add activities based on interests
    userActivities.forEach(interest => {
      if (activities[language] && activities[language][interest]) {
        allActivities.push(...activities[language][interest]);
      }
    });
    
    // Add general activities
    if (activities[language] && activities[language].general) {
      allActivities.push(...activities[language].general);
    }
    
    // If no specific activities, use all general ones
    if (allActivities.length === 0) {
      allActivities = activities[language]?.general || activities.el.general;
    }
    
    return allActivities.slice(0, 8); // Limit to 8 activities for the wheel
  };

  const wheelActivities = getRelevantActivities();
  const segmentAngle = 360 / wheelActivities.length;

  const spinWheel = () => {
    if (isSpinning) return;
    
    setIsSpinning(true);
    setShowResult(false);
    setSelectedActivity(null);

    // Random spin: 3-5 full rotations plus random angle
    const minSpins = 3;
    const maxSpins = 5;
    const spins = Math.random() * (maxSpins - minSpins) + minSpins;
    const finalAngle = Math.random() * 360;
    const totalRotation = spins * 360 + finalAngle;
    
    setRotation(prev => prev + totalRotation);
    
    // Calculate which segment we land on
    const normalizedAngle = (360 - (finalAngle % 360)) % 360;
    const selectedIndex = Math.floor(normalizedAngle / segmentAngle) % wheelActivities.length;
    
    setTimeout(() => {
      setIsSpinning(false);
      setSelectedActivity(wheelActivities[selectedIndex]);
      setShowResult(true);
    }, 3000);
  };

  const resetWheel = () => {
    setSelectedActivity(null);
    setShowResult(false);
    setRotation(0);
  };

  // Wheel colors
  const colors = [
    'bg-red-400', 'bg-blue-400', 'bg-green-400', 'bg-yellow-400',
    'bg-purple-400', 'bg-pink-400', 'bg-indigo-400', 'bg-orange-400'
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-orange-50 to-yellow-50">
      <header className="bg-gradient-to-r from-orange-500 to-yellow-500 text-white p-6 shadow-lg">
        <div className="flex items-center gap-4">
          <button
            onClick={() => setCurrentView(userRole === 'grandparent' ? 'grandparentHome' : 'childHome')}
            className="text-white hover:bg-white/20 rounded-lg p-2 transition-colors"
          >
            <ChevronLeft className="w-6 h-6" />
          </button>
          <h1 className={`${textSizeClasses[textSize]} font-bold flex-1`}>
            üé° {t.activityWheel}
          </h1>
        </div>
      </header>
      
      <main className="p-4">
        <div className="max-w-2xl mx-auto">
          {/* Instructions */}
          <div className="text-center mb-8">
            <p className={`${textSizeClasses[textSize]} text-gray-600 mb-4`}>
              {isSpinning ? t.spinning : t.spinWheel}
            </p>
          </div>

          {/* Wheel Container */}
          <div className="relative flex justify-center items-center mb-8">
            {/* Pointer */}
            <div className="absolute top-0 z-20 transform -translate-x-1/2 -translate-y-2">
              <div className="w-0 h-0 border-l-4 border-r-4 border-b-8 border-l-transparent border-r-transparent border-b-red-600"></div>
            </div>
            
            {/* Wheel */}
            <div className="relative">
              <div 
                className={`w-80 h-80 rounded-full relative overflow-hidden shadow-2xl transition-transform duration-3000 ease-out ${
                  isSpinning ? 'animate-pulse' : ''
                }`}
                style={{ 
                  transform: `rotate(${rotation}deg)`,
                  transitionDuration: isSpinning ? '3s' : '0.3s'
                }}
              >
                {wheelActivities.map((activity, index) => {
                  const startAngle = index * segmentAngle;
                  const endAngle = (index + 1) * segmentAngle;
                  
                  return (
                    <div
                      key={index}
                      className={`absolute w-full h-full ${colors[index % colors.length]}`}
                      style={{
                        clipPath: `polygon(50% 50%, ${
                          50 + 50 * Math.cos((startAngle - 90) * Math.PI / 180)
                        }% ${
                          50 + 50 * Math.sin((startAngle - 90) * Math.PI / 180)
                        }%, ${
                          50 + 50 * Math.cos((endAngle - 90) * Math.PI / 180)
                        }% ${
                          50 + 50 * Math.sin((endAngle - 90) * Math.PI / 180)
                        }%)`
                      }}
                    >
                      <div 
                        className="absolute text-white font-bold text-sm p-2 transform"
                        style={{
                          top: `${50 + 25 * Math.sin((startAngle + segmentAngle/2 - 90) * Math.PI / 180)}%`,
                          left: `${50 + 25 * Math.cos((startAngle + segmentAngle/2 - 90) * Math.PI / 180)}%`,
                          transform: `translate(-50%, -50%) rotate(${startAngle + segmentAngle/2}deg)`,
                          maxWidth: '80px',
                          textAlign: 'center',
                          fontSize: wheelActivities.length > 6 ? '10px' : '12px'
                        }}
                      >
                        {activity.slice(0, 20)}
                        {activity.length > 20 ? '...' : ''}
                      </div>
                    </div>
                  );
                })}
                
                {/* Center circle */}
                <div className="absolute top-1/2 left-1/2 w-8 h-8 bg-white rounded-full shadow-lg transform -translate-x-1/2 -translate-y-1/2 z-10 flex items-center justify-center">
                  <Star className="w-4 h-4 text-yellow-500" />
                </div>
              </div>
            </div>
          </div>

          {/* Controls */}
          <div className="text-center space-y-4">
            <button
              onClick={spinWheel}
              disabled={isSpinning}
              className={`${buttonSizeClasses[textSize]} bg-gradient-to-r from-green-500 to-blue-500 text-white rounded-xl shadow-lg hover:shadow-xl transition-all transform hover:scale-105 disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none px-8`}
            >
              <Play className="inline mr-2 w-6 h-6" />
              {isSpinning ? t.spinning : t.spinWheel}
            </button>

            <button
              onClick={resetWheel}
              className={`${buttonSizeClasses[textSize]} bg-gray-500 text-white rounded-xl shadow-lg hover:shadow-xl transition-all transform hover:scale-105 px-6 ml-4`}
            >
              <RotateCcw className="inline mr-2 w-5 h-5" />
              {t.back}
            </button>
          </div>

          {/* Result */}
          {showResult && selectedActivity && (
            <div className="mt-8">
              <div className="bg-gradient-to-r from-green-400 to-blue-500 text-white rounded-2xl p-6 shadow-xl text-center transform scale-105">
                <Trophy className="w-16 h-16 mx-auto mb-4 text-yellow-300" />
                <h2 className={`${textSizeClasses[textSize]} font-bold mb-3`}>
                  üéâ {t.selectedActivity}!
                </h2>
                <p className={`${textSizeClasses[textSize]} text-lg mb-6 bg-white bg-opacity-20 rounded-lg p-4`}>
                  {selectedActivity}
                </p>
                <div className="flex gap-4 justify-center">
                  <button
                    onClick={() => setCurrentView('activities')}
                    className={`${buttonSizeClasses[textSize]} bg-white text-blue-600 rounded-xl font-semibold hover:bg-gray-100 transition-colors px-8 py-4 shadow-lg`}
                  >
                    {t.startActivity} üöÄ
                  </button>
                  <button
                    onClick={() => setCurrentView(userRole === 'grandparent' ? 'grandparentHome' : 'childHome')}
                    className={`${buttonSizeClasses[textSize]} bg-yellow-400 text-yellow-900 rounded-xl font-semibold hover:bg-yellow-300 transition-colors px-8 py-4 shadow-lg`}
                  >
                    üè† ŒëœÅœáŒπŒ∫ŒÆ
                  </button>
                </div>
              </div>
            </div>
          )}

          {/* Activity suggestions based on interests */}
          <div className="mt-8">
            <h3 className={`${textSizeClasses[textSize]} font-bold text-gray-800 mb-4 text-center`}>
              üí° {t.basedOnInterests}
            </h3>
            <div className="grid grid-cols-2 gap-3">
              {(userRole === 'grandparent' ? userInterests : grandparentInterests).slice(0, 4).map(interest => {
                const interestData = availableInterests.find(i => i.id === interest);
                return interestData ? (
                  <div key={interest} className="bg-white rounded-lg p-3 shadow-md text-center">
                    <div className="text-2xl mb-1">{interestData.icon}</div>
                    <div className="text-sm font-medium text-gray-700">{t[interest]}</div>
                  </div>
                ) : null;
              })}
            </div>
          </div>
        </div>
      </main>
    </div>
  );
};

