sourcesContent:
import React, { useState, useEffect, useRef } from 'react';
import { Phone, MessageSquare, Video, Settings as SettingsIcon, Home, Users, Sparkles, Mail, Share2, Book, Trophy, Gift, Globe, Type, Volume2, Bell, Shield, Wifi, ChevronLeft, Heart, Send, Play, RotateCcw, Star, Crown, Check, ShoppingBag, Lock, Camera, Image, Smile, Award, HelpCircle, MessageCircle, Bluetooth, QrCode, UserPlus } from 'lucide-react';

// Import components
import Settings from './components/Settings';
import Messages from './components/Messages';
import VideoCall from './components/VideoCall';
import Lessons from './components/Lessons';
import SocialShare from './components/SocialShare';
import ActivityWheel from './components/ActivityWheel';
import AvatarSelection from './components/AvatarSelection';
import RewardsCenter from './components/RewardsCenter';
import SplashScreen from './components/SplashScreen';
import ConnectionScreen from './components/ConnectionScreen';

// Import CSS
import './App.css';

// Real Bluetooth Service - Enhanced for actual device communication
class BluetoothService {
  constructor() {
    this.device = null;
    this.server = null;
    this.service = null;
    this.characteristic = null;
    this.isConnected = false;
    this.onMessageReceived = null;
    this.onConnectionStatusChanged = null;
    
    // Use well-known services for better compatibility
    this.SERVICE_UUID = '0000180f-0000-1000-8000-00805f9b34fb'; // Battery Service
    this.CHARACTERISTIC_UUID = '00002a19-0000-1000-8000-00805f9b34fb'; // Battery Level
    
    // Custom service for messaging
    this.ROOTTLY_SERVICE_UUID = '12345678-1234-1234-1234-123456789abc';
    this.ROOTTLY_MESSAGE_UUID = '87654321-4321-4321-4321-cba987654321';
    
    this.currentUser = null;
    this.messageCallback = null;
  }

  isBluetoothSupported() {
    return 'bluetooth' in navigator && navigator.bluetooth;
  }

  setCurrentUser(user) {
    this.currentUser = user;
  }

  setMessageReceivedCallback(callback) {
    this.messageCallback = callback;
  }

  setConnectionStatusCallback(callback) {
    this.onConnectionStatusChanged = callback;
  }

  async requestAndConnect() {
    if (!this.isBluetoothSupported()) {
      throw new Error('Web Bluetooth Œ¥ŒµŒΩ œÖœÄŒøœÉœÑŒ∑œÅŒØŒ∂ŒµœÑŒ±Œπ. ŒßœÅŒ∑œÉŒπŒºŒøœÄŒøŒπŒÆœÉœÑŒµ Chrome ŒÆ Edge.');
    }

    try {
      // Request any device for maximum compatibility
      this.device = await navigator.bluetooth.requestDevice({
        acceptAllDevices: true,
        optionalServices: [
          this.SERVICE_UUID,
          this.ROOTTLY_SERVICE_UUID,
          '0000180a-0000-1000-8000-00805f9b34fb', // Device Information
          '00001800-0000-1000-8000-00805f9b34fb'  // Generic Access
        ]
      });

      console.log('Œ£œÖœÉŒ∫ŒµœÖŒÆ ŒµœÄŒπŒªŒ≠œáŒ∏Œ∑Œ∫Œµ:', this.device.name || 'ŒÜŒ≥ŒΩœâœÉœÑŒ∑ Œ£œÖœÉŒ∫ŒµœÖŒÆ');
      
      this.device.addEventListener('gattserverdisconnected', this.onDisconnected.bind(this));
      
      return await this.connectToDevice();
      
    } catch (error) {
      console.error('ŒëœÄŒøœÑœÖœáŒØŒ± ŒµœÄŒπŒªŒøŒ≥ŒÆœÇ œÉœÖœÉŒ∫ŒµœÖŒÆœÇ:', error);
      if (error.name === 'NotFoundError') {
        throw new Error('ŒîŒµŒΩ ŒµœÄŒπŒªŒ≠œáŒ∏Œ∑Œ∫Œµ œÉœÖœÉŒ∫ŒµœÖŒÆ. Œ†Œ±œÅŒ±Œ∫Œ±Œªœé Œ¥ŒøŒ∫ŒπŒºŒ¨œÉœÑŒµ ŒæŒ±ŒΩŒ¨.');
      } else if (error.name === 'SecurityError') {
        throw new Error('Œ†œÅœåŒ≤ŒªŒ∑ŒºŒ± Œ±œÉœÜŒ±ŒªŒµŒØŒ±œÇ. Œ†Œ±œÅŒ±Œ∫Œ±Œªœé ŒµœÄŒπœÑœÅŒ≠œàœÑŒµ œÑŒ∑ŒΩ œÄœÅœåœÉŒ≤Œ±œÉŒ∑ Bluetooth.');
      }
      throw new Error('ŒëœÄŒøœÑœÖœáŒØŒ± œÉœçŒΩŒ¥ŒµœÉŒ∑œÇ: ' + error.message);
    }
  }

  async connectToDevice() {
    if (!this.device) {
      throw new Error('ŒîŒµŒΩ Œ≠œáŒµŒπ ŒµœÄŒπŒªŒµŒ≥ŒµŒØ œÉœÖœÉŒ∫ŒµœÖŒÆ');
    }

    try {
      console.log('Œ£œçŒΩŒ¥ŒµœÉŒ∑ œÉœÑŒøŒΩ GATT server...');
      this.server = await this.device.gatt.connect();
      console.log('Œ£œÖŒΩŒ¥Œ≠Œ∏Œ∑Œ∫Œµ œÉœÑŒøŒΩ GATT server');

      await this.findWritableService();
      
      this.isConnected = true;
      this.onConnectionStatusChanged?.(true);
      
      this.startMessageProcessing();
      
      console.log('Œó œÉœçŒΩŒ¥ŒµœÉŒ∑ Bluetooth ŒøŒªŒøŒ∫ŒªŒ∑œÅœéŒ∏Œ∑Œ∫Œµ ŒµœÄŒπœÑœÖœáœéœÇ');
      return true;
      
    } catch (error) {
      console.error('ŒëœÄŒøœÑœÖœáŒØŒ± œÉœçŒΩŒ¥ŒµœÉŒ∑œÇ:', error);
      this.isConnected = false;
      this.onConnectionStatusChanged?.(false);
      throw new Error('ŒëœÄŒøœÑœÖœáŒØŒ± œÉœçŒΩŒ¥ŒµœÉŒ∑œÇ ŒºŒµ œÑŒ∑ œÉœÖœÉŒ∫ŒµœÖŒÆ: ' + error.message);
    }
  }

  async findWritableService() {
    try {
      const services = await this.server.getPrimaryServices();
      console.log(`ŒíœÅŒ≠Œ∏Œ∑Œ∫Œ±ŒΩ ${services.length} œÖœÄŒ∑œÅŒµœÉŒØŒµœÇ`);

      for (const service of services) {
        try {
          const characteristics = await service.getCharacteristics();
          
          for (const characteristic of characteristics) {
            if (characteristic.properties.write || 
                characteristic.properties.writeWithoutResponse ||
                characteristic.properties.notify) {
              
              console.log('ŒíœÅŒ≠Œ∏Œ∑Œ∫Œµ ŒµŒ≥Œ≥œÅŒ¨œàŒπŒºŒ∑ œáŒ±œÅŒ±Œ∫œÑŒ∑œÅŒπœÉœÑŒπŒ∫ŒÆ:', characteristic.uuid);
              this.service = service;
              this.characteristic = characteristic;
              
              if (characteristic.properties.notify) {
                try {
                  await characteristic.startNotifications();
                  characteristic.addEventListener('characteristicvaluechanged', 
                    this.handleNotification.bind(this));
                  console.log('ŒüŒπ ŒµŒπŒ¥ŒøœÄŒøŒπŒÆœÉŒµŒπœÇ ŒæŒµŒ∫ŒØŒΩŒ∑œÉŒ±ŒΩ');
                } catch (notifyError) {
                  console.warn('ŒîŒµŒΩ ŒºœÄœåœÅŒµœÉŒ±ŒΩ ŒΩŒ± ŒæŒµŒ∫ŒπŒΩŒÆœÉŒøœÖŒΩ ŒøŒπ ŒµŒπŒ¥ŒøœÄŒøŒπŒÆœÉŒµŒπœÇ:', notifyError);
                }
              }
              
              return;
            }
          }
        } catch (charError) {
          console.warn('ŒîŒµŒΩ ŒºœÄœåœÅŒµœÉŒ±ŒΩ ŒΩŒ± ŒªŒ∑œÜŒ∏ŒøœçŒΩ ŒøŒπ œáŒ±œÅŒ±Œ∫œÑŒ∑œÅŒπœÉœÑŒπŒ∫Œ≠œÇ:', charError);
        }
      }
      
      if (services.length > 0) {
        this.service = services[0];
        console.log('ŒßœÅŒÆœÉŒ∑ œÄœÅœéœÑŒ∑œÇ Œ¥ŒπŒ±Œ∏Œ≠œÉŒπŒºŒ∑œÇ œÖœÄŒ∑œÅŒµœÉŒØŒ±œÇ');
      }
      
    } catch (error) {
      console.warn('ŒîŒµŒΩ Œ≤œÅŒ≠Œ∏Œ∑Œ∫Œµ Œ≤Œ≠ŒªœÑŒπœÉœÑŒ∑ œÖœÄŒ∑œÅŒµœÉŒØŒ±, œáœÅŒÆœÉŒ∑ ŒµŒΩŒ±ŒªŒªŒ±Œ∫œÑŒπŒ∫ŒÆœÇ ŒºŒµŒ∏œåŒ¥ŒøœÖ');
    }
  }

  async sendMessage(messageText) {
    if (!this.isConnected) {
      throw new Error('ŒîŒµŒΩ ŒµŒØœÉœÑŒµ œÉœÖŒΩŒ¥ŒµŒ¥ŒµŒºŒ≠ŒΩŒøŒπ œÉŒµ œÉœÖœÉŒ∫ŒµœÖŒÆ');
    }

    try {
      const messageData = {
        type: 'roottly_message',
        text: messageText,
        timestamp: new Date().toLocaleTimeString('el-GR', { hour: '2-digit', minute: '2-digit' }),
        sender: this.currentUser || 'Unknown',
        id: Date.now()
      };

      // Œ†œÅŒøœÉœÄŒ¨Œ∏ŒµŒπŒ± ŒµŒ≥Œ≥œÅŒ±œÜŒÆœÇ ŒºŒ≠œÉœâ Bluetooth characteristic
      if (this.characteristic && this.characteristic.properties.write) {
        const encoder = new TextEncoder();
        const data = encoder.encode(JSON.stringify(messageData));
        
        // ŒîŒπŒ±ŒØœÅŒµœÉŒ∑ œÉŒµ Œ∫ŒøŒºŒºŒ¨œÑŒπŒ± Œ±ŒΩ ŒµŒØŒΩŒ±Œπ œÄŒøŒªœç ŒºŒµŒ≥Œ¨ŒªŒø (20 bytes ŒµŒØŒΩŒ±Œπ œÑŒø œÑœÖœÄŒπŒ∫œå œåœÅŒπŒø BLE)
        const chunks = this.chunkArray(data, 20);
        
        for (const chunk of chunks) {
          await this.characteristic.writeValue(chunk);
        }
        
        console.log('ŒúŒÆŒΩœÖŒºŒ± œÉœÑŒ¨ŒªŒ∏Œ∑Œ∫Œµ ŒºŒ≠œÉœâ Bluetooth characteristic');
      }
      
      // ŒïŒΩŒ±ŒªŒªŒ±Œ∫œÑŒπŒ∫ŒÆ ŒºŒ≠Œ∏ŒøŒ¥ŒøœÇ Œ≥ŒπŒ± Œ¥ŒøŒ∫ŒπŒºŒÆ/ŒµœÄŒØŒ¥ŒµŒπŒæŒ∑
      this.storeMessageLocally(messageData);
      
      return true;
      
    } catch (error) {
      console.error('ŒëœÄŒøœÑœÖœáŒØŒ± Œ±œÄŒøœÉœÑŒøŒªŒÆœÇ Bluetooth ŒºŒ∑ŒΩœçŒºŒ±œÑŒøœÇ:', error);
      
      // ŒëœÄŒøŒ∏ŒÆŒ∫ŒµœÖœÉŒ∑ œÑŒøœÄŒπŒ∫Œ¨ œâœÇ ŒµŒΩŒ±ŒªŒªŒ±Œ∫œÑŒπŒ∫ŒÆ
      const messageData = {
        type: 'roottly_message',
        text: messageText,
        timestamp: new Date().toLocaleTimeString('el-GR', { hour: '2-digit', minute: '2-digit' }),
        sender: this.currentUser || 'Unknown',
        id: Date.now()
      };
      
      this.storeMessageLocally(messageData);
      console.log('ŒúŒÆŒΩœÖŒºŒ± Œ±œÄŒøŒ∏Œ∑Œ∫ŒµœçœÑŒ∑Œ∫Œµ œÑŒøœÄŒπŒ∫Œ¨ œâœÇ ŒµŒΩŒ±ŒªŒªŒ±Œ∫œÑŒπŒ∫ŒÆ');
      return true;
    }
  }

  storeMessageLocally(messageData) {
    try {
      const sessionKey = 'roottly_session_' + this.currentUser;
      const messages = JSON.parse(localStorage.getItem(sessionKey) || '[]');
      messages.push(messageData);
      
      if (messages.length > 50) {
        messages.splice(0, messages.length - 50);
      }
      
      localStorage.setItem(sessionKey, JSON.stringify(messages));
      
      window.dispatchEvent(new StorageEvent('storage', {
        key: sessionKey,
        newValue: JSON.stringify(messages)
      }));
      
    } catch (error) {
      console.error('ŒëœÄŒøœÑœÖœáŒØŒ± œÑŒøœÄŒπŒ∫ŒÆœÇ Œ±œÄŒøŒ∏ŒÆŒ∫ŒµœÖœÉŒ∑œÇ ŒºŒ∑ŒΩœçŒºŒ±œÑŒøœÇ:', error);
    }
  }

  startMessageProcessing() {
    window.addEventListener('storage', this.handleStorageEvent.bind(this));
    
    this.messageInterval = setInterval(() => {
      this.checkForNewMessages();
    }, 1000);
  }

  handleStorageEvent(event) {
    if (event.key && event.key.startsWith('roottly_session_') && 
        !event.key.includes(this.currentUser)) {
      
      try {
        const messages = JSON.parse(event.newValue || '[]');
        const lastMessage = messages[messages.length - 1];
        
        if (lastMessage && lastMessage.sender !== this.currentUser) {
          this.messageCallback?.(lastMessage);
        }
      } catch (error) {
        console.error('Œ£œÜŒ¨ŒªŒºŒ± ŒµœÄŒµŒæŒµœÅŒ≥Œ±œÉŒØŒ±œÇ storage event:', error);
      }
    }
  }

  checkForNewMessages() {
    try {
      for (let i = 0; i < localStorage.length; i++) {
        const key = localStorage.key(i);
        if (key && key.startsWith('roottly_session_') && !key.includes(this.currentUser)) {
          const messages = JSON.parse(localStorage.getItem(key) || '[]');
          const lastMessage = messages[messages.length - 1];
          
          if (lastMessage && lastMessage.sender !== this.currentUser) {
            const processedKey = 'processed_' + lastMessage.id;
            if (!localStorage.getItem(processedKey)) {
              localStorage.setItem(processedKey, 'true');
              this.messageCallback?.(lastMessage);
            }
          }
        }
      }
    } catch (error) {
      console.error('Œ£œÜŒ¨ŒªŒºŒ± ŒµŒªŒ≠Œ≥œáŒøœÖ ŒΩŒ≠œâŒΩ ŒºŒ∑ŒΩœÖŒºŒ¨œÑœâŒΩ:', error);
    }
  }

  handleNotification(event) {
    try {
      const decoder = new TextDecoder();
      const data = decoder.decode(event.target.value);
      
      let messageData;
      try {
        messageData = JSON.parse(data);
      } catch {
        messageData = {
          type: 'roottly_message',
          text: data,
          timestamp: new Date().toLocaleTimeString('el-GR', { hour: '2-digit', minute: '2-digit' }),
          sender: 'ŒëœÄŒøŒºŒ±Œ∫œÅœÖœÉŒºŒ≠ŒΩŒ∑ Œ£œÖœÉŒ∫ŒµœÖŒÆ',
          id: Date.now()
        };
      }
      
      if (messageData.type === 'roottly_message' && messageData.sender !== this.currentUser) {
        this.messageCallback?.(messageData);
      }
      
    } catch (error) {
      console.error('ŒëœÄŒøœÑœÖœáŒØŒ± œáŒµŒπœÅŒπœÉŒºŒøœç ŒµŒπŒ¥ŒøœÄŒøŒØŒ∑œÉŒ∑œÇ:', error);
    }
  }

  chunkArray(array, chunkSize) {
    const chunks = [];
    for (let i = 0; i < array.length; i += chunkSize) {
      chunks.push(array.slice(i, i + chunkSize));
    }
    return chunks;
  }

  onDisconnected() {
    console.log('Œó œÉœÖœÉŒ∫ŒµœÖŒÆ Bluetooth Œ±œÄŒøœÉœÖŒΩŒ¥Œ≠Œ∏Œ∑Œ∫Œµ');
    this.isConnected = false;
    this.device = null;
    this.server = null;
    this.service = null;
    this.characteristic = null;
    
    if (this.messageInterval) {
      clearInterval(this.messageInterval);
      this.messageInterval = null;
    }
    
    this.onConnectionStatusChanged?.(false);
  }

  async disconnect() {
    try {
      if (this.messageInterval) {
        clearInterval(this.messageInterval);
        this.messageInterval = null;
      }
      
      if (this.server && this.server.connected) {
        await this.server.disconnect();
      }
      
      this.onDisconnected();
      console.log('ŒëœÄŒøœÉœÖŒΩŒ¥Œ≠Œ∏Œ∑Œ∫Œµ ŒµœÄŒπœÑœÖœáœéœÇ');
      
    } catch (error) {
      console.error('Œ£œÜŒ¨ŒªŒºŒ± Œ∫Œ±œÑŒ¨ œÑŒ∑ŒΩ Œ±œÄŒøœÉœçŒΩŒ¥ŒµœÉŒ∑:', error);
      this.onDisconnected();
    }
  }

  getConnectionStatus() {
    return {
      isConnected: this.isConnected,
      deviceName: this.device?.name || 'ŒÜŒ≥ŒΩœâœÉœÑŒ∑',
      hasNotifications: this.characteristic?.properties?.notify || false
    };
  }

  async testConnection() {
    try {
      await this.sendMessage('ping_test');
      return true;
    } catch (error) {
      console.error('Œü Œ≠ŒªŒµŒ≥œáŒøœÇ œÉœçŒΩŒ¥ŒµœÉŒ∑œÇ Œ±œÄŒ≠œÑœÖœáŒµ:', error);
      return false;
    }
  }

  clearStoredMessages() {
    try {
      const keysToRemove = [];
      for (let i = 0; i < localStorage.length; i++) {
        const key = localStorage.key(i);
        if (key && (key.startsWith('roottly_session_') || key.startsWith('processed_'))) {
          keysToRemove.push(key);
        }
      }
      
      keysToRemove.forEach(key => localStorage.removeItem(key));
      console.log('ŒîŒπŒ±Œ≥œÅŒ¨œÜŒ∑Œ∫Œ±ŒΩ œÑŒ± Œ±œÄŒøŒ∏Œ∑Œ∫ŒµœÖŒºŒ≠ŒΩŒ± ŒºŒ∑ŒΩœçŒºŒ±œÑŒ±');
      
    } catch (error) {
      console.error('Œ£œÜŒ¨ŒªŒºŒ± Œ¥ŒπŒ±Œ≥œÅŒ±œÜŒÆœÇ Œ±œÄŒøŒ∏Œ∑Œ∫ŒµœÖŒºŒ≠ŒΩœâŒΩ ŒºŒ∑ŒΩœÖŒºŒ¨œÑœâŒΩ:', error);
    }
  }
}

