          buttonSizeClasses={buttonSizeClasses}
          textSize={textSize}
          isConnected={isConnected}
          sendBluetoothMessage={sendBluetoothMessage}
        />
      );
      
    case 'lessons':
      return (
        <Lessons
          setCurrentView={setCurrentView}
          completedLessons={completedLessons}
          completeLesson={completeLesson}
          gameScore={gameScore}
          setGameScore={setGameScore}
          t={t}
          textSizeClasses={textSizeClasses}
          buttonSizeClasses={buttonSizeClasses}
          textSize={textSize}
          language={language}
        />
      );
      
    case 'socialShare':
      return (
        <SocialShare
          setCurrentView={setCurrentView}
          grandparentName={grandparentName}
          grandparentDevice={grandparentDevice}
          userName={userName}
          setMessages={setMessages}
          messages={messages}
          t={t}
          textSizeClasses={textSizeClasses}
          buttonSizeClasses={buttonSizeClasses}
          textSize={textSize}
          language={language}
        />
      );

    case 'avatarSelection':
      return (
        <AvatarSelection
          setCurrentView={setCurrentView}
          userRole={userRole}
          selectedAvatar={selectedAvatar}
          setSelectedAvatar={setSelectedAvatar}
          selectedAccessories={selectedAccessories}
          setSelectedAccessories={setSelectedAccessories}
          gameScore={gameScore}
          setGameScore={setGameScore}
          t={t}
          textSizeClasses={textSizeClasses}
          buttonSizeClasses={buttonSizeClasses}
          textSize={textSize}
        />
      );

    case 'rewardsCenter':
      return (
        <RewardsCenter
          setCurrentView={setCurrentView}
          userRole={userRole}
          gameScore={gameScore}
          setGameScore={setGameScore}
          completedLessons={completedLessons}
          unlockedFeatures={unlockedFeatures}
          setUnlockedFeatures={setUnlockedFeatures}
          claimedRewards={claimedRewards}
          setClaimedRewards={setClaimedRewards}
          userName={userName}
          t={t}
          textSizeClasses={textSizeClasses}
          buttonSizeClasses={buttonSizeClasses}
          textSize={textSize}
        />
      );
      
    default:
      return <SplashScreen setCurrentView={setCurrentView} />;
  }
}
