import React, { useState, useEffect } from 'react';
import { Phone, MessageSquare, Video, Settings as SettingsIcon, Home, Users, Sparkles, Mail, Share2, Book, Trophy, Gift } from 'lucide-react';

// Import translations
import { translations } from './translations/translations';

// Import components
import Settings from './components/Settings';
import Activities from './components/Activities';
import Messages from './components/Messages';
import VideoCall from './components/VideoCall';
import Lessons from './components/Lessons';
import SocialShare from './components/SocialShare';
import Onboarding from './components/Onboarding';
import ActivityWheel from './components/ActivityWheel';
import AvatarSelection from './components/AvatarSelection';
import RewardsCenter from './components/RewardsCenter';

// Import CSS
import './App.css';

export default function App() {
  // State management
  const [language, setLanguage] = useState('el');
  const [textSize, setTextSize] = useState('medium');
  const [currentView, setCurrentView] = useState('onboarding');
  const [onboardingStep, setOnboardingStep] = useState(1);
  
  // User data
  const [userRole, setUserRole] = useState('');
  const [userName, setUserName] = useState('');
  const [grandparentName, setGrandparentName] = useState('');
  const [grandparentPhone, setGrandparentPhone] = useState('');
  const [grandparentDevice, setGrandparentDevice] = useState('');
  const [userInterests, setUserInterests] = useState([]);
  const [grandparentInterests, setGrandparentInterests] = useState([]);
  
  // App data
  const [messages, setMessages] = useState([]);
  const [messageInput, setMessageInput] = useState('');
  const [showVideoCall, setShowVideoCall] = useState(false);
  const [gameScore, setGameScore] = useState(50);
  const [completedLessons, setCompletedLessons] = useState([]);
  const [selectedAvatar, setSelectedAvatar] = useState('elder_man');
  const [selectedAccessories, setSelectedAccessories] = useState([]);
  const [unlockedFeatures, setUnlockedFeatures] = useState([]);
  
  const t = translations[language];
  
  // Text size classes
  const textSizeClasses = {
    small: 'text-base',
    medium: 'text-lg',
    large: 'text-xl'
  };
  
  const buttonSizeClasses = {
    small: 'p-3 text-base',
    medium: 'p-4 text-lg',
    large: 'p-5 text-xl'
  };
  
  // Toggle interest
  const toggleInterest = (interest, isGrandparent = false) => {
    if (isGrandparent) {
      setGrandparentInterests(prev =>
        prev.includes(interest)
          ? prev.filter(i => i !== interest)
          : [...prev, interest]
      );
    } else {
      setUserInterests(prev =>
        prev.includes(interest)
          ? prev.filter(i => i !== interest)
          : [...prev, interest]
      );
    }
  };

  // Add lesson completion with points
  const completeLesson = (lessonId) => {
    if (!completedLessons.includes(lessonId)) {
      setCompletedLessons(prev => [...prev, lessonId]);
      setGameScore(prev => prev + 15);
    }
  };

  // Get current avatar with accessories
  const getCurrentAvatarDisplay = () => {
    const avatars = {
      'elder_man': '👨‍🦳',
      'elder_woman': '👵',
      'young_man': '👦',
      'young_woman': '👧',
      'happy_elder': '😊👴',
      'cool_elder': '😎👵',
      'wise_elder': '🧙‍♂️',
      'tech_elder': '🤓👴',
      'superhero': '🦸‍♂️',
      'princess': '👸'
    };
    
    const accessories = {
      'hat_basic': '🧢',
      'hat_formal': '🎩',
      'crown': '👑',
      'glasses': '👓',
      'sunglasses': '🕶️',
      'bow_tie': '🎀',
      'flower': '🌸',
      'medal': '🏅',
      'star': '⭐',
      'heart': '💖'
    };
    
    const avatar = avatars[selectedAvatar] || '👨‍🦳';
    const accessoryEmojis = selectedAccessories.map(id => accessories[id] || '').join('');
    return avatar + accessoryEmojis;
  };

  // Long press help functionality
  const [pressTimer, setPressTimer] = useState(null);
  
  const handleLongPressStart = (helpText) => {
    const timer = setTimeout(() => {
      alert(helpText);
    }, 1000);
    setPressTimer(timer);
  };

  const handleLongPressEnd = () => {
    if (pressTimer) {
      clearTimeout(pressTimer);
      setPressTimer(null);
    }
  };
  
  // Render Grandparent Home
  const renderGrandparentHome = () => (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-50">
      <header className="bg-gradient-to-r from-purple-500 to-pink-500 text-white p-6 shadow-lg">
        <div className="flex items-center justify-between">
          <div>
            <h1 className={`${textSizeClasses[textSize]} font-bold`}>
              Γεια σας, {userName}! 🌟
            </h1>
            <div className="flex items-center gap-4 mt-2">
              <div className="flex items-center gap-2">
                <Trophy className="w-6 h-6 text-yellow-300" />
                <span className="text-yellow-300 font-semibold text-xl">{gameScore} πόντοι</span>
              </div>
            </div>
          </div>
          <button
            onClick={() => setCurrentView('avatarSelection')}
            className="text-8xl hover:scale-110 transition-transform cursor-pointer"
            onMouseDown={() => handleLongPressStart('Πατήστε για να αλλάξετε το avatar σας!')}
            onMouseUp={handleLongPressEnd}
            onTouchStart={() => handleLongPressStart('Πατήστε για να αλλάξετε το avatar σας!')}
            onTouchEnd={handleLongPressEnd}
          >
            {getCurrentAvatarDisplay()}
          </button>
        </div>
      </header>
      
      <main className="p-6 pb-32">
        <div className="grid grid-cols-2 gap-6 max-w-3xl mx-auto">
          <button
            onClick={() => setCurrentView('messages')}
            onMouseDown={() => handleLongPressStart('Στείλτε μηνύματα στην οικογένειά σας!')}
            onMouseUp={handleLongPressEnd}
            onTouchStart={() => handleLongPressStart('Στείλτε μηνύματα στην οικογένειά σας!')}
            onTouchEnd={handleLongPressEnd}
            className={`${buttonSizeClasses[textSize]} bg-gradient-to-r from-blue-400 to-blue-600 text-white rounded-3xl shadow-xl hover:shadow-2xl transition-all transform hover:scale-105 border-4 border-white`}
          >
            <MessageSquare className="w-16 h-16 mx-auto mb-3" />
            <div className="text-2xl font-bold">Μηνύματα</div>
          </button>
          
          <button
            onClick={() => setShowVideoCall(true)}
            onMouseDown={() => handleLongPressStart('Κάντε βιντεοκλήση με την οικογένειά σας!')}
            onMouseUp={handleLongPressEnd}
            onTouchStart={() => handleLongPressStart('Κάντε βιντεοκλήση με την οικογένειά σας!')}
            onTouchEnd={handleLongPressEnd}
            className={`${buttonSizeClasses[textSize]} bg-gradient-to-r from-green-400 to-green-600 text-white rounded-3xl shadow-xl hover:shadow-2xl transition-all transform hover:scale-105 border-4 border-white`}
          >
            <Video className="w-16 h-16 mx-auto mb-3" />
            <div className="text-2xl font-bold">Βιντεοκλήση</div>
          </button>
          
          <button
            onClick={() => setCurrentView('activityWheel')}
            onMouseDown={() => handleLongPressStart('Περιστρέψτε τη ρόδα για τυχαία δραστηριότητα!')}
            onMouseUp={handleLongPressEnd}
            onTouchStart={() => handleLongPressStart('Περιστρέψτε τη ρόδα για τυχαία δραστηριότητα!')}
            onTouchEnd={handleLongPressEnd}
            className={`${buttonSizeClasses[textSize]} bg-gradient-to-r from-purple-400 to-purple-600 text-white rounded-3xl shadow-xl hover:shadow-2xl transition-all transform hover:scale-105 border-4 border-white`}
          >
            <Sparkles className="w-16 h-16 mx-auto mb-3" />
            <div className="text-2xl font-bold">Δραστηριότητες</div>
          </button>
          
          <button
            onClick={() => setCurrentView('lessons')}
            onMouseDown={() => handleLongPressStart('Μάθετε νέες δεξιότητες τεχνολογίας!')}
            onMouseUp={handleLongPressEnd}
            onTouchStart={() => handleLongPressStart('Μάθετε νέες δεξιότητες τεχνολογίας!')}
            onTouchEnd={handleLongPressEnd}
            className={`${buttonSizeClasses[textSize]} bg-gradient-to-r from-orange-400 to-orange-600 text-white rounded-3xl shadow-xl hover:shadow-2xl transition-all transform hover:scale-105 border-4 border-white relative`}
          >
            <Book className="w-16 h-16 mx-auto mb-3" />
            <div className="text-2xl font-bold">Μαθήματα</div>
            {completedLessons.length > 0 && (
              <div className="absolute -top-2 -right-2 bg-yellow-400 text-yellow-900 px-3 py-1 rounded-full text-lg font-bold border-2 border-white">
                {completedLessons.length}/5 ✓
              </div>
            )}
          </button>
        </div>

        <div className="grid grid-cols-2 gap-6 max-w-3xl mx-auto mt-6">
          <button
            onClick={() => setCurrentView('rewardsCenter')}
            onMouseDown={() => handleLongPressStart('Δείτε τις ανταμοιβές σας και αγοράστε νέα πράγματα!')}
            onMouseUp={handleLongPressEnd}
            onTouchStart={() => handleLongPressStart('Δείτε τις ανταμοιβές σας και αγοράστε νέα πράγματα!')}
            onTouchEnd={handleLongPressEnd}
            className={`${buttonSizeClasses[textSize]} bg-gradient-to-r from-yellow-400 to-yellow-600 text-white rounded-3xl shadow-xl hover:shadow-2xl transition-all transform hover:scale-105 border-4 border-white`}
          >
            <Gift className="w-16 h-16 mx-auto mb-3" />
            <div className="text-2xl font-bold">Ανταμοιβές</div>
          </button>

          <button
            onClick={() => setCurrentView('settings')}
            onMouseDown={() => handleLongPressStart('Αλλάξτε ρυθμίσεις και προσαρμόστε την εφαρμογή!')}
            onMouseUp={handleLongPressEnd}
            onTouchStart={() => handleLongPressStart('Αλλάξτε ρυθμίσεις και προσαρμόστε την εφαρμογή!')}
            onTouchEnd={handleLongPressEnd}
            className={`${buttonSizeClasses[textSize]} bg-gradient-to-r from-gray-400 to-gray-600 text-white rounded-3xl shadow-xl hover:shadow-2xl transition-all transform hover:scale-105 border-4 border-white`}
          >
            <SettingsIcon className="w-16 h-16 mx-auto mb-3" />
            <div className="text-2xl font-bold">Ρυθμίσεις</div>
          </button>
        </div>

        <div className="mt-8 max-w-3xl mx-auto">
          <div className="bg-gradient-to-r from-yellow-100 to-orange-100 border-4 border-yellow-400 rounded-2xl p-6 shadow-lg">
            <div className="flex items-center gap-4">
              <div className="text-4xl">💡</div>
              <div>
                <h3 className={`${textSizeClasses[textSize]} font-bold text-yellow-800 mb-2`}>
                  Σημερινή Συμβουλή
                </h3>
                <p className="text-yellow-700 text-lg">
                  Κρατήστε πατημένο οποιοδήποτε κουμπί για 1 δευτερόλεπτο να δείτε τι κάνει!
                </p>
              </div>
            </div>
          </div>
        </div>
      </main>
      
      <nav className="fixed bottom-0 left-0 right-0 bg-white border-t-4 border-purple-200 px-6 py-4 shadow-2xl">
        <div className="flex justify-center">
          <button 
            className="bg-purple-500 text-white p-4 rounded-full shadow-lg"
            onMouseDown={() => handleLongPressStart('Είστε στην αρχική σελίδα!')}
            onMouseUp={handleLongPressEnd}
            onTouchStart={() => handleLongPressStart('Είστε στην αρχική σελίδα!')}
            onTouchEnd={handleLongPressEnd}
          >
            <Home className="w-10 h-10" />
          </button>
        </div>
      </nav>
    </div>
  );
  
  // Render Child Home
  const renderChildHome = () => (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-green-50">
      <header className="bg-gradient-to-r from-blue-500 to-green-500 text-white p-6 shadow-lg">
        <div className="flex items-center justify-between">
          <div>
            <h1 className={`${textSizeClasses[textSize]} font-bold`}>
              {t.welcome}, {userName}! 👋
            </h1>
            <p className="text-sm mt-1">
              📱 {grandparentName} - {grandparentDevice === 'basic' ? t.basicPhone : t.smartphone}
            </p>
          </div>
          <button
            onClick={() => setCurrentView('avatarSelection')}
            className="text-6xl hover:scale-110 transition-transform cursor-pointer"
          >
            {getCurrentAvatarDisplay()}
          </button>
        </div>
      </header>
      
      <main className="p-4 pb-24">
        <div className="grid grid-cols-2 gap-4 max-w-2xl mx-auto">
          {grandparentDevice === 'basic' ? (
            <button
              onClick={() => setCurrentView('messages')}
              className={`${buttonSizeClasses[textSize]} bg-gradient-to-r from-blue-400 to-blue-600 text-white rounded-2xl shadow-lg hover:shadow-xl transition-all transform hover:scale-105`}
            >
              <Mail className="w-12 h-12 mx-auto mb-2" />
              {t.sendSMS}
            </button>
          ) : (
            <button
              onClick={() => setCurrentView('messages')}
              className={`${buttonSizeClasses[textSize]} bg-gradient-to-r from-blue-400 to-blue-600 text-white rounded-2xl shadow-lg hover:shadow-xl transition-all transform hover:scale-105`}
            >
              <MessageSquare className="w-12 h-12 mx-auto mb-2" />
              {t.message}
            </button>
          )}
          
          <button
            onClick={() => setCurrentView('socialShare')}
            className={`${buttonSizeClasses[textSize]} bg-gradient-to-r from-purple-400 to-purple-600 text-white rounded-2xl shadow-lg hover:shadow-xl transition-all transform hover:scale-105`}
          >
            <Share2 className="w-12 h-12 mx-auto mb-2" />
            {t.socialShare}
          </button>
          
          {grandparentDevice === 'smartphone' && (
            <>
              <button
                onClick={() => setShowVideoCall(true)}
                className={`${buttonSizeClasses[textSize]} bg-gradient-to-r from-green-400 to-green-600 text-white rounded-2xl shadow-lg hover:shadow-xl transition-all transform hover:scale-105`}
              >
                <Video className="w-12 h-12 mx-auto mb-2" />
                {t.videoCall}
              </button>
              
              <button
                onClick={() => setCurrentView('activityWheel')}
                className={`${buttonSizeClasses[textSize]} bg-gradient-to-r from-orange-400 to-orange-600 text-white rounded-2xl shadow-lg hover:shadow-xl transition-all transform hover:scale-105`}
              >
                <Sparkles className="w-12 h-12 mx-auto mb-2" />
                {t.activities}
              </button>
            </>
          )}
        </div>

        <div className="mt-8 max-w-2xl mx-auto">
          <div className="bg-green-100 border-l-4 border-green-500 p-4 rounded-r-lg">
            <p className={`${textSizeClasses[textSize]} text-green-800 flex items-center gap-2`}>
              <div className="w-3 h-3 bg-green-500 rounded-full animate-pulse"></div>
              {t.connectedWith} {grandparentName}
            </p>
          </div>
        </div>
      </main>
      
      <nav className="fixed bottom-0 left-0 right-0 bg-white border-t-2 border-gray-200 px-4 py-2">
        <div className="flex justify-around items-center max-w-md mx-auto">
          <button className={`${buttonSizeClasses[textSize]} text-blue-500`}>
            <Home className="w-8 h-8" />
          </button>
          <button
            onClick={() => setCurrentView('settings')}
            className={`${buttonSizeClasses[textSize]} text-gray-600`}
          >
            <SettingsIcon className="w-8 h-8" />
          </button>
        </div>
      </nav>
    </div>
  );
  
  // Prevent zooming on double tap
  useEffect(() => {
    document.addEventListener('gesturestart', e => e.preventDefault());
    const metaViewport = document.querySelector('meta[name=viewport]');
    if (metaViewport) {
      metaViewport.setAttribute('content', 
        'width=device-width, initial-scale=1.0, maximum-scale=1.0, user-scalable=no'
      );
    }
  }, []);
  
  // Show video call overlay
  if (showVideoCall) {
    return <VideoCall 
      contactName={userRole === 'child' ? grandparentName : userName} 
      onEndCall={() => setShowVideoCall(false)}
      language={language}
      textSizeClasses={textSizeClasses}
      textSize={textSize}
    />;
  }
  
  // Main render
  switch (currentView) {
    case 'onboarding':
      return (
        <Onboarding
          onboardingStep={onboardingStep}
          setOnboardingStep={setOnboardingStep}
          userRole={userRole}
          setUserRole={setUserRole}
          userName={userName}
          setUserName={setUserName}
          grandparentName={grandparentName}
          setGrandparentName={setGrandparentName}
          grandparentPhone={grandparentPhone}
          setGrandparentPhone={setGrandparentPhone}
          grandparentDevice={grandparentDevice}
          setGrandparentDevice={setGrandparentDevice}
          userInterests={userInterests}
          grandparentInterests={grandparentInterests}
          toggleInterest={toggleInterest}
          setCurrentView={setCurrentView}
          t={t}
          textSizeClasses={textSizeClasses}
          buttonSizeClasses={buttonSizeClasses}
          textSize={textSize}
        />
      );
      
    case 'grandparentHome':
      return renderGrandparentHome();
      
    case 'childHome':
      return renderChildHome();
      
    case 'avatarSelection':
      return (
        <AvatarSelection
          setCurrentView={setCurrentView}
          userRole={userRole}
          selectedAvatar={selectedAvatar}
          setSelectedAvatar={setSelectedAvatar}
          selectedAccessories={selectedAccessories}
          setSelectedAccessories={setSelectedAccessories}
          gameScore={gameScore}
          t={t}
          textSizeClasses={textSizeClasses}
          buttonSizeClasses={buttonSizeClasses}
          textSize={textSize}
        />
      );

    case 'rewardsCenter':
      return (
        <RewardsCenter
          setCurrentView={setCurrentView}
          userRole={userRole}
          gameScore={gameScore}
          setGameScore={setGameScore}
          completedLessons={completedLessons}
          unlockedFeatures={unlockedFeatures}
          setUnlockedFeatures={setUnlockedFeatures}
          t={t}
          textSizeClasses={textSizeClasses}
          buttonSizeClasses={buttonSizeClasses}
          textSize={textSize}
        />
      );
      
    case 'settings':
      return (
        <Settings
          language={language}
          setLanguage={setLanguage}
          textSize={textSize}
          setTextSize={setTextSize}
          setCurrentView={setCurrentView}
          userRole={userRole}
          t={t}
          textSizeClasses={textSizeClasses}
          buttonSizeClasses={buttonSizeClasses}
        />
      );
      
    case 'activityWheel':
      return (
        <ActivityWheel
          userRole={userRole}
          userInterests={userInterests}
          grandparentInterests={grandparentInterests}
          setCurrentView={setCurrentView}
          t={t}
          textSizeClasses={textSizeClasses}
          buttonSizeClasses={buttonSizeClasses}
          textSize={textSize}
          language={language}
        />
      );
      
    case 'activities':
      return (
        <Activities
          userRole={userRole}
          userInterests={userInterests}
          grandparentInterests={grandparentInterests}
          setCurrentView={setCurrentView}
          t={t}
          textSizeClasses={textSizeClasses}
          buttonSizeClasses={buttonSizeClasses}
          textSize={textSize}
          language={language}
        />
      );
      
    case 'messages':
      return (
        <Messages
          messages={messages}
          messageInput={messageInput}
          setMessageInput={setMessageInput}
          setMessages={setMessages}
          setCurrentView={setCurrentView}
          userRole={userRole}
          grandparentName={grandparentName}
          grandparentDevice={grandparentDevice}
          userName={userName}
          grandparentPhone={grandparentPhone}
          t={t}
          textSizeClasses={textSizeClasses}
          buttonSizeClasses={buttonSizeClasses}
          textSize={textSize}
        />
      );
      
    case 'lessons':
      return (
        <Lessons
          setCurrentView={setCurrentView}
          completedLessons={completedLessons}
          completeLesson={completeLesson}
          gameScore={gameScore}
          setGameScore={setGameScore}
          t={t}
          textSizeClasses={textSizeClasses}
          buttonSizeClasses={buttonSizeClasses}
          textSize={textSize}
          language={language}
        />
      );
      
    case 'socialShare':
      return (
        <SocialShare
          setCurrentView={setCurrentView}
          grandparentName={grandparentName}
          grandparentDevice={grandparentDevice}
          userName={userName}
          setMessages={setMessages}
          messages={messages}
          t={t}
          textSizeClasses={textSizeClasses}
          buttonSizeClasses={buttonSizeClasses}
          textSize={textSize}
          language={language}
        />
      );
      
    default:
      return renderGrandparentHome();
  }
}
