import React, { useEffect, useState } from 'react';
import { Wifi, Users, Heart, Sparkles } from 'lucide-react';

const SplashScreen = ({ setCurrentView }) => {
  const [progress, setProgress] = useState(0);
  const [currentMessage, setCurrentMessage] = useState(0);

  const loadingMessages = [
    "Φορτώνει...",
    "Προετοιμάζει την οικογενειακή επικοινωνία...",
    "Συνδέει γενιές...",
    "Σχεδόν έτοιμο!"
  ];

  // Roottly Logo Component
  const RoottlyLogo = ({ size = 'xlarge', animated = true }) => {
    return (
      <div className="flex flex-col items-center">
        <div className={`${size === 'xlarge' ? 'w-48 h-48' : 'w-32 h-32'} relative mb-8`}>
          {/* Main logo container */}
          <div className={`w-full h-full bg-gradient-to-br from-blue-500 to-orange-500 rounded-3xl shadow-2xl flex items-center justify-center relative overflow-hidden ${animated ? 'animate-pulse' : ''}`}>
            
            {/* Animated connection circles representing family bonds */}
            <div className="absolute inset-4">
              {/* Grandparent circle */}
              <div className={`w-6 h-6 bg-white rounded-full absolute top-2 left-2 opacity-90 ${animated ? 'animate-bounce' : ''}`} 
                   style={animated ? {animationDelay: '0s', animationDuration: '2s'} : {}}>
              </div>
              
              {/* Child circle */}
              <div className={`w-8 h-8 bg-white rounded-full absolute top-3 right-2 opacity-80 ${animated ? 'animate-bounce' : ''}`}
                   style={animated ? {animationDelay: '0.5s', animationDuration: '2s'} : {}}>
              </div>
              
              {/* Family member circles */}
              <div className={`w-4 h-4 bg-white rounded-full absolute bottom-3 left-3 opacity-85 ${animated ? 'animate-bounce' : ''}`}
                   style={animated ? {animationDelay: '1s', animationDuration: '2s'} : {}}>
              </div>
              
              <div className={`w-5 h-5 bg-white rounded-full absolute bottom-2 right-3 opacity-75 ${animated ? 'animate-bounce' : ''}`}
                   style={animated ? {animationDelay: '1.5s', animationDuration: '2s'} : {}}>
              </div>
              
              {/* Connecting lines - animated */}
              <svg className="absolute inset-0 w-full h-full" viewBox="0 0 100 100">
                <defs>
                  <linearGradient id="lineGradient" x1="0%" y1="0%" x2="100%" y2="100%">
                    <stop offset="0%" stopColor="white" stopOpacity="0.8"/>
                    <stop offset="100%" stopColor="white" stopOpacity="0.4"/>
                  </linearGradient>
                </defs>
                
                {/* Connection lines between family members */}
                <path 
                  d="M25 25 Q50 45 75 35" 
                  stroke="url(#lineGradient)" 
                  strokeWidth="3" 
                  fill="none" 
                  className={animated ? 'animate-pulse' : ''}
                  style={animated ? {animationDuration: '3s'} : {}}
                />
                <path 
                  d="M30 75 Q50 55 70 70" 
                  stroke="url(#lineGradient)" 
                  strokeWidth="3" 
                  fill="none" 
                  className={animated ? 'animate-pulse' : ''}
                  style={animated ? {animationDelay: '1s', animationDuration: '3s'} : {}}
                />
                <path 
                  d="M25 25 Q35 50 30 75" 
                  stroke="url(#lineGradient)" 
                  strokeWidth="2" 
                  fill="none" 
                  className={animated ? 'animate-pulse' : ''}
                  style={animated ? {animationDelay: '2s', animationDuration: '3s'} : {}}
                />
                <path 
                  d="M75 35 Q65 50 70 70" 
                  stroke="url(#lineGradient)" 
                  strokeWidth="2" 
                  fill="none" 
                  className={animated ? 'animate-pulse' : ''}
                  style={animated ? {animationDelay: '0.5s', animationDuration: '3s'} : {}}
                />
              </svg>
            </div>
            
            {/* Central R for Roottly */}
            <div className={`text-white font-bold text-6xl z-10 ${animated ? 'animate-pulse' : ''}`}>
              R
            </div>
            
            {/* Decorative elements */}
            <div className="absolute top-4 right-4">
              <Heart className={`w-4 h-4 text-white opacity-60 ${animated ? 'animate-ping' : ''}`} 
                     style={animated ? {animationDelay: '2s', animationDuration: '2s'} : {}} />
            </div>
            <div className="absolute bottom-4 left-4">
              <Users className={`w-4 h-4 text-white opacity-60 ${animated ? 'animate-ping' : ''}`}
                     style={animated ? {animationDelay: '3s', animationDuration: '2s'} : {}} />
            </div>
          </div>
        </div>
        
        {/* App name and tagline */}
        <div className="text-center">
          <div className={`text-6xl font-bold bg-gradient-to-r from-blue-600 to-orange-600 bg-clip-text text-transparent mb-4 ${animated ? 'animate-pulse' : ''}`}>
            Roottly
          </div>
          <div className="text-2xl text-gray-600 font-medium mb-2">
            Συνδέοντας γενιές
          </div>
          <div className="text-lg text-gray-500 flex items-center justify-center gap-2">
            <Heart className="w-5 h-5 text-red-400" />
            Οικογενειακή επικοινωνία
            <Heart className="w-5 h-5 text-red-400" />
          </div>
        </div>
      </div>
    );
  };

  // Progress animation
  useEffect(() => {
    const progressTimer = setInterval(() => {
      setProgress(prev => {
        if (prev >= 100) {
          clearInterval(progressTimer);
          return 100;
        }
        return prev + 2;
      });
    }, 60);

    return () => clearInterval(progressTimer);
  }, []);

  // Message rotation
  useEffect(() => {
    const messageTimer = setInterval(() => {
      setCurrentMessage(prev => (prev + 1) % loadingMessages.length);
    }, 1000);

    return () => clearInterval(messageTimer);
  }, []);

  // Auto advance after animation
  useEffect(() => {
    const timer = setTimeout(() => {
      setCurrentView('onboarding');
    }, 4000);

    return () => clearTimeout(timer);
  }, [setCurrentView]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-orange-50 flex items-center justify-center p-4">
      <div className="text-center w-full max-w-lg">
        {/* Main logo */}
        <RoottlyLogo size="xlarge" animated={true} />
        
        {/* Loading message */}
        <div className="mt-12 mb-8">
          <p className="text-2xl text-gray-600 font-medium animate-fade-in">
            {loadingMessages[currentMessage]}
          </p>
        </div>
        
        {/* Progress bar */}
        <div className="w-full max-w-md mx-auto mb-8">
          <div className="w-full bg-gray-200 rounded-full h-4 overflow-hidden shadow-inner">
            <div 
              className="h-4 bg-gradient-to-r from-blue-500 to-orange-500 rounded-full transition-all duration-300 ease-out shadow-sm"
              style={{ width: `${progress}%` }}
            >
              <div className="h-full bg-white bg-opacity-30 animate-pulse"></div>
            </div>
          </div>
          <div className="text-center mt-3">
            <span className="text-lg font-semibold text-gray-700">{progress}%</span>
          </div>
        </div>
        
        {/* Feature highlights */}
        <div className="grid grid-cols-3 gap-6 mt-12 opacity-60">
          <div className="text-center">
            <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-2">
              <Users className="w-6 h-6 text-blue-600" />
            </div>
            <p className="text-sm text-gray-600">Οικογένεια</p>
          </div>
          
          <div className="text-center">
            <div className="w-12 h-12 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-2">
              <Wifi className="w-6 h-6 text-green-600" />
            </div>
            <p className="text-sm text-gray-600">Σύνδεση</p>
          </div>
          
          <div className="text-center">
            <div className="w-12 h-12 bg-orange-100 rounded-full flex items-center justify-center mx-auto mb-2">
              <Sparkles className="w-6 h-6 text-orange-600" />
            </div>
            <p className="text-sm text-gray-600">Διασκέδαση</p>
          </div>
        </div>
        
        {/* Version info */}
        <div className="mt-8 text-center">
          <p className="text-sm text-gray-400">
            Έκδοση 1.0.0 • Made with ❤️ in Greece
          </p>
        </div>
      </div>

      {/* Background decorative elements */}
      <div className="fixed inset-0 pointer-events-none overflow-hidden">
        <div className="absolute top-20 left-20 w-32 h-32 bg-blue-200 rounded-full opacity-20 animate-float"></div>
        <div className="absolute top-40 right-32 w-24 h-24 bg-orange-200 rounded-full opacity-20 animate-float" style={{animationDelay: '2s'}}></div>
        <div className="absolute bottom-32 left-40 w-20 h-20 bg-green-200 rounded-full opacity-20 animate-float" style={{animationDelay: '4s'}}></div>
        <div className="absolute bottom-20 right-20 w-28 h-28 bg-purple-200 rounded-full opacity-20 animate-float" style={{animationDelay: '1s'}}></div>
      </div>
    </div>
  );
};

export default SplashScreen;
