          t={t}
          textSizeClasses={textSizeClasses}
          buttonSizeClasses={buttonSizeClasses}
          textSize={textSize}
        />
      );
      
    case 'grandparentHome':
      return renderGrandparentHome();
      
    case 'childHome':
      return renderChildHome();
      
    case 'avatarSelection':
      return (
        <AvatarSelection
          setCurrentView={setCurrentView}
          userRole={userRole}
          selectedAvatar={selectedAvatar}
          setSelectedAvatar={setSelectedAvatar}
          selectedAccessories={selectedAccessories}
          setSelectedAccessories={setSelectedAccessories}
          gameScore={gameScore}
          setGameScore={setGameScore}
          t={t}
          textSizeClasses={textSizeClasses}
          buttonSizeClasses={buttonSizeClasses}
          textSize={textSize}
        />
      );

    case 'rewardsCenter':
      return (
        <RewardsCenter
          setCurrentView={setCurrentView}
          userRole={userRole}
          gameScore={gameScore}
          setGameScore={setGameScore}
          completedLessons={completedLessons}
          unlockedFeatures={unlockedFeatures}
          setUnlockedFeatures={setUnlockedFeatures}
          t={t}
          textSizeClasses={textSizeClasses}
          buttonSizeClasses={buttonSizeClasses}
          textSize={textSize}
        />
      );
      
    case 'settings':
      return (
        <Settings
          language={language}
          setLanguage={setLanguage}
          textSize={textSize}
          setTextSize={setTextSize}
          setCurrentView={setCurrentView}
          userRole={userRole}
          t={t}
          textSizeClasses={textSizeClasses}
          buttonSizeClasses={buttonSizeClasses}
        />
      );
      
    case 'activityWheel':
      return (
        <ActivityWheel
          userRole={userRole}
          userInterests={userInterests}
          grandparentInterests={grandparentInterests}
          setCurrentView={setCurrentView}
          t={t}
          textSizeClasses={textSizeClasses}
          buttonSizeClasses={buttonSizeClasses}
          textSize={textSize}
          language={language}
        />
      );
      
    case 'messages':
      return (
        <Messages
          messages={messages}
          messageInput={messageInput}
          setMessageInput={setMessageInput}
          setMessages={setMessages}
          setCurrentView={setCurrentView}
          userRole={userRole}
          grandparentName={grandparentName}
          grandparentDevice={grandparentDevice}
          userName={userName}
          grandparentPhone={grandparentPhone}
          t={t}
          textSizeClasses={textSizeClasses}
          buttonSizeClasses={buttonSizeClasses}
          textSize={textSize}
        />
      );
      
    case 'lessons':
      return (
        <Lessons
          setCurrentView={setCurrentView}
          completedLessons={completedLessons}
          completeLesson={completeLesson}
          gameScore={gameScore}
          setGameScore={setGameScore}
          t={t}
          textSizeClasses={textSizeClasses}
          buttonSizeClasses={buttonSizeClasses}
          textSize={textSize}
          language={language}
        />
      );
      
    case 'socialShare':
      return (
        <SocialShare
          setCurrentView={setCurrentView}
          grandparentName={grandparentName}
          grandparentDevice={grandparentDevice}
          userName={userName}
          setMessages={setMessages}
          messages={messages}
          t={t}
          textSizeClasses={textSizeClasses}
          buttonSizeClasses={buttonSizeClasses}
          textSize={textSize}
          language={language}
        />
      );
      
    default:
      return renderGrandparentHome();
  }
}
