import React, { useState } from 'react';
import { ChevronLeft, Send, Phone, Video, Paperclip, Smile } from 'lucide-react';

const Messages = ({ 
  messages, 
  messageInput, 
  setMessageInput, 
  setMessages,
  setCurrentView, 
  userRole, 
  grandparentName,
  grandparentDevice,
  userName,
  t, 
  textSizeClasses, 
  buttonSizeClasses,
  textSize
}) => {
  
  const [showEmojis, setShowEmojis] = useState(false);
  
  const emojis = ['üòä', '‚ù§Ô∏è', 'üëç', 'üéâ', 'üå∫', '‚òï', 'üç∞', 'üåû', 'üåô', '‚≠ê'];
  
  const handleSendMessage = () => {
    if (messageInput.trim()) {
      const newMessage = {
        id: Date.now(),
        text: messageInput,
        type: grandparentDevice === 'basic' ? 'sms' : 'sent',
        timestamp: new Date().toLocaleTimeString('el-GR', { hour: '2-digit', minute: '2-digit' })
      };
      
      setMessages([...messages, newMessage]);
      setMessageInput('');
      
      // Simulate response after 2 seconds
      setTimeout(() => {
        const responses = {
          el: ['ŒìŒµŒπŒ± œÉŒøœÖ Œ±Œ≥Œ¨œÄŒ∑ ŒºŒøœÖ! üòä', 'Œ§Œπ Œ∫Œ¨ŒΩŒµŒπœÇ;', 'Œ£Œµ Œ±Œ≥Œ±œÄœé œÄŒøŒªœç! ‚ù§Ô∏è', 'Œ†œåœÑŒµ Œ∏Œ± Œ≠œÅŒ∏ŒµŒπœÇ ŒΩŒ± ŒºŒµ Œ¥ŒµŒπœÇ;'],
          en: ['Hello my dear! üòä', 'How are you?', 'I love you so much! ‚ù§Ô∏è', 'When will you come visit?']
        };
        
        const randomResponse = responses[t.language || 'el'][Math.floor(Math.random() * responses[t.language || 'el'].length)];
        
        setMessages(prev => [...prev, {
          id: Date.now(),
          text: randomResponse,
          type: 'received',
          timestamp: new Date().toLocaleTimeString('el-GR', { hour: '2-digit', minute: '2-digit' })
        }]);
      }, 2000);
    }
  };
  
  const addEmoji = (emoji) => {
    setMessageInput(prev => prev + emoji);
    setShowEmojis(false);
  };
  
  const isGrandparentView = userRole === 'grandparent';
  const contactName = isGrandparentView ? userName : grandparentName;
  
  // Initial messages
  React.useEffect(() => {
    if (messages.length === 0) {
      const initialMessages = {
        el: [
          { id: 1, text: 'ŒöŒ±ŒªŒ∑ŒºŒ≠œÅŒ± œÄŒ±ŒπŒ¥ŒØ ŒºŒøœÖ! ‚òÄÔ∏è', type: 'received', timestamp: '09:00' },
          { id: 2, text: 'ŒöŒ±ŒªŒ∑ŒºŒ≠œÅŒ±! Œ§Œπ Œ∫Œ¨ŒΩŒµŒπœÇ;', type: 'sent', timestamp: '09:05' },
          { id: 3, text: 'ŒïŒØŒºŒ±Œπ Œ∫Œ±ŒªŒ¨! Œ£Œµ œÉŒ∫Œ≠œÜœÑŒøŒºŒ±Œπ üòä', type: 'received', timestamp: '09:06' }
        ],
        en: [
          { id: 1, text: 'Good morning dear! ‚òÄÔ∏è', type: 'received', timestamp: '09:00' },
          { id: 2, text: 'Good morning! How are you?', type: 'sent', timestamp: '09:05' },
          { id: 3, text: "I'm well! Thinking of you üòä", type: 'received', timestamp: '09:06' }
        ]
      };
      
      setMessages(initialMessages[t.language || 'el'] || initialMessages.el);
    }
  }, []);
  
  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-purple-50 flex flex-col">
      {/* Header */}
      <header className="bg-gradient-to-r from-blue-500 to-purple-500 text-white shadow-lg">
        <div className="p-4">
          <div className="flex items-center gap-4">
            <button
              onClick={() => setCurrentView(userRole === 'grandparent' ? 'grandparentHome' : 'childHome')}
              className="text-white hover:bg-white/20 rounded-lg p-2 transition-colors"
            >
              <ChevronLeft className="w-6 h-6" />
            </button>
            <div className="flex-1">
              <h1 className={`${textSizeClasses[textSize]} font-bold`}>
                {userRole === 'child' ? grandparentName : t.messages}
              </h1>
              {grandparentDevice === 'basic' && userRole === 'child' && (
                <p className="text-sm opacity-80">SMS - {grandparentPhone}</p>
              )}
            </div>
            {grandparentDevice !== 'basic' && (
              <div className="flex gap-2">
                <button className="p-2 hover:bg-white/20 rounded-lg transition-colors">
                  <Phone className="w-5 h-5" />
                </button>
                <button className="p-2 hover:bg-white/20 rounded-lg transition-colors">
                  <Video className="w-5 h-5" />
                </button>
              </div>
            )}
          </div>
        </div>
      </header>
      
      {/* Messages Area */}
      <div className="flex-1 p-4 overflow-y-auto">
        <div className="space-y-4 max-w-2xl mx-auto">
          {grandparentDevice === 'basic' && userRole === 'child' && (
            <div className="bg-yellow-100 border-2 border-yellow-300 rounded-lg p-3 mb-4 text-center">
              <p className="text-yellow-800">
                {t.smsWillBeSent} {grandparentPhone}
              </p>
            </div>
          )}
          
          {messages.map(msg => (
            <div
              key={msg.id}
              className={`flex ${msg.type === 'sent' || msg.type === 'sms' ? 'justify-end' : 'justify-start'}`}
            >
              <div
                className={`${buttonSizeClasses[textSize]} rounded-2xl max-w-xs sm:max-w-sm ${
                  msg.type === 'sent' || msg.type === 'sms'
                    ? 'bg-blue-500 text-white'
                    : 'bg-white shadow-md'
                }`}
              >
                {msg.image && <div className="text-4xl mb-2">{msg.image}</div>}
                <p className="break-words">{msg.text}</p>
                <p className={`text-xs mt-1 ${
                  msg.type === 'sent' || msg.type === 'sms' ? 'text-blue-100' : 'text-gray-500'
                }`}>
                  {msg.timestamp}
                </p>
              </div>
            </div>
          ))}
        </div>
      </div>
      
      {/* Input Area */}
      <div className="bg-white border-t-2 border-gray-200">
        {showEmojis && (
          <div className="p-3 border-b flex flex-wrap gap-2">
            {emojis.map(emoji => (
              <button
                key={emoji}
                onClick={() => addEmoji(emoji)}
                className="text-2xl p-2 hover:bg-gray-100 rounded-lg transition-colors"
              >
                {emoji}
              </button>
            ))}
          </div>
        )}
        
        <div className="p-4">
          <div className="flex gap-2 max-w-2xl mx-auto">
            <button
              onClick={() => setShowEmojis(!showEmojis)}
              className={`${buttonSizeClasses[textSize]} text-gray-600 hover:text-gray-800 transition-colors`}
            >
              <Smile className="w-6 h-6" />
            </button>
            
            <input
              type="text"
              value={messageInput}
              onChange={(e) => setMessageInput(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
              placeholder={t.typeMessage}
              className={`flex-1 ${buttonSizeClasses[textSize]} border-2 border-gray-300 rounded-lg focus:border-blue-500 focus:outline-none`}
            />
            
            <button
              onClick={handleSendMessage}
              disabled={!messageInput.trim()}
              className={`${buttonSizeClasses[textSize]} bg-blue-500 text-white rounded-lg hover:bg-blue-600 disabled:opacity-50 disabled:cursor-not-allowed transition-all px-6`}
            >
              <Send className="w-5 h-5" />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Messages;
