import React, { useState, useEffect } from 'react';
import { Phone, MessageSquare, Video, Settings as SettingsIcon, Home, Users, Sparkles, Mail, Share2, Book, Trophy, GamepadIcon } from 'lucide-react';

// Import translations
import { translations } from './translations/translations';

// Import components
import Settings from './components/Settings';
import Activities from './components/Activities';
import Messages from './components/Messages';
import VideoCall from './components/VideoCall';
import Lessons from './components/Lessons';
import SocialShare from './components/SocialShare';
import Onboarding from './components/Onboarding';
import ActivityWheel from './components/ActivityWheel';

// Import CSS
import './App.css';

export default function App() {
  // State management
  const [language, setLanguage] = useState('el');
  const [textSize, setTextSize] = useState('medium');
  const [currentView, setCurrentView] = useState('onboarding');
  const [onboardingStep, setOnboardingStep] = useState(1);
  
  // User data
  const [userRole, setUserRole] = useState('');
  const [userName, setUserName] = useState('');
  const [grandparentName, setGrandparentName] = useState('');
  const [grandparentPhone, setGrandparentPhone] = useState('');
  const [grandparentDevice, setGrandparentDevice] = useState('');
  const [userInterests, setUserInterests] = useState([]);
  const [grandparentInterests, setGrandparentInterests] = useState([]);
  
  // App data
  const [messages, setMessages] = useState([]);
  const [messageInput, setMessageInput] = useState('');
  const [showVideoCall, setShowVideoCall] = useState(false);
  const [gameScore, setGameScore] = useState(0);
  const [completedLessons, setCompletedLessons] = useState([]);
  
  const t = translations[language];
  
  // Text size classes
  const textSizeClasses = {
    small: 'text-base',
    medium: 'text-lg',
    large: 'text-xl'
  };
  
  const buttonSizeClasses = {
    small: 'p-3 text-base',
    medium: 'p-4 text-lg',
    large: 'p-5 text-xl'
  };
  
  // Toggle interest
  const toggleInterest = (interest, isGrandparent = false) => {
    if (isGrandparent) {
      setGrandparentInterests(prev =>
        prev.includes(interest)
          ? prev.filter(i => i !== interest)
          : [...prev, interest]
      );
    } else {
      setUserInterests(prev =>
        prev.includes(interest)
          ? prev.filter(i => i !== interest)
          : [...prev, interest]
      );
    }
  };

  // Add lesson completion
  const completeLesson = (lessonId) => {
    if (!completedLessons.includes(lessonId)) {
      setCompletedLessons(prev => [...prev, lessonId]);
      setGameScore(prev => prev + 10);
    }
  };
  
  // Render Grandparent Home
  const renderGrandparentHome = () => (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-50">
      <header className="bg-gradient-to-r from-purple-500 to-pink-500 text-white p-6 shadow-lg">
        <div className="flex items-center justify-between">
          <div>
            <h1 className={`${textSizeClasses[textSize]} font-bold`}>
              {t.welcome}, {userName}! 🌟
            </h1>
            <div className="flex items-center gap-2 mt-1">
              <Trophy className="w-5 h-5 text-yellow-300" />
              <span className="text-yellow-300 font-semibold">{gameScore} {t.points}</span>
            </div>
          </div>
          <div className="text-6xl">
            👨‍🦳
          </div>
        </div>
      </header>
      
      <main className="p-4 pb-24">
        <div className="grid grid-cols-2 gap-4 max-w-2xl mx-auto">
          <button
            onClick={() => setCurrentView('messages')}
            className={`${buttonSizeClasses[textSize]} bg-gradient-to-r from-blue-400 to-blue-600 text-white rounded-2xl shadow-lg hover:shadow-xl transition-all transform hover:scale-105`}
          >
            <MessageSquare className="w-12 h-12 mx-auto mb-2" />
            {t.message}
          </button>
          
          <button
            onClick={() => setShowVideoCall(true)}
            className={`${buttonSizeClasses[textSize]} bg-gradient-to-r from-green-400 to-green-600 text-white rounded-2xl shadow-lg hover:shadow-xl transition-all transform hover:scale-105`}
          >
            <Video className="w-12 h-12 mx-auto mb-2" />
            {t.videoCall}
          </button>
          
          <button
            onClick={() => setCurrentView('activityWheel')}
            className={`${buttonSizeClasses[textSize]} bg-gradient-to-r from-purple-400 to-purple-600 text-white rounded-2xl shadow-lg hover:shadow-xl transition-all transform hover:scale-105`}
          >
            <Sparkles className="w-12 h-12 mx-auto mb-2" />
            {t.activities}
          </button>
          
          <button
            onClick={() => setCurrentView('lessons')}
            className={`${buttonSizeClasses[textSize]} bg-gradient-to-r from-orange-400 to-orange-600 text-white rounded-2xl shadow-lg hover:shadow-xl transition-all transform hover:scale-105`}
          >
            <Book className="w-12 h-12 mx-auto mb-2" />
            {t.lessons}
            {completedLessons.length > 0 && (
              <div className="text-xs bg-yellow-400 text-yellow-900 px-2 py-1 rounded-full mt-1">
                {completedLessons.length}/5 ✓
              </div>
            )}
          </button>
        </div>

        {/* Quick tips section */}
        <div className="mt-8 max-w-2xl mx-auto">
          <div className="bg-yellow-100 border-l-4 border-yellow-500 p-4 rounded-r-lg">
            <p className={`${textSizeClasses[textSize]} text-yellow-800`}>
              💡 {t.todayTip}: {t.tipOfTheDay}
            </p>
          </div>
        </div>
      </main>
      
      <nav className="fixed bottom-0 left-0 right-0 bg-white border-t-2 border-gray-200 px-4 py-2">
        <div className="flex justify-around items-center max-w-md mx-auto">
          <button className={`${buttonSizeClasses[textSize]} text-purple-500`}>
            <Home className="w-8 h-8" />
          </button>
          <button
            onClick={() => setCurrentView('settings')}
            className={`${buttonSizeClasses[textSize]} text-gray-600`}
          >
            <SettingsIcon className="w-8 h-8" />
          </button>
        </div>
      </nav>
    </div>
  );
  
  // Render Child Home
  const renderChildHome = () => (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-green-50">
      <header className="bg-gradient-to-r from-blue-500 to-green-500 text-white p-6 shadow-lg">
        <div className="flex items-center justify-between">
          <div>
            <h1 className={`${textSizeClasses[textSize]} font-bold`}>
              {t.welcome}, {userName}! 👋
            </h1>
            <p className="text-sm mt-1">
              📱 {grandparentName} - {grandparentDevice === 'basic' ? t.basicPhone : t.smartphone}
            </p>
          </div>
          <div className="text-6xl">
            👶
          </div>
        </div>
      </header>
      
      <main className="p-4 pb-24">
        <div className="grid grid-cols-2 gap-4 max-w-2xl mx-auto">
          {grandparentDevice === 'basic' ? (
            <button
              onClick={() => setCurrentView('messages')}
              className={`${buttonSizeClasses[textSize]} bg-gradient-to-r from-blue-400 to-blue-600 text-white rounded-2xl shadow-lg hover:shadow-xl transition-all transform hover:scale-105`}
            >
              <Mail className="w-12 h-12 mx-auto mb-2" />
              {t.sendSMS}
            </button>
          ) : (
            <button
              onClick={() => setCurrentView('messages')}
              className={`${buttonSizeClasses[textSize]} bg-gradient-to-r from-blue-400 to-blue-600 text-white rounded-2xl shadow-lg hover:shadow-xl transition-all transform hover:scale-105`}
            >
              <MessageSquare className="w-12 h-12 mx-auto mb-2" />
              {t.message}
            </button>
          )}
          
          <button
            onClick={() => setCurrentView('socialShare')}
            className={`${buttonSizeClasses[textSize]} bg-gradient-to-r from-purple-400 to-purple-600 text-white rounded-2xl shadow-lg hover:shadow-xl transition-all transform hover:scale-105`}
          >
            <Share2 className="w-12 h-12 mx-auto mb-2" />
            {t.socialShare}
          </button>
          
          {grandparentDevice === 'smartphone' && (
            <>
              <button
                onClick={() => setShowVideoCall(true)}
                className={`${buttonSizeClasses[textSize]} bg-gradient-to-r from-green-400 to-green-600 text-white rounded-2xl shadow-lg hover:shadow-xl transition-all transform hover:scale-105`}
              >
                <Video className="w-12 h-12 mx-auto mb-2" />
                {t.videoCall}
              </button>
              
              <button
                onClick={() => setCurrentView('activityWheel')}
                className={`${buttonSizeClasses[textSize]} bg-gradient-to-r from-orange-400 to-orange-600 text-white rounded-2xl shadow-lg hover:shadow-xl transition-all transform hover:scale-105`}
              >
                <Sparkles className="w-12 h-12 mx-auto mb-2" />
                {t.activities}
              </button>
            </>
          )}
        </div>

        {/* Connection status */}
        <div className="mt-8 max-w-2xl mx-auto">
          <div className="bg-green-100 border-l-4 border-green-500 p-4 rounded-r-lg">
            <p className={`${textSizeClasses[textSize]} text-green-800 flex items-center gap-2`}>
              <div className="w-3 h-3 bg-green-500 rounded-full animate-pulse"></div>
              {t.connectedWith} {grandparentName}
            </p>
          </div>
        </div>
      </main>
      
      <nav className="fixed bottom-0 left-0 right-0 bg-white border-t-2 border-gray-200 px-4 py-2">
        <div className="flex justify-around items-center max-w-md mx-auto">
          <button className={`${buttonSizeClasses[textSize]} text-blue-500`}>
            <Home className="w-8 h-8" />
          </button>
          <button
            onClick={() => setCurrentView('settings')}
            className={`${buttonSizeClasses[textSize]} text-gray-600`}
          >
            <SettingsIcon className="w-8 h-8" />
          </button>
        </div>
      </nav>
    </div>
  );
  
  // Prevent zooming on double tap
  useEffect(() => {
    document.addEventListener('gesturestart', e => e.preventDefault());
    const metaViewport = document.querySelector('meta[name=viewport]');
    if (metaViewport) {
      metaViewport.setAttribute('content', 
        'width=device-width, initial-scale=1.0, maximum-scale=1.0, user-scalable=no'
      );
    }
  }, []);
  
  // Show video call overlay
  if (showVideoCall) {
    return <VideoCall 
      contactName={userRole === 'child' ? grandparentName : userName} 
      onEndCall={() => setShowVideoCall(false)}
      language={language}
      textSizeClasses={textSizeClasses}
      textSize={textSize}
    />;
  }
  
  // Main render
  switch (currentView) {
    case 'onboarding':
      return (
        <Onboarding
          onboardingStep={onboardingStep}
          setOnboardingStep={setOnboardingStep}
          userRole={userRole}
          setUserRole={setUserRole}
          userName={userName}
          setUserName={setUserName}
          grandparentName={grandparentName}
          setGrandparentName={setGrandparentName}
          grandparentPhone={grandparentPhone}
          setGrandparentPhone={setGrandparentPhone}
          grandparentDevice={grandparentDevice}
          setGrandparentDevice={setGrandparentDevice}
          userInterests={userInterests}
          grandparentInterests={grandparentInterests}
          toggleInterest={toggleInterest}
          setCurrentView={setCurrentView}
          t={t}
          textSizeClasses={textSizeClasses}
          buttonSizeClasses={buttonSizeClasses}
          textSize={textSize}
        />
      );
      
    case 'grandparentHome':
      return renderGrandparentHome();
      
    case 'childHome':
      return renderChildHome();
      
    case 'settings':
      return (
        <Settings
          language={language}
          setLanguage={setLanguage}
          textSize={textSize}
          setTextSize={setTextSize}
          setCurrentView={setCurrentView}
          userRole={userRole}
          t={t}
          textSizeClasses={textSizeClasses}
          buttonSizeClasses={buttonSizeClasses}
        />
      );
      
    case 'activityWheel':
      return (
        <ActivityWheel
          userRole={userRole}
          userInterests={userInterests}
          grandparentInterests={grandparentInterests}
          setCurrentView={setCurrentView}
          t={t}
          textSizeClasses={textSizeClasses}
          buttonSizeClasses={buttonSizeClasses}
          textSize={textSize}
          language={language}
        />
      );
      
    case 'activities':
      return (
        <Activities
          userRole={userRole}
          userInterests={userInterests}
          grandparentInterests={grandparentInterests}
          setCurrentView={setCurrentView}
          t={t}
          textSizeClasses={textSizeClasses}
          buttonSizeClasses={buttonSizeClasses}
          textSize={textSize}
          language={language}
        />
      );
      
    case 'messages':
      return (
        <Messages
          messages={messages}
          messageInput={messageInput}
          setMessageInput={setMessageInput}
          setMessages={setMessages}
          setCurrentView={setCurrentView}
          userRole={userRole}
          grandparentName={grandparentName}
          grandparentDevice={grandparentDevice}
          userName={userName}
          grandparentPhone={grandparentPhone}
          t={t}
          textSizeClasses={textSizeClasses}
          buttonSizeClasses={buttonSizeClasses}
          textSize={textSize}
        />
      );
      
    case 'lessons':
      return (
        <Lessons
          setCurrentView={setCurrentView}
          completedLessons={completedLessons}
          completeLesson={completeLesson}
          gameScore={gameScore}
          setGameScore={setGameScore}
          t={t}
          textSizeClasses={textSizeClasses}
          buttonSizeClasses={buttonSizeClasses}
          textSize={textSize}
          language={language}
        />
      );
      
    case 'socialShare':
      return (
        <SocialShare
          setCurrentView={setCurrentView}
          grandparentName={grandparentName}
          grandparentDevice={grandparentDevice}
          userName={userName}
          setMessages={setMessages}
          messages={messages}
          t={t}
          textSizeClasses={textSizeClasses}
          buttonSizeClasses={buttonSizeClasses}
          textSize={textSize}
          language={language}
        />
      );
      
    default:
      return renderGrandparentHome();
  }
}
