import React, { useState } from 'react';
import { ChevronLeft, Trophy, Gift, Star, Crown, Zap, Heart, Lock, Check, ShoppingBag, Sparkles } from 'lucide-react';

const RewardsCenter = ({ 
  setCurrentView, 
  userRole,
  gameScore,
  setGameScore,
  completedLessons,
  unlockedFeatures,
  setUnlockedFeatures,
  t, 
  textSizeClasses, 
  buttonSizeClasses,
  textSize
}) => {
  const [claimedRewards, setClaimedRewards] = useState([]);
  const [activeTab, setActiveTab] = useState('achievements');

  const rewards = [
    {
      id: 'avatar_pack_1',
      name: 'Î Î±ÎºÎ­Ï„Î¿ Avatar "ÎšÎ»Î±ÏƒÎ¹ÎºÏŒ"',
      description: 'ÎÎµÎºÎ»ÎµÎ¹Î´ÏÏƒÏ„Îµ 3 Î½Î­Î± avatars: Î§Î±ÏÎ¿ÏÎ¼ÎµÎ½Î¿Ï‚ Î Î±Ï€Ï€Î¿ÏÏ‚, ÎšÎ¿Ï…Î» Î“Î¹Î±Î³Î¹Î¬, Î£Î¿Ï†ÏŒÏ‚ Î Î±Ï€Ï€Î¿ÏÏ‚',
      cost: 150,
      icon: 'ğŸ‘¥',
      type: 'avatar_pack',
      category: 'Avatars'
    },
    {
