import React, { useState } from 'react';
import { ChevronLeft, Trophy, Gift, Star, Crown, Zap, Heart, Lock, Check, ShoppingBag, Sparkles } from 'lucide-react';

const RewardsCenter = ({ 
  setCurrentView, 
  userRole,
  gameScore,
  setGameScore,
  completedLessons,
  unlockedFeatures,
  setUnlockedFeatures,
  t, 
  textSizeClasses, 
  buttonSizeClasses,
  textSize
}) => {
  const [claimedRewards, setClaimedRewards] = useState([]);
  const [activeTab, setActiveTab] = useState('achievements');

  const rewards = [
    {
      id: 'avatar_pack_1',
      name: 'Πακέτο Avatar "Κλασικό"',
      description: 'Ξεκλειδώστε 3 νέα avatars: Χαρούμενος Παππούς, Κουλ Γιαγιά, Σοφός Παππούς',
      cost: 150,
      icon: '👥',
      type: 'avatar_pack',
      category: 'Avatars'
    },
    {
      id: 'accessory_pack_1',
      name: 'Πακέτο Αξεσουάρ "Βασικό"',
      description: 'Πάρτε καπέλο, γυαλιά και λουλούδι δωρεάν!',
      cost: 80,
      icon: '🎩',
      type: 'accessory_pack',
      category: 'Αξεσουάρ'
    },
    {
      id: 'larger_text',
      name: 'Μεγαλύτερα Γράμματα',
      description: 'Ξεκλειδώστε ακόμα μεγαλύτερη γραμματοσειρά για καλύτερη ανάγνωση',
      cost: 100,
      icon: '🔤',
      type: 'feature',
      category: 'Χαρακτηριστικά'
    },
    {
      id: 'voice_commands',
      name: 'Φωνητικές Εντολές',
      description: 'Χρησιμοποιήστε τη φωνή σας για να πλοηγηθείτε στην εφαρμογή',
      cost: 200,
      icon: '🎤',
      type: 'feature',
      category: 'Χαρακτηριστικά'
    },
    {
      id: 'themes_pack',
      name: 'Πακέτο Θεμάτων',
      description: 'Αλλάξτε τα χρώματα της εφαρμογής: Θάλασσα, Φύση, Νύχτα',
      cost: 75,
      icon: '🎨',
      type: 'theme_pack',
      category: 'Εμφάνιση'
    },
    {
      id: 'premium_lessons',
      name: 'Premium Μαθήματα',
      description: 'Πρόσβαση σε 15 επιπλέον μαθήματα για προχωρημένους',
      cost: 300,
      icon: '📚',
      type: 'feature',
      category: 'Μαθήματα'
    },
    {
      id: 'family_sharing',
      name: 'Οικογενειακή Κοινοποίηση',
      description: 'Μοιραστείτε την πρόοδό σας με όλη την οικογένεια',
      cost: 120,
      icon: '👨‍👩‍👧‍👦',
      type: 'feature',
      category: 'Κοινωνικά'
    },
    {
      id: 'celebration_effects',
      name: 'Εφέ Γιορτής',
      description: 'Πυροτεχνήματα και κομφετί όταν ολοκληρώνετε μαθήματα!',
      cost: 50,
      icon: '🎉',
      type: 'feature',
      category: 'Εφέ'
    }
  ];

  const achievements = [
    {
      id: 'first_lesson',
      name: 'Πρώτο Μάθημα',
      description: 'Ολοκληρώστε το πρώτο σας μάθημα',
      reward: 25,
      icon: '🎯',
      completed: completedLessons.length >= 1
    },
    {
      id: 'three_lessons',
      name: 'Τρία Μαθήματα',
      description: 'Ολοκληρώστε 3 μαθήματα',
      reward: 50,
      icon: '🏆',
      completed: completedLessons.length >= 3
    },
    {
      id: 'five_lessons',
      name: 'Όλα τα Μαθήματα',
      description: 'Ολοκληρώστε όλα τα 5 βασικά μαθήματα',
      reward: 100,
      icon: '⭐',
      completed: completedLessons.length >= 5
    },
    {
      id: 'master_tech',
      name: 'Μάστερ Τεχνολογίας',
      description: 'Κερδίστε 500 πόντους συνολικά',
      reward: 150,
      icon: '👑',
      completed: gameScore + claimedRewards.reduce((sum, id) => {
        const achievement = achievements.find(a => a.id === id);
        return sum + (achievement ? achievement.reward : 0);
      }, 0) >= 500
    },
    {
      id: 'daily_user',
      name: 'Καθημερινός Χρήστης',
      description: 'Συνδεθείτε στην εφαρμογή για 7 συνεχόμενες μέρες',
      reward: 75,
      icon: '📅',
      completed: true // Simulated
    },
    {
      id: 'social_butterfly',
      name: 'Κοινωνική Πεταλούδα',
      description: 'Στείλτε 20 μηνύματα στην οικογένεια',
      reward: 60,
      icon: '🦋',
      completed: true // Simulated
    },
    {
      id: 'avatar_collector',
      name: 'Συλλέκτης Avatar',
      description: 'Αγοράστε 5 διαφορετικά avatars',
      reward: 80,
      icon: '🎭',
      completed: false
    },
    {
      id: 'big_spender',
      name: 'Μεγάλος Αγοραστής',
      description: 'Ξοδέψτε 300 πόντους στο κατάστημα',
      reward: 120,
      icon: '💎',
      completed: false
    }
  ];

  const canAfford = (cost) => gameScore >= cost;
  const isClaimed = (rewardId) => claimedRewards.includes(rewardId);

  const claimReward = (reward) => {
    if (canAfford(reward.cost) && !isClaimed(reward.id)) {
      setGameScore(prev => prev - reward.cost);
      setClaimedRewards(prev => [...prev, reward.id]);
      setUnlockedFeatures(prev => [...prev, reward.id]);
      
      // Show success message
      const successDiv = document.createElement('div');
      successDiv.className = 'fixed top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 bg-green-600 text-white p-10 rounded-3xl shadow-2xl z-50 text-center border-4 border-white max-w-lg';
      successDiv.innerHTML = `
        <div class="text-8xl mb-6">🎉</div>
        <h3 class="text-3xl font-bold mb-4">Συγχαρητήρια!</h3>
        <p class="text-xl mb-4">Κερδίσατε:</p>
        <p class="text-2xl font-bold mb-8">${reward.name}</p>
        <button onclick="this.parentElement.remove()" class="bg-white text-green-600 px-10 py-4 rounded-2xl font-bold hover:bg-gray-100 text-2xl">
          Υπέροχα!
        </button>
      `;
      document.body.appendChild(successDiv);
      setTimeout(() => successDiv.remove(), 10000);
    }
  };

  const claimAchievement = (achievement) => {
    if (achievement.completed && !isClaimed(achievement.id)) {
      setGameScore(prev => prev + achievement.reward);
      setClaimedRewards(prev => [...prev, achievement.id]);
      
      const successDiv = document.createElement('div');
      successDiv.className = 'fixed top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 bg-yellow-500 text-white p-10 rounded-3xl shadow-2xl z-50 text-center border-4 border-white max-w-lg';
      successDiv.innerHTML = `
        <div class="text-8xl mb-6">🏆</div>
        <h3 class="text-3xl font-bold mb-4">Νέο Επίτευγμα!</h3>
        <p class="text-2xl font-bold mb-4">${achievement.name}</p>
        <p class="text-xl mb-8">+${achievement.reward} πόντοι!</p>
        <button onclick="this.parentElement.remove()" class="bg-white text-yellow-600 px-10 py-4 rounded-2xl font-bold hover:bg-gray-100 text-2xl">
          Φανταστικά!
        </button>
      `;
      document.body.appendChild(successDiv);
      setTimeout(() => successDiv.remove(), 10000);
    }
  };

  const getProgressPercentage = () => {
    return Math.min((gameScore / 1000) * 100, 100);
  };

  const getNextMilestone = () => {
    const milestones = [100, 250, 500, 750, 1000];
    return milestones.find(m => gameScore < m) || 1000;
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-yellow-50 to-orange-50">
      <header className="bg-gradient-to-r from-yellow-500 to-orange-500 text-white p-10 shadow-2xl">
        <div className="flex items-center gap-8">
          <button
            onClick={() => setCurrentView(userRole === 'grandparent' ? 'grandparentHome' : 'childHome')}
            className="text-white hover:bg-white/20 rounded-3xl p-6 transition-colors border-4 border-white/30 hover:border-white shadow-lg"
          >
            <ChevronLeft className="w-16 h-16" />
          </button>
          <div className="flex-1">
            <h1 className={`${textSizeClasses[textSize]} font-bold text-4xl mb-3`}>
              🏆 Κέντρο Ανταμοιβών
            </h1>
            <div className="flex items-center gap-8 mt-4">
              <div className="flex items-center gap-4 bg-white bg-opacity-25 rounded-3xl px-8 py-4 shadow-lg">
                <Trophy className="w-10 h-10 text-yellow-300" />
                <span className="text-yellow-300 font-bold text-3xl">{gameScore} πόντοι</span>
              </div>
              <div className="flex items-center gap-3">
                <Sparkles className="w-8 h-8 text-green-300" />
                <span className="text-green-300 font-semibold text-xl">Επόμενος στόχος: {getNextMilestone()}</span>
              </div>
            </div>
          </div>
        </div>
        
        {/* Progress Bar */}
        <div className="mt-8">
          <div className="flex justify-between text-lg mb-3">
            <span className="text-yellow-100">Πρόοδος προς επόμενο επίπεδο</span>
            <span className="text-yellow-100">{Math.floor(getProgressPercentage())}%</span>
          </div>
          <div className="w-full bg-white bg-opacity-30 rounded-full h-6">
            <div 
              className="bg-yellow-300 h-6 rounded-full transition-all duration-500 shadow-lg"
              style={{ width: `${getProgressPercentage()}%` }}
            ></div>
          </div>
        </div>
      </header>

      <main className="p-8">
        <div className="max-w-6xl mx-auto">
          {/* Tabs */}
          <div className="flex mb-10 bg-white rounded-3xl shadow-xl p-4">
            <button
              onClick={() => setActiveTab('achievements')}
              className={`flex-1 ${buttonSizeClasses[textSize]} rounded-3xl transition-all text-2xl font-bold ${
                activeTab === 'achievements' 
                ? 'bg-yellow-500 text-white shadow-lg' 
                : 'text-yellow-600 hover:bg-yellow-100'
              }`}
            >
              🏅 Επιτεύγματα
            </button>
            <button
              onClick={() => setActiveTab('shop')}
              className={`flex-1 ${buttonSizeClasses[textSize]} rounded-3xl transition-all text-2xl font-bold ${
                activeTab === 'shop' 
                ? 'bg-orange-500 text-white shadow-lg' 
                : 'text-orange-600 hover:bg-orange-100'
              }`}
            >
              🛍️ Κατάστημα
            </button>
          </div>

          {/* Achievements Section */}
          {activeTab === 'achievements' && (
            <div className="space-y-8">
              <h2 className={`${textSizeClasses[textSize]} font-bold text-gray-800 mb-8 flex items-center gap-4 text-3xl`}>
                🏅 Τα Επιτεύγματά σας
              </h2>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                {achievements.map(achievement => (
                  <div
                    key={achievement.id}
                    className={`bg-white rounded-3xl p-10 shadow-xl border-4 transition-all hover:shadow-2xl ${
                      achievement.completed && !isClaimed(achievement.id)
                        ? 'border-green-400 bg-green-50 animate-pulse'
                        : isClaimed(achievement.id)
                        ? 'border-blue-400 bg-blue-50'
                        : 'border-gray-200'
                    }`}
                  >
                    <div className="flex items-center gap-8">
                      <div className={`text-8xl ${!achievement.completed ? 'grayscale' : ''}`}>
                        {achievement.icon}
                      </div>
                      <div className="flex-1">
                        <h3 className={`${textSizeClasses[textSize]} font-bold text-gray-800 text-2xl mb-3`}>
                          {achievement.name}
                        </h3>
                        <p className="text-gray-600 mb-6 text-xl leading-relaxed">{achievement.description}</p>
                        <div className="flex items-center justify-between">
                          <div className="text-3xl font-bold text-yellow-600">
                            💰 +{achievement.reward} πόντοι
                          </div>
                          {isClaimed(achievement.id) ? (
                            <span className="text-blue-600 font-bold flex items-center gap-3 text-xl">
                              <Check className="w-8 h-8" />
                              Κερδήθηκε!
                            </span>
                          ) : achievement.completed ? (
                            <button
                              onClick={() => claimAchievement(achievement)}
                              className="bg-green-500 text-white px-8 py-4 rounded-3xl font-bold hover:bg-green-600 transition-colors text-xl shadow-lg"
                            >
                              Κέρδισε τώρα!
                            </button>
                          ) : (
                            <span className="text-gray-500 flex items-center gap-3 text-xl">
                              <Lock className="w-6 h-6" />
                              Κλειδωμένο
                            </span>
                          )}
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Rewards Shop */}
          {activeTab === 'shop' && (
            <div className="space-y-8">
              <h2 className={`${textSizeClasses[textSize]} font-bold text-gray-800 mb-8 flex items-center gap-4 text-3xl`}>
                🛍️ Κατάστημα Ανταμοιβών
              </h2>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
                {rewards.map(reward => {
                  const affordable = canAfford(reward.cost);
                  const claimed = isClaimed(reward.id);
                  
                  return (
                    <div
                      key={reward.id}
                      className={`bg-white rounded-3xl p-10 shadow-xl transition-all border-4 ${
                        claimed
                          ? 'border-green-400 bg-green-50'
                          : affordable
                          ? 'hover:shadow-2xl transform hover:scale-105 border-orange-200 hover:border-orange-400'
                          : 'opacity-75 border-gray-200'
                      }`}
                    >
                      <div className="text-center">
                        <div className="text-8xl mb-6">{reward.icon}</div>
                        <div className="inline-block bg-orange-100 text-orange-800 px-4 py-2 rounded-full text-lg font-semibold mb-4">
                          {reward.category}
                        </div>
                        <h3 className={`${textSizeClasses[textSize]} font-bold text-gray-800 mb-4 text-2xl leading-tight`}>
                          {reward.name}
                        </h3>
                        <p className="text-gray-600 text-lg mb-8 leading-relaxed">{reward.description}</p>
                        
                        <div className="space-y-4">
                          {claimed ? (
                            <div className="text-green-600 font-bold flex items-center justify-center gap-3 text-xl">
                              <Check className="w-8 h-8" />
                              Αγοράστηκε!
                            </div>
                          ) : (
                            <>
                              <div className={`font-bold text-3xl ${affordable ? 'text-orange-600' : 'text-red-500'}`}>
                                💰 {reward.cost} πόντοι
                              </div>
                              <button
                                onClick={() => claimReward(reward)}
                                disabled={!affordable}
                                className={`w-full py-4 px-8 rounded-3xl font-bold transition-colors text-xl ${
                                  affordable
                                    ? 'bg-orange-500 text-white hover:bg-orange-600 shadow-lg'
                                    : 'bg-gray-300 text-gray-500 cursor-not-allowed'
                                }`}
                              >
                                {affordable ? (
                                  <>
                                    <ShoppingBag className="inline w-6 h-6 mr-3" />
                                    Αγορά
                                  </>
                                ) : (
                                  'Χρειάζεστε περισσότερους πόντους'
                                )}
                              </button>
                            </>
                          )}
                        </div>
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
          )}

          {/* Tips Section */}
          <div className="mt-16 bg-gradient-to-r from-blue-100 to-purple-100 border-4 border-blue-300 rounded-3xl p-10">
            <h3 className={`${textSizeClasses[textSize]} font-bold text-blue-800 mb-6 flex items-center gap-4 text-3xl`}>
              💡 Πώς να κερδίσετε πόντους
            </h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-8 text-blue-700">
              <div className="space-y-4">
                <p className="flex items-center gap-4 text-xl">
                  <span className="text-3xl">📚</span>
                  <span>Ολοκληρώστε μαθήματα: <strong>+25 πόντοι</strong></span>
                </p>
                <p className="flex items-center gap-4 text-xl">
                  <span className="text-3xl">🏆</span>
                  <span>Περάστε κουίζ με άριστα: <strong>+30 πόντοι</strong></span>
                </p>
                <p className="flex items-center gap-4 text-xl">
                  <span className="text-3xl">🎯</span>
                  <span>Κερδίστε επιτεύγματα: <strong>+25-150 πόντοι</strong></span>
                </p>
              </div>
              <div className="space-y-4">
                <p className="flex items-center gap-4 text-xl">
                  <span className="text-3xl">📅</span>
                  <span>Χρησιμοποιήστε καθημερινά: <strong>+10 πόντοι</strong></span>
                </p>
                <p className="flex items-center gap-4 text-xl">
                  <span className="text-3xl">💬</span>
                  <span>Στείλτε μηνύματα: <strong>+5 πόντοι</strong></span>
                </p>
                <p className="flex items-center gap-4 text-xl">
                  <span className="text-3xl">🎡</span>
                  <span>Χρησιμοποιήστε δραστηριότητες: <strong>+8 πόντοι</strong></span>
                </p>
              </div>
            </div>
            
            <div className="mt-8 bg-white bg-opacity-50 rounded-3xl p-6 text-center">
              <p className="text-blue-800 font-bold text-2xl">
                🎯 Στόχος: Μαζέψτε 1000 πόντους για να γίνετε "Μάστερ της Τεχνολογίας"!
              </p>
            </div>
          </div>

          {/* Statistics */}
          <div className="mt-10 bg-white rounded-3xl shadow-xl p-10 border-4 border-yellow-200">
            <h3 className="text-3xl font-bold text-yellow-800 mb-8 text-center">📊 Τα Στατιστικά σας</h3>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-8 text-center">
              <div className="bg-green-100 rounded-3xl p-6">
                <div className="text-4xl mb-3">📚</div>
                <div className="text-3xl font-bold text-green-800">{completedLessons.length}</div>
                <div className="text-green-600 text-xl">Μαθήματα</div>
              </div>
              <div className="bg-blue-100 rounded-3xl p-6">
                <div className="text-4xl mb-3">🏆</div>
                <div className="text-3xl font-bold text-blue-800">{achievements.filter(a => a.completed && isClaimed(a.id)).length}</div>
                <div className="text-blue-600 text-xl">Επιτεύγματα</div>
              </div>
              <div className="bg-purple-100 rounded-3xl p-6">
                <div className="text-4xl mb-3">🛍️</div>
                <div className="text-3xl font-bold text-purple-800">{claimedRewards.filter(id => rewards.find(r => r.id === id)).length}</div>
                <div className="text-purple-600 text-xl">Αγορές</div>
              </div>
              <div className="bg-orange-100 rounded-3xl p-6">
                <div className="text-4xl mb-3">💰</div>
                <div className="text-3xl font-bold text-orange-800">{gameScore}</div>
                <div className="text-orange-600 text-xl">Πόντοι</div>
              </div>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
};

export default RewardsCenter;
