import React, { useState } from 'react';
import { ChevronLeft, Globe, Type, Bell, Shield, Moon, Volume2, Wifi, Battery, Phone, HelpCircle, HeadphonesIcon, MessageCircle } from 'lucide-react';

const Settings = ({ 
  language, 
  setLanguage, 
  textSize, 
  setTextSize, 
  setCurrentView, 
  userRole, 
  t, 
  textSizeClasses, 
  buttonSizeClasses 
}) => {
  const [notifications, setNotifications] = useState(true);
  const [darkMode, setDarkMode] = useState(false);
  const [volume, setVolume] = useState(70);
  const [autoAnswer, setAutoAnswer] = useState(false);
  const [largeButtons, setLargeButtons] = useState(true);
  
  const settingsTranslations = {
    el: {
      generalSettings: "ŒìŒµŒΩŒπŒ∫Œ≠œÇ Œ°œÖŒ∏ŒºŒØœÉŒµŒπœÇ",
      accessibility: "Œ†œÅŒøœÉŒ≤Œ±œÉŒπŒºœåœÑŒ∑œÑŒ±",
      privacy: "ŒëœÄœåœÅœÅŒ∑œÑŒø & ŒëœÉœÜŒ¨ŒªŒµŒπŒ±",
      notifications: "ŒïŒπŒ¥ŒøœÄŒøŒπŒÆœÉŒµŒπœÇ",
      darkMode: "Œ£Œ∫ŒøœÑŒµŒπŒΩŒÆ ŒªŒµŒπœÑŒøœÖœÅŒ≥ŒØŒ±",
      volume: "ŒàŒΩœÑŒ±œÉŒ∑ ŒÆœáŒøœÖ",
      autoAnswer: "ŒëœÖœÑœåŒºŒ±œÑŒ∑ Œ±œÄŒ¨ŒΩœÑŒ∑œÉŒ∑ Œ∫ŒªŒÆœÉŒµœâŒΩ",
      largeButtons: "ŒúŒµŒ≥Œ¨ŒªŒ± Œ∫ŒøœÖŒºœÄŒπŒ¨",
      networkStatus: "ŒöŒ±œÑŒ¨œÉœÑŒ±œÉŒ∑ Œ¥ŒπŒ∫œÑœçŒøœÖ",
      batteryLevel: "ŒïœÄŒØœÄŒµŒ¥Œø ŒºœÄŒ±œÑŒ±œÅŒØŒ±œÇ",
      emergencyContacts: "ŒïœÄŒ±œÜŒ≠œÇ Œ≠Œ∫œÑŒ±Œ∫œÑŒ∑œÇ Œ±ŒΩŒ¨Œ≥Œ∫Œ∑œÇ",
      help: "ŒíŒøŒÆŒ∏ŒµŒπŒ± & Œ•œÄŒøœÉœÑŒÆœÅŒπŒæŒ∑",
      about: "Œ£œáŒµœÑŒπŒ∫Œ¨ ŒºŒµ œÑŒ∑ŒΩ ŒµœÜŒ±œÅŒºŒøŒ≥ŒÆ",
      version: "ŒàŒ∫Œ¥ŒøœÉŒ∑",
      contact: "ŒïœÄŒπŒ∫ŒøŒπŒΩœâŒΩŒØŒ±",
      tutorials: "ŒüŒ¥Œ∑Œ≥ŒøŒØ ŒßœÅŒÆœÉŒ∑œÇ",
      liveChatSupport: "ŒñœâŒΩœÑŒ±ŒΩŒÆ Œ•œÄŒøœÉœÑŒÆœÅŒπŒæŒ∑"
    },
    en: {
      generalSettings: "General Settings",
      accessibility: "Accessibility",
      privacy: "Privacy & Security",
      notifications: "Notifications",
      darkMode: "Dark mode",
      volume: "Volume",
      autoAnswer: "Auto-answer calls",
      largeButtons: "Large buttons",
      networkStatus: "Network status",
      batteryLevel: "Battery level",
      emergencyContacts: "Emergency contacts",
      help: "Help & Support",
      about: "About the app",
      version: "Version",
      contact: "Contact",
      tutorials: "User Guides",
      liveChatSupport: "Live Chat Support"
    }
  };
  
  const st = settingsTranslations[language];

  const showHelpDialog = (title, message) => {
    const helpDiv = document.createElement('div');
    helpDiv.className = 'fixed top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 bg-blue-600 text-white p-8 rounded-3xl shadow-2xl z-50 max-w-lg text-center border-4 border-white';
    helpDiv.innerHTML = `
      <div class="text-5xl mb-4">üí°</div>
      <h3 class="text-2xl font-bold mb-4">${title}</h3>
      <p class="text-lg mb-6 leading-relaxed">${message}</p>
      <button onclick="this.parentElement.remove()" class="bg-white text-blue-600 px-8 py-3 rounded-2xl font-bold hover:bg-gray-100 text-xl">
        ŒöŒ±œÑŒ¨ŒªŒ±Œ≤Œ±!
      </button>
    `;
    document.body.appendChild(helpDiv);
    
    setTimeout(() => {
      if (helpDiv.parentElement) {
        helpDiv.remove();
      }
    }, 15000);
  };

  const showEmergencyContacts = () => {
    showHelpDialog(
      "üìû ŒïœÄŒ±œÜŒ≠œÇ ŒàŒ∫œÑŒ±Œ∫œÑŒ∑œÇ ŒëŒΩŒ¨Œ≥Œ∫Œ∑œÇ",
      "ŒïŒ¥œé ŒºœÄŒøœÅŒµŒØœÑŒµ ŒΩŒ± œÄœÅŒøœÉŒ∏Œ≠œÉŒµœÑŒµ œÑŒ∑ŒªŒ≠œÜœâŒΩŒ± ŒøŒπŒ∫ŒøŒ≥Œ≠ŒΩŒµŒπŒ±œÇ Œ∫Œ±Œπ œÜŒØŒªœâŒΩ Œ≥ŒπŒ± Œ≥œÅŒÆŒ≥ŒøœÅŒ∑ Œ∫ŒªŒÆœÉŒ∑. Œ£Œµ œÄŒµœÅŒØœÄœÑœâœÉŒ∑ Œ±ŒΩŒ¨Œ≥Œ∫Œ∑œÇ, œÄŒ±œÑŒÆœÉœÑŒµ œÄŒ±œÅŒ±œÑŒµœÑŒ±ŒºŒ≠ŒΩŒ± œÑŒø Œ∫ŒøœÖŒºœÄŒØ Œ∫Œ±Œπ Œ∏Œ± Œ∫Œ±ŒªŒ≠œÉŒµŒπ Œ±œÖœÑœåŒºŒ±œÑŒ±!"
    );
  };

  const showPrivacyInfo = () => {
    showHelpDialog(
      "üîê ŒëœÄœåœÅœÅŒ∑œÑŒø & ŒëœÉœÜŒ¨ŒªŒµŒπŒ±",
      "Œ§Œ± œÄœÅŒøœÉœâœÄŒπŒ∫Œ¨ œÉŒ±œÇ œÉœÑŒøŒπœáŒµŒØŒ± ŒµŒØŒΩŒ±Œπ Œ±œÉœÜŒ±ŒªŒÆ! ŒîŒµŒΩ œÑŒ± ŒºŒøŒπœÅŒ±Œ∂œåŒºŒ±œÉœÑŒµ ŒºŒµ Œ∫Œ±ŒΩŒ≠ŒΩŒ±ŒΩ. ŒßœÅŒ∑œÉŒπŒºŒøœÄŒøŒπŒøœçŒºŒµ Œ∫œÅœÖœÄœÑŒøŒ≥œÅŒ¨œÜŒ∑œÉŒ∑ Œ≥ŒπŒ± ŒΩŒ± œÄœÅŒøœÉœÑŒ±œÑŒµœçŒøœÖŒºŒµ œÑŒπœÇ œÉœÖŒΩŒøŒºŒπŒªŒØŒµœÇ Œ∫Œ±Œπ œÑŒπœÇ œÜœâœÑŒøŒ≥œÅŒ±œÜŒØŒµœÇ œÉŒ±œÇ."
    );
  };

  const showTutorials = () => {
    showHelpDialog(
      "üìö ŒüŒ¥Œ∑Œ≥ŒøŒØ ŒßœÅŒÆœÉŒ∑œÇ",
      "ŒïŒ¥œé Œ∏Œ± Œ≤œÅŒµŒØœÑŒµ Œ≤ŒÆŒºŒ±-Œ≤ŒÆŒºŒ± ŒøŒ¥Œ∑Œ≥ŒØŒµœÇ Œ≥ŒπŒ± œåŒªŒµœÇ œÑŒπœÇ ŒªŒµŒπœÑŒøœÖœÅŒ≥ŒØŒµœÇ œÑŒ∑œÇ ŒµœÜŒ±œÅŒºŒøŒ≥ŒÆœÇ! ŒëœÄœå œÑŒø œÄœéœÇ ŒΩŒ± œÉœÑŒ≠ŒªŒΩŒµœÑŒµ ŒºŒ∑ŒΩœçŒºŒ±œÑŒ± ŒºŒ≠œáœÅŒπ œÑŒø œÄœéœÇ ŒΩŒ± Œ∫Œ¨ŒΩŒµœÑŒµ Œ≤ŒπŒΩœÑŒµŒøŒ∫ŒªŒÆœÉŒµŒπœÇ - œåŒªŒ± ŒµŒæŒ∑Œ≥Œ∑ŒºŒ≠ŒΩŒ± Œ±œÄŒªŒ¨!"
    );
  };

  const showLiveChatSupport = () => {
    showHelpDialog(
      "üí¨ ŒñœâŒΩœÑŒ±ŒΩŒÆ Œ•œÄŒøœÉœÑŒÆœÅŒπŒæŒ∑",
      "ŒßœÅŒµŒπŒ¨Œ∂ŒµœÉœÑŒµ Œ≤ŒøŒÆŒ∏ŒµŒπŒ±; ŒúŒπŒªŒÆœÉœÑŒµ ŒºŒµ Œ≠ŒΩŒ±ŒΩ Œ±œÄœå œÑŒøœÖœÇ œÜŒπŒªŒπŒ∫ŒøœçœÇ ŒºŒ±œÇ ŒµŒ∫œÄŒ±ŒπŒ¥ŒµœÖœÑŒ≠œÇ! ŒïŒØŒºŒ±œÉœÑŒµ ŒµŒ¥œé Œ≥ŒπŒ± ŒΩŒ± œÉŒ±œÇ Œ≤ŒøŒ∑Œ∏ŒÆœÉŒøœÖŒºŒµ ŒºŒµ ŒøœÄŒøŒπŒ±Œ¥ŒÆœÄŒøœÑŒµ ŒµœÅœéœÑŒ∑œÉŒ∑ Œ≠œáŒµœÑŒµ Œ≥ŒπŒ± œÑŒ∑ŒΩ ŒµœÜŒ±œÅŒºŒøŒ≥ŒÆ."
    );
  };

  const showContactInfo = () => {
    showHelpDialog(
      "üìß ŒïœÄŒπŒ∫ŒøŒπŒΩœâŒΩŒØŒ±",
      "ŒúœÄŒøœÅŒµŒØœÑŒµ ŒΩŒ± ŒµœÄŒπŒ∫ŒøŒπŒΩœâŒΩŒÆœÉŒµœÑŒµ ŒºŒ±Œ∂ŒØ ŒºŒ±œÇ:\nüìû Œ§Œ∑ŒªŒ≠œÜœâŒΩŒø: 210-1234567\nüìß Email: help@roottly.gr\nüïê ŒèœÅŒµœÇ ŒµŒæœÖœÄŒ∑œÅŒ≠œÑŒ∑œÉŒ∑œÇ: 9:00-17:00 ŒîŒµœÖœÑŒ≠œÅŒ±-Œ†Œ±œÅŒ±œÉŒ∫ŒµœÖŒÆ"
    );
  };
  
  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100">
      <header className="bg-gradient-to-r from-gray-600 to-gray-800 text-white p-8 shadow-lg">
        <div className="flex items-center gap-6">
          <button
            onClick={() => setCurrentView(userRole === 'grandparent' ? 'grandparentHome' : 'childHome')}
            className="text-white hover:bg-white/20 rounded-2xl p-4 transition-colors border-4 border-white/30 hover:border-white shadow-lg"
          >
            <ChevronLeft className="w-12 h-12" />
          </button>
          <h1 className={`${textSizeClasses[textSize]} font-bold flex-1 text-3xl`}>
            ‚öôÔ∏è {t.settings}
          </h1>
        </div>
      </header>
      
      <main className="p-6">
        <div className="max-w-3xl mx-auto space-y-8">
          {/* General Settings */}
          <div className="bg-white rounded-2xl shadow-lg p-8 border-2 border-gray-200">
            <h2 className={`${textSizeClasses[textSize]} font-bold mb-6 text-gray-800 text-2xl`}>
              üîß {st.generalSettings}
            </h2>
            
            {/* Language */}
            <div className="mb-8">
              <h3 className={`${textSizeClasses[textSize]} font-semibold mb-4 flex items-center gap-3 text-xl`}>
                <Globe className="w-7 h-7" />
                {t.language}
              </h3>
              <div className="grid grid-cols-2 gap-4">
                <button
                  onClick={() => setLanguage('el')}
                  className={`${buttonSizeClasses[textSize]} rounded-2xl border-3 transition-all text-xl ${
                    language === 'el'
                      ? 'bg-blue-500 text-white border-blue-500'
                      : 'bg-white border-gray-300 hover:border-blue-300'
                  }`}
                >
                  üá¨üá∑ ŒïŒªŒªŒ∑ŒΩŒπŒ∫Œ¨
                </button>
                <button
                  onClick={() => setLanguage('en')}
                  className={`${buttonSizeClasses[textSize]} rounded-2xl border-3 transition-all text-xl ${
                    language === 'en'
                      ? 'bg-blue-500 text-white border-blue-500'
                      : 'bg-white border-gray-300 hover:border-blue-300'
                  }`}
                >
                  üá¨üáß English
                </button>
              </div>
            </div>
            
            {/* Notifications */}
            <div className="mb-8">
              <div className="flex items-center justify-between">
                <h3 className={`${textSizeClasses[textSize]} font-semibold flex items-center gap-3 text-xl`}>
                  <Bell className="w-7 h-7" />
                  {st.notifications}
                </h3>
                <button
                  onClick={() => setNotifications(!notifications)}
                  className={`w-20 h-10 rounded-full transition-colors ${
                    notifications ? 'bg-green-500' : 'bg-gray-300'
                  }`}
                >
                  <div className={`w-8 h-8 bg-white rounded-full shadow-md transform transition-transform ${
                    notifications ? 'translate-x-10' : 'translate-x-1'
                  }`} />
                </button>
              </div>
            </div>
            
            {/* Dark Mode */}
            <div className="mb-8">
              <div className="flex items-center justify-between">
                <h3 className={`${textSizeClasses[textSize]} font-semibold flex items-center gap-3 text-xl`}>
                  <Moon className="w-7 h-7" />
                  {st.darkMode}
                </h3>
                <button
                  onClick={() => setDarkMode(!darkMode)}
                  className={`w-20 h-10 rounded-full transition-colors ${
                    darkMode ? 'bg-blue-500' : 'bg-gray-300'
                  }`}
                >
                  <div className={`w-8 h-8 bg-white rounded-full shadow-md transform transition-transform ${
                    darkMode ? 'translate-x-10' : 'translate-x-1'
                  }`} />
                </button>
              </div>
            </div>
          </div>
          
          {/* Accessibility */}
          <div className="bg-white rounded-2xl shadow-lg p-8 border-2 border-gray-200">
            <h2 className={`${textSizeClasses[textSize]} font-bold mb-6 text-gray-800 text-2xl`}>
              ‚ôø {st.accessibility}
            </h2>
            
            {/* Text Size */}
            <div className="mb-8">
              <h3 className={`${textSizeClasses[textSize]} font-semibold mb-4 flex items-center gap-3 text-xl`}>
                <Type className="w-7 h-7" />
                {t.textSize}
              </h3>
              <div className="grid grid-cols-3 gap-4">
                {['small', 'medium', 'large'].map(size => (
                  <button
                    key={size}
                    onClick={() => setTextSize(size)}
                    className={`${buttonSizeClasses[size]} rounded-2xl border-3 transition-all ${
                      textSize === size
                        ? 'bg-blue-500 text-white border-blue-500'
                        : 'bg-white border-gray-300 hover:border-blue-300'
                    }`}
                  >
                    {t[size]}
                  </button>
                ))}
              </div>
            </div>
            
            {/* Volume */}
            <div className="mb-8">
              <h3 className={`${textSizeClasses[textSize]} font-semibold mb-4 flex items-center gap-3 text-xl`}>
                <Volume2 className="w-7 h-7" />
                {st.volume}: {volume}%
              </h3>
              <input
                type="range"
                min="0"
                max="100"
                value={volume}
                onChange={(e) => setVolume(e.target.value)}
                className="w-full h-4 bg-gray-200 rounded-lg appearance-none cursor-pointer slider"
              />
            </div>
            
            {/* Large Buttons */}
            <div className="mb-8">
              <div className="flex items-center justify-between">
                <h3 className={`${textSizeClasses[textSize]} font-semibold flex items-center gap-3 text-xl`}>
                  üîò {st.largeButtons}
                </h3>
                <button
                  onClick={() => setLargeButtons(!largeButtons)}
                  className={`w-20 h-10 rounded-full transition-colors ${
                    largeButtons ? 'bg-green-500' : 'bg-gray-300'
                  }`}
                >
                  <div className={`w-8 h-8 bg-white rounded-full shadow-md transform transition-transform ${
                    largeButtons ? 'translate-x-10' : 'translate-x-1'
                  }`} />
                </button>
              </div>
            </div>
            
            {/* Auto Answer */}
            <div>
              <div className="flex items-center justify-between">
                <h3 className={`${textSizeClasses[textSize]} font-semibold flex items-center gap-3 text-xl`}>
                  <Phone className="w-7 h-7" />
                  {st.autoAnswer}
                </h3>
                <button
                  onClick={() => setAutoAnswer(!autoAnswer)}
                  className={`w-20 h-10 rounded-full transition-colors ${
                    autoAnswer ? 'bg-green-500' : 'bg-gray-300'
                  }`}
                >
                  <div className={`w-8 h-8 bg-white rounded-full shadow-md transform transition-transform ${
                    autoAnswer ? 'translate-x-10' : 'translate-x-1'
                  }`} />
                </button>
              </div>
            </div>
          </div>
          
          {/* System Info */}
          <div className="bg-white rounded-2xl shadow-lg p-8 border-2 border-gray-200">
            <h2 className={`${textSizeClasses[textSize]} font-bold mb-6 text-gray-800 text-2xl`}>
              üìä {language === 'el' ? 'Œ†ŒªŒ∑œÅŒøœÜŒøœÅŒØŒµœÇ Œ£œÖœÉœÑŒÆŒºŒ±œÑŒøœÇ' : 'System Information'}
            </h2>
            
            <div className="space-y-5">
             <div className="flex items-center justify-between">
               <span className="flex items-center gap-3 text-xl">
                 <Wifi className="w-7 h-7 text-green-500" />
                 {st.networkStatus}
               </span>
               <span className="text-green-600 font-semibold text-xl">
                 {language === 'el' ? 'Œ£œÖŒΩŒ¥ŒµŒ¥ŒµŒºŒ≠ŒΩŒø' : 'Connected'}
               </span>
             </div>
             
             <div className="flex items-center justify-between">
               <span className="flex items-center gap-3 text-xl">
                 <Battery className="w-7 h-7 text-green-500" />
                 {st.batteryLevel}
               </span>
               <span className="text-green-600 font-semibold text-xl">85%</span>
             </div>
             
             <div className="flex items-center justify-between">
               <span className="text-xl">{st.version}</span>
               <span className="text-gray-600 text-xl">1.0.0</span>
             </div>
           </div>
         </div>
         
         {/* Emergency & Help */}
         <div className="bg-white rounded-2xl shadow-lg p-8 border-2 border-gray-200">
           <h2 className={`${textSizeClasses[textSize]} font-bold mb-6 text-gray-800 text-2xl`}>
             üÜò {st.help}
           </h2>
           
           <div className="space-y-4">
             <button 
               onClick={showEmergencyContacts}
               className={`w-full ${buttonSizeClasses[textSize]} bg-red-500 text-white rounded-2xl hover:bg-red-600 transition-colors flex items-center justify-center gap-3 text-xl font-bold py-5`}
             >
               <Phone className="w-7 h-7" />
               {st.emergencyContacts}
             </button>
             
             <button 
               onClick={showPrivacyInfo}
               className={`w-full ${buttonSizeClasses[textSize]} bg-blue-500 text-white rounded-2xl hover:bg-blue-600 transition-colors flex items-center justify-center gap-3 text-xl font-bold py-5`}
             >
               <Shield className="w-7 h-7" />
               {st.privacy}
             </button>
             
             <button 
               onClick={showTutorials}
               className={`w-full ${buttonSizeClasses[textSize]} bg-green-500 text-white rounded-2xl hover:bg-green-600 transition-colors flex items-center justify-center gap-3 text-xl font-bold py-5`}
             >
               <HelpCircle className="w-7 h-7" />
               {st.tutorials}
             </button>

             <button 
               onClick={showLiveChatSupport}
               className={`w-full ${buttonSizeClasses[textSize]} bg-purple-500 text-white rounded-2xl hover:bg-purple-600 transition-colors flex items-center justify-center gap-3 text-xl font-bold py-5`}
             >
               <MessageCircle className="w-7 h-7" />
               {st.liveChatSupport}
             </button>
             
             <button 
               onClick={showContactInfo}
               className={`w-full ${buttonSizeClasses[textSize]} bg-gray-500 text-white rounded-2xl hover:bg-gray-600 transition-colors text-xl font-bold py-5`}
             >
               {st.contact}
             </button>
           </div>
         </div>
       </div>
     </main>
   </div>
 );
};

export default Settings;
