import React from 'react';
import { ChevronLeft, Video, Camera, Monitor } from 'lucide-react';

const Lessons = ({ setCurrentView, t, textSizeClasses, buttonSizeClasses, textSize }) => {
  const lessons = [
    { id: 1, title: t.lesson1, icon: <Video className="w-8 h-8" />, color: 'bg-blue-500' },
    { id: 2, title: t.lesson2, icon: <Camera className="w-8 h-8" />, color: 'bg-green-500' },
    { id: 3, title: t.lesson3, icon: <Monitor className="w-8 h-8" />, color: 'bg-purple-500' }
  ];
  
  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-50 to-blue-50">
      <header className="bg-gradient-to-r from-indigo-500 to-blue-500 text-white p-6 shadow-lg">
        <div className="flex items-center gap-4">
          <button
            onClick={() => setCurrentView('grandparentHome')}
            className="text-white hover:bg-white/20 rounded-lg p-2 transition-colors"
          >
            <ChevronLeft className="w-6 h-6" />
          </button>
          <h1 className={`${textSizeClasses[textSize]} font-bold flex-1`}>
            {t.digitalLessons}
          </h1>
        </div>
      </header>
      
      <main className="p-4">
        <div className="max-w-2xl mx-auto space-y-4">
          {lessons.map(lesson => (
            <button
              key={lesson.id}
              onClick={() => alert(`Starting lesson: ${lesson.title}`)}
              className="w-full bg-white rounded-xl shadow-md p-6 hover:shadow-lg transition-shadow flex items-center gap-4"
            >
              <div className={`${lesson.color} text-white p-4 rounded-xl`}>
                {lesson.icon}
              </div>
              <div className="flex-1 text-left">
                <h3 className={`${textSizeClasses[textSize]} font-bold text-gray-800`}>
                  {lesson.title}
                </h3>
                <p className="text-gray-600 mt-1">Click to start</p>
              </div>
              <ChevronLeft className="w-6 h-6 text-gray-400 rotate-180" />
            </button>
          ))}
        </div>
      </main>
    </div>
  );
};

export default Lessons;
