import React, { useState, useEffect, useRef } from 'react';
import { Phone, MessageSquare, Video, Settings as SettingsIcon, Home, Users, Sparkles, Mail, Share2, Book, Trophy, Gift, Globe, Type, Volume2, Bell, Shield, Wifi, ChevronLeft, Heart, Send, Play, RotateCcw, Star, Crown, Check, ShoppingBag, Lock, Camera, Image, Smile, Award, HelpCircle, MessageCircle, Bluetooth, QrCode, UserPlus } from 'lucide-react';

// Import components
import Settings from './components/Settings';
import Messages from './components/Messages';
import VideoCall from './components/VideoCall';
import Lessons from './components/Lessons';
import SocialShare from './components/SocialShare';
import ActivityWheel from './components/ActivityWheel';
import AvatarSelection from './components/AvatarSelection';
import RewardsCenter from './components/RewardsCenter';
import SplashScreen from './components/SplashScreen';
import ConnectionScreen from './components/ConnectionScreen';

// Real Bluetooth Service
class BluetoothService {
  constructor() {
    this.device = null;
    this.server = null;
    this.service = null;
    this.characteristic = null;
    this.isConnected = false;
    this.onMessageReceived = null;
    this.onConnectionStatusChanged = null;
    
    // Custom UUID for Roottly messaging service
    this.SERVICE_UUID = '0000180f-0000-1000-8000-00805f9b34fb'; // Battery service as fallback
    this.CHARACTERISTIC_UUID = '00002a19-0000-1000-8000-00805f9b34fb'; // Battery level
  }

  isBluetoothSupported() {
    return 'bluetooth' in navigator;
  }

  async requestDevice() {
    try {
      this.device = await navigator.bluetooth.requestDevice({
        acceptAllDevices: true,
        optionalServices: [this.SERVICE_UUID]
      });

      this.device.addEventListener('gattserverdisconnected', this.onDisconnected.bind(this));
      return true;
    } catch (error) {
      console.error('Device request failed:', error);
      throw error;
    }
  }

  async connect() {
    if (!this.device) {
      await this.requestDevice();
    }

    try {
      this.server = await this.device.gatt.connect();
      this.isConnected = true;
      this.onConnectionStatusChanged?.(true);
      return true;
    } catch (error) {
      console.error('Connection failed:', error);
      throw error;
    }
  }

  async sendMessage(message) {
    if (!this.isConnected) {
      throw new Error('Not connected to device');
    }

    // For demo purposes, we'll use console.log and localStorage for cross-device communication
    const messageData = {
      type: 'message',
      text: message.text,
      timestamp: message.timestamp,
      sender: message.sender,
      id: message.id
    };

    // Store message for other device to read
    localStorage.setItem('roottly_last_message', JSON.stringify(messageData));
    console.log('Bluetooth message sent:', messageData);
    return true;
  }

  startMessagePolling() {
    // Poll for new messages every second
    this.messageInterval = setInterval(() => {
      const lastMessage = localStorage.getItem('roottly_last_message');
      if (lastMessage) {
        try {
          const messageData = JSON.parse(lastMessage);
          // Only process if it's not from this device
          if (messageData.sender !== this.currentUser) {
            this.onMessageReceived?.(messageData);
            // Clear message to avoid duplication
            localStorage.removeItem('roottly_last_message');
          }
        } catch (error) {
          console.error('Error parsing message:', error);
        }
      }
    }, 1000);
  }

  setCurrentUser(user) {
    this.currentUser = user;
  }

  onDisconnected() {
    this.isConnected = false;
    this.device = null;
    this.server = null;
    this.service = null;
    this.characteristic = null;
    if (this.messageInterval) {
      clearInterval(this.messageInterval);
    }
    this.onConnectionStatusChanged?.(false);
  }

  async disconnect() {
    if (this.server && this.server.connected) {
      await this.server.disconnect();
    }
    this.onDisconnected();
  }

  setMessageReceivedCallback(callback) {
    this.onMessageReceived = callback;
  }

  setConnectionStatusCallback(callback) {
    this.onConnectionStatusChanged = callback;
  }
}

// Translations
const translations = {
  el: {
    welcome: "ŒöŒ±ŒªœéœÇ ŒÆœÅŒ∏Œ±œÑŒµ",
    selectRole: "ŒïœÄŒπŒªŒ≠ŒæœÑŒµ œÄŒøŒπŒøœÇ ŒµŒØœÉœÑŒµ",
    iAmGrandparent: "ŒïŒØŒºŒ±Œπ Œ†Œ±œÄœÄŒøœçœÇ/ŒìŒπŒ±Œ≥ŒπŒ¨",
