import React from 'react';
import { ChevronLeft, Heart, Sparkles } from 'lucide-react';
import { availableInterests } from '../translations/translations';

const Activities = ({ 
  userRole,
  userInterests,
  grandparentInterests,
  setCurrentView, 
  t, 
  textSizeClasses, 
  buttonSizeClasses,
  language 
}) => {
  
  const getCommonInterests = () => {
    return userInterests.filter(interest => grandparentInterests.includes(interest));
  };

  const getSuggestedActivities = () => {
    const activities = {
      el: {
        cooking: ["Μαγειρέψτε παραδοσιακές συνταγές μαζί", "Φτιάξτε γλυκά για την οικογένεια"],
        gardening: ["Φυτέψτε λουλούδια στον κήπο", "Φροντίστε τα φυτά μαζί"],
        music: ["Ακούστε αγαπημένα τραγούδια", "Τραγουδήστε παλιές επιτυχίες"],
        reading: ["Διαβάστε ένα βιβλίο μαζί", "Μοιραστείτε αγαπημένες ιστορίες"],
        walking: ["Κάντε βόλτα στο πάρκο", "Επισκεφθείτε νέα μέρη"],
        photos: ["Δείτε παλιές φωτογραφίες", "Τραβήξτε νέες φωτογραφίες"],
        games: ["Παίξτε επιτραπέζια", "Λύστε σταυρόλεξα"],
        crafts: ["Δημιουργήστε χειροτεχνίες", "Ζωγραφίστε μαζί"],
        movies: ["Δείτε αγαπημένες ταινίες", "Πηγαίνετε σινεμά"],
        travel: ["Σχεδιάστε ένα ταξίδι", "Θυμηθείτε παλιά ταξίδια"],
        general: ["Πιείτε καφέ και συζητήστε", "Μοιραστείτε οικογενειακές ιστορίες"]
      },
      en: {
        cooking: ["Cook traditional recipes together", "Make desserts for the family"],
        gardening: ["Plant flowers in the garden", "Take care of plants together"],
        music: ["Listen to favorite songs", "Sing old hits"],
        reading: ["Read a book together", "Share favorite stories"],
        walking: ["Take a walk in the park", "Visit new places"],
        photos: ["Look at old photos", "Take new photos"],
        games: ["Play board games", "Solve crosswords"],
        crafts: ["Create handicrafts", "Paint together"],
        movies: ["Watch favorite movies", "Go to cinema"],
        travel: ["Plan a trip", "Remember old trips"],
        general: ["Have coffee and chat", "Share family stories"]
      }
    };

    const commonInterests = getCommonInterests();
    let suggestedActivities = [];
    
    if (commonInterests.length > 0) {
      commonInterests.forEach(interest => {
        if (activities[language][interest]) {
          suggestedActivities.push(...activities[language][interest]);
        }
      });
    }
    
    if (activities[language].general) {
      suggestedActivities.push(...activities[language].general);
    }
    
    return [...new Set(suggestedActivities)].slice(0, 8);
  };

  const commonInterests = getCommonInterests();
  const suggestedActivities = getSuggestedActivities();

  return (
    <div className="min-h-screen bg-gradient-to-br from-orange-50 to-yellow-50">
      <header className="bg-gradient-to-r from-orange-500 to-yellow-500 text-white p-6 shadow-lg">
        <div className="flex items-center gap-4">
          <button
            onClick={() => setCurrentView(userRole === 'grandparent' ? 'grandparentHome' : 'childHome')}
            className="text-white hover:bg-white/20 rounded-lg p-2 transition-colors"
          >
            <ChevronLeft className="w-6 h-6" />
          </button>
          <h1 className={`${textSizeClasses[textSize]} font-bold flex-1`}>
            {t.activities}
          </h1>
        </div>
      </header>
      
      <main className="p-4">
        <div className="max-w-2xl mx-auto">
          {userRole === 'child' && commonInterests.length > 0 && (
            <div className="bg-green-100 border-2 border-green-300 rounded-lg p-4 mb-6">
              <p className={`${textSizeClasses[textSize]} text-green-800 font-semibold`}>
                {t.basedOnInterests}:
              </p>
              <div className="flex flex-wrap gap-2 mt-2">
                {commonInterests.map(interest => {
                  const interestData = availableInterests.find(i => i.id === interest);
                  return (
                    <span key={interest} className="bg-green-200 px-3 py-1 rounded-full text-green-800">
                      {interestData?.icon} {t[interest]}
                    </span>
                  );
                })}
              </div>
            </div>
          )}
          
          <h2 className={`${textSizeClasses[textSize]} font-bold mb-4`}>
            {t.suggestedActivities}
          </h2>
          
          <div className="space-y-3">
            {suggestedActivities.map((activity, index) => (
              <div
                key={index}
                className="bg-white rounded-xl shadow-md p-4 hover:shadow-lg transition-shadow"
              >
                <div className="flex items-center gap-4">
                  <div className="w-12 h-12 bg-orange-100 rounded-full flex items-center justify-center text-orange-600 font-bold">
                    {index + 1}
                  </div>
                  <p className={`${textSizeClasses[textSize]} flex-1`}>{activity}</p>
                  <button className="text-orange-500 hover:text-orange-700">
                    <Heart className="w-6 h-6" />
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </main>
    </div>
  );
};

export default Activities;
