import React from 'react';
import { ChevronLeft, Send } from 'lucide-react';

const Messages = ({ 
  messages, 
  messageInput, 
  setMessageInput, 
  setMessages,
  setCurrentView, 
  userRole, 
  grandparentName,
  t, 
  textSizeClasses, 
  buttonSizeClasses 
}) => {
  
  const handleSendMessage = () => {
    if (messageInput.trim()) {
      setMessages([...messages, {
        id: Date.now(),
        text: messageInput,
        type: 'sent',
        timestamp: new Date().toLocaleTimeString()
      }]);
      setMessageInput('');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-purple-50 flex flex-col">
      <header className="bg-gradient-to-r from-blue-500 to-purple-500 text-white p-6 shadow-lg">
        <div className="flex items-center gap-4">
          <button
            onClick={() => setCurrentView(userRole === 'grandparent' ? 'grandparentHome' : 'childHome')}
            className="text-white hover:bg-white/20 rounded-lg p-2 transition-colors"
          >
            <ChevronLeft className="w-6 h-6" />
          </button>
          <h1 className={`${textSizeClasses[textSize]} font-bold flex-1`}>
            {userRole === 'child' ? grandparentName : t.messages}
          </h1>
        </div>
      </header>
      
      <div className="flex-1 p-4 overflow-y-auto">
        <div className="space-y-4 max-w-2xl mx-auto">
          {messages.map(msg => (
            <div
              key={msg.id}
              className={`${buttonSizeClasses[textSize]} rounded-2xl max-w-sm ${
                msg.type === 'sms' || msg.type === 'sent'
                  ? 'bg-blue-500 text-white ml-auto'
                  : 'bg-white'
              }`}
            >
              {msg.image && <div className="text-4xl mb-2">{msg.image}</div>}
              <p>{msg.text}</p>
              <p className={`text-xs mt-1 ${
                msg.type === 'sms' || msg.type === 'sent' ? 'text-blue-100' : 'text-gray-500'
              }`}>
                {msg.timestamp}
              </p>
            </div>
          ))}
        </div>
      </div>
      
      <div className="p-4 bg-white border-t-2 border-gray-200">
        <div className="flex gap-2 max-w-2xl mx-auto">
          <input
            type="text"
            value={messageInput}
            onChange={(e) => setMessageInput(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
            placeholder={t.typeMessage}
            className={`flex-1 ${buttonSizeClasses[textSize]} border-2 border-gray-300 rounded-lg focus:border-blue-500 focus:outline-none`}
          />
          <button
            onClick={handleSendMessage}
            className={`${buttonSizeClasses[textSize]} bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors px-6`}
          >
            <Send className="w-5 h-5" />
          </button>
        </div>
      </div>
    </div>
  );
};

export default Messages;
