import React, { useState } from 'react';
import { ChevronLeft, Globe, Type, Bell, Shield, Moon, Volume2, Wifi, Battery, Phone } from 'lucide-react';

const Settings = ({ 
  language, 
  setLanguage, 
  textSize, 
  setTextSize, 
  setCurrentView, 
  userRole, 
  t, 
  textSizeClasses, 
  buttonSizeClasses 
}) => {
  const [notifications, setNotifications] = useState(true);
  const [darkMode, setDarkMode] = useState(false);
  const [volume, setVolume] = useState(70);
  const [autoAnswer, setAutoAnswer] = useState(false);
  const [largeButtons, setLargeButtons] = useState(true);
  
  const settingsTranslations = {
    el: {
      generalSettings: "Γενικές Ρυθμίσεις",
      accessibility: "Προσβασιμότητα",
      privacy: "Απόρρητο & Ασφάλεια",
      notifications: "Ειδοποιήσεις",
      darkMode: "Σκοτεινή λειτουργία",
      volume: "Ένταση ήχου",
      autoAnswer: "Αυτόματη απάντηση κλήσεων",
      largeButtons: "Μεγάλα κουμπιά",
      networkStatus: "Κατάσταση δικτύου",
      batteryLevel: "Επίπεδο μπαταρίας",
      emergencyContacts: "Επαφές έκτακτης ανάγκης",
      help: "Βοήθεια & Υποστήριξη",
      about: "Σχετικά με την εφαρμογή",
      version: "Έκδοση",
      contact: "Επικοινωνία"
    },
    en: {
      generalSettings: "General Settings",
      accessibility: "Accessibility",
      privacy: "Privacy & Security",
      notifications: "Notifications",
      darkMode: "Dark mode",
      volume: "Volume",
      autoAnswer: "Auto-answer calls",
      largeButtons: "Large buttons",
      networkStatus: "Network status",
      batteryLevel: "Battery level",
      emergencyContacts: "Emergency contacts",
      help: "Help & Support",
      about: "About the app",
      version: "Version",
      contact: "Contact"
    }
  };
  
  const st = settingsTranslations[language];
  
  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100">
      <header className="bg-gradient-to-r from-gray-600 to-gray-800 text-white p-6 shadow-lg">
        <div className="flex items-center gap-4">
          <button
            onClick={() => setCurrentView(userRole === 'grandparent' ? 'grandparentHome' : 'childHome')}
            className="text-white hover:bg-white/20 rounded-lg p-2 transition-colors"
          >
            <ChevronLeft className="w-6 h-6" />
          </button>
          <h1 className={`${textSizeClasses[textSize]} font-bold flex-1`}>
            {t.settings}
          </h1>
        </div>
      </header>
      
      <main className="p-4">
        <div className="max-w-2xl mx-auto space-y-6">
          {/* General Settings */}
          <div className="bg-white rounded-xl shadow-md p-6">
            <h2 className={`${textSizeClasses[textSize]} font-bold mb-4 text-gray-800`}>
              {st.generalSettings}
            </h2>
            
            {/* Language */}
            <div className="mb-6">
              <h3 className={`${textSizeClasses[textSize]} font-semibold mb-3 flex items-center gap-2`}>
                <Globe className="w-5 h-5" />
                {t.language}
              </h3>
              <div className="grid grid-cols-2 gap-3">
                <button
                  onClick={() => setLanguage('el')}
                  className={`${buttonSizeClasses[textSize]} rounded-lg border-2 transition-all ${
                    language === 'el'
                      ? 'bg-blue-500 text-white border-blue-500'
                      : 'bg-white border-gray-300 hover:border-blue-300'
                  }`}
                >
                  🇬🇷 Ελληνικά
                </button>
                <button
                  onClick={() => setLanguage('en')}
                  className={`${buttonSizeClasses[textSize]} rounded-lg border-2 transition-all ${
                    language === 'en'
                      ? 'bg-blue-500 text-white border-blue-500'
                      : 'bg-white border-gray-300 hover:border-blue-300'
                  }`}
                >
                  🇬🇧 English
                </button>
              </div>
            </div>
            
            {/* Notifications */}
            <div className="mb-6">
              <div className="flex items-center justify-between">
                <h3 className={`${textSizeClasses[textSize]} font-semibold flex items-center gap-2`}>
                  <Bell className="w-5 h-5" />
                  {st.notifications}
                </h3>
                <button
                  onClick={() => setNotifications(!notifications)}
                  className={`w-16 h-8 rounded-full transition-colors ${
                    notifications ? 'bg-green-500' : 'bg-gray-300'
                  }`}
                >
                  <div className={`w-6 h-6 bg-white rounded-full shadow-md transform transition-transform ${
                    notifications ? 'translate-x-8' : 'translate-x-1'
                  }`} />
                </button>
              </div>
            </div>
            
            {/* Dark Mode */}
            <div className="mb-6">
              <div className="flex items-center justify-between">
                <h3 className={`${textSizeClasses[textSize]} font-semibold flex items-center gap-2`}>
                  <Moon className="w-5 h-5" />
                  {st.darkMode}
                </h3>
                <button
                  onClick={() => setDarkMode(!darkMode)}
                  className={`w-16 h-8 rounded-full transition-colors ${
                    darkMode ? 'bg-blue-500' : 'bg-gray-300'
                  }`}
                >
                  <div className={`w-6 h-6 bg-white rounded-full shadow-md transform transition-transform ${
                    darkMode ? 'translate-x-8' : 'translate-x-1'
                  }`} />
                </button>
              </div>
            </div>
          </div>
          
          {/* Accessibility */}
          <div className="bg-white rounded-xl shadow-md p-6">
            <h2 className={`${textSizeClasses[textSize]} font-bold mb-4 text-gray-800`}>
              {st.accessibility}
            </h2>
            
            {/* Text Size */}
            <div className="mb-6">
              <h3 className={`${textSizeClasses[textSize]} font-semibold mb-3 flex items-center gap-2`}>
                <Type className="w-5 h-5" />
                {t.textSize}
              </h3>
              <div className="grid grid-cols-3 gap-3">
                {['small', 'medium', 'large'].map(size => (
                  <button
                    key={size}
                    onClick={() => setTextSize(size)}
                    className={`${buttonSizeClasses[size]} rounded-lg border-2 transition-all ${
                      textSize === size
                        ? 'bg-blue-500 text-white border-blue-500'
                        : 'bg-white border-gray-300 hover:border-blue-300'
                    }`}
                  >
                    {t[size]}
                  </button>
                ))}
              </div>
            </div>
            
            {/* Volume */}
            <div className="mb-6">
              <h3 className={`${textSizeClasses[textSize]} font-semibold mb-3 flex items-center gap-2`}>
                <Volume2 className="w-5 h-5" />
                {st.volume}: {volume}%
              </h3>
              <input
                type="range"
                min="0"
                max="100"
                value={volume}
                onChange={(e) => setVolume(e.target.value)}
                className="w-full h-3 bg-gray-200 rounded-lg appearance-none cursor-pointer"
              />
            </div>
            
            {/* Large Buttons */}
            <div className="mb-6">
              <div className="flex items-center justify-between">
                <h3 className={`${textSizeClasses[textSize]} font-semibold flex items-center gap-2`}>
                  {st.largeButtons}
                </h3>
                <button
                  onClick={() => setLargeButtons(!largeButtons)}
                  className={`w-16 h-8 rounded-full transition-colors ${
                    largeButtons ? 'bg-green-500' : 'bg-gray-300'
                  }`}
                >
                  <div className={`w-6 h-6 bg-white rounded-full shadow-md transform transition-transform ${
                    largeButtons ? 'translate-x-8' : 'translate-x-1'
                  }`} />
                </button>
              </div>
            </div>
            
            {/* Auto Answer */}
            <div>
              <div className="flex items-center justify-between">
                <h3 className={`${textSizeClasses[textSize]} font-semibold flex items-center gap-2`}>
                  <Phone className="w-5 h-5" />
                  {st.autoAnswer}
                </h3>
                <button
                  onClick={() => setAutoAnswer(!autoAnswer)}
                  className={`w-16 h-8 rounded-full transition-colors ${
                    autoAnswer ? 'bg-green-500' : 'bg-gray-300'
                  }`}
                >
                  <div className={`w-6 h-6 bg-white rounded-full shadow-md transform transition-transform ${
                    autoAnswer ? 'translate-x-8' : 'translate-x-1'
                  }`} />
                </button>
              </div>
            </div>
          </div>
          
          {/* System Info */}
          <div className="bg-white rounded-xl shadow-md p-6">
            <h2 className={`${textSizeClasses[textSize]} font-bold mb-4 text-gray-800`}>
              {language === 'el' ? 'Πληροφορίες Συστήματος' : 'System Information'}
            </h2>
            
            <div className="space-y-4">
             <div className="flex items-center justify-between">
               <span className="flex items-center gap-2">
                 <Wifi className="w-5 h-5 text-green-500" />
                 {st.networkStatus}
               </span>
               <span className="text-green-600 font-semibold">
                 {language === 'el' ? 'Συνδεδεμένο' : 'Connected'}
               </span>
             </div>
             
             <div className="flex items-center justify-between">
               <span className="flex items-center gap-2">
                 <Battery className="w-5 h-5 text-green-500" />
                 {st.batteryLevel}
               </span>
               <span className="text-green-600 font-semibold">85%</span>
             </div>
             
             <div className="flex items-center justify-between">
               <span>{st.version}</span>
               <span className="text-gray-600">1.0.0</span>
             </div>
           </div>
         </div>
         
         {/* Emergency & Help */}
         <div className="bg-white rounded-xl shadow-md p-6">
           <h2 className={`${textSizeClasses[textSize]} font-bold mb-4 text-gray-800`}>
             {st.help}
           </h2>
           
           <div className="space-y-3">
             <button className={`w-full ${buttonSizeClasses[textSize]} bg-red-500 text-white rounded-lg hover:bg-red-600 transition-colors flex items-center justify-center gap-2`}>
               <Phone className="w-5 h-5" />
               {st.emergencyContacts}
             </button>
             
             <button className={`w-full ${buttonSizeClasses[textSize]} bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors flex items-center justify-center gap-2`}>
               <Shield className="w-5 h-5" />
               {st.privacy}
             </button>
             
             <button className={`w-full ${buttonSizeClasses[textSize]} bg-gray-500 text-white rounded-lg hover:bg-gray-600 transition-colors`}>
               {st.contact}
             </button>
           </div>
         </div>
       </div>
     </main>
   </div>
 );
};

export default Settings;
