import React, { useState } from 'react';
import { ChevronLeft, Trophy, Gift, Star, Crown, Zap, Heart, Lock, Check } from 'lucide-react';

const RewardsCenter = ({ 
  setCurrentView, 
  userRole,
  gameScore,
  setGameScore,
  completedLessons,
  unlockedFeatures,
  setUnlockedFeatures,
  t, 
  textSizeClasses, 
  buttonSizeClasses,
  textSize
}) => {
  const [claimedRewards, setClaimedRewards] = useState([]);

  const rewards = [
    {
      id: 'avatar_pack_1',
      name: 'Πακέτο Avatar "Κλασικό"',
      description: 'Ξεκλειδώστε 3 νέα avatars: Χαρούμενος Παππούς, Κουλ Γιαγιά, Σοφός Παππούς',
      cost: 150,
      icon: '👥',
      type: 'avatar_pack',
      category: 'Avatars'
    },
    {
      id: 'accessory_pack_1',
      name: 'Πακέτο Αξεσουάρ "Βασικό"',
      description: 'Πάρτε καπέλο, γυαλιά και λουλούδι δωρεάν!',
      cost: 80,
      icon: '🎩',
      type: 'accessory_pack',
      category: 'Αξεσουάρ'
    },
    {
      id: 'larger_text',
      name: 'Μεγαλύτερα Γράμματα',
      description: 'Ξεκλειδώστε ακόμα μεγαλύτερη γραμματοσειρά για καλύτερη ανάγνωση',
      cost: 100,
      icon: '🔤',
      type: 'feature',
      category: 'Χαρακτηριστικά'
    },
    {
      id: 'voice_commands',
      name: 'Φωνητικές Εντολές',
      description: 'Χρησιμοποιήστε τη φωνή σας για να πλοηγηθείτε στην εφαρμογή',
      cost: 200,
      icon: '🎤',
      type: 'feature',
      category: 'Χαρακτηριστικά'
    },
    {
      id: 'themes_pack',
      name: 'Πακέτο Θεμάτων',
      description: 'Αλλάξτε τα χρώματα της εφαρμογής: Θάλασσα, Φύση, Νύχτα',
      cost: 75,
      icon: '🎨',
      type: 'theme_pack',
      category: 'Εμφάνιση'
    },
    {
      id: 'premium_lessons',
      name: 'Premium Μαθήματα',
      description: 'Πρόσβαση σε 10 επιπλέον μαθήματα για προχωρημένους',
      cost: 300,
      icon: '📚',
      type: 'feature',
      category: 'Μαθήματα'
    },
    {
      id: 'family_sharing',
      name: 'Οικογενειακή Κοινοποίηση',
      description: 'Μοιραστείτε την πρόοδό σας με όλη την οικογένεια',
      cost: 120,
      icon: '👨‍👩‍👧‍👦',
      type: 'feature',
      category: 'Κοινωνικά'
    },
    {
      id: 'celebration_effects',
      name: 'Εφέ Γιορτής',
      description: 'Πυροτεχνήματα και κομφετί όταν ολοκληρώνετε μαθήματα!',
      cost: 50,
      icon: '🎉',
      type: 'feature',
      category: 'Εφέ'
    }
  ];

  const achievements = [
    {
      id: 'first_lesson',
      name: 'Πρώτο Μάθημα',
      description: 'Ολοκληρώστε το πρώτο σας μάθημα',
      reward: 20,
      icon: '🎯',
      completed: completedLessons.length >= 1
    },
    {
      id: 'three_lessons',
      name: 'Τρία Μαθήματα',
      description: 'Ολοκληρώστε 3 μαθήματα',
      reward: 50,
      icon: '🏆',
      completed: completedLessons.length >= 3
    },
    {
      id: 'all_lessons',
      name: 'Ειδικός Τεχνολογίας',
      description: 'Ολοκληρώστε όλα τα μαθήματα',
      reward: 100,
      icon: '👑',
      completed: completedLessons.length >= 5
    },
    {
      id: 'daily_user',
      name: 'Καθημερινός Χρήστης',
      description: 'Χρησιμοποιήστε την εφαρμογή για 7 μέρες',
      reward: 75,
      icon: '📅',
      completed: true // Simulated
    }
  ];

  const canAfford = (cost) => gameScore >= cost;
  const isUnlocked = (rewardId) => unlockedFeatures.includes(rewardId);
  const isClaimed = (rewardId) => claimedRewards.includes(rewardId);

  const claimReward = (reward) => {
    if (canAfford(reward.cost) && !isClaimed(reward.id)) {
      setGameScore(prev => prev - reward.cost);
      setClaimedRewards(prev => [...prev, reward.id]);
      setUnlockedFeatures(prev => [...prev, reward.id]);
      
      // Show success message
      alert(`🎉 Κερδίσατε: ${reward.name}!`);
    }
  };

  const claimAchievement = (achievement) => {
    if (achievement.completed && !isClaimed(achievement.id)) {
      setGameScore(prev => prev + achievement.reward);
      setClaimedRewards(prev => [...prev, achievement.id]);
      alert(`🏆 Νέο επίτευγμα: ${achievement.name}! +${achievement.reward} πόντοι!`);
    }
  };

  const getProgressPercentage = () => {
    return Math.min((gameScore / 500) * 100, 100);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-yellow-50 to-orange-50">
      <header className="bg-gradient-to-r from-yellow-500 to-orange-500 text-white p-6 shadow-lg">
        <div className="flex items-center gap-4">
          <button
            onClick={() => setCurrentView(userRole === 'grandparent' ? 'grandparentHome' : 'childHome')}
            className="text-white hover:bg-white/20 rounded-xl p-3 transition-colors"
          >
            <ChevronLeft className="w-8 h-8" />
          </button>
          <div className="flex-1">
            <h1 className={`${textSizeClasses[textSize]} font-bold`}>
              🏆 Κέντρο Ανταμοιβών
            </h1>
            <div className="flex items-center gap-4 mt-2">
              <div className="flex items-center gap-2">
                <Trophy className="w-5 h-5 text-yellow-300" />
                <span className="text-yellow-300 font-semibold text-xl">{gameScore} πόντοι</span>
              </div>
            </div>
          </div>
        </div>
        
        {/* Progress Bar */}
        <div className="mt-4">
          <div className="flex justify-between text-sm mb-1">
            <span>Πρόοδος προς επόμενο επίπεδο</span>
            <span>{Math.floor(getProgressPercentage())}%</span>
          </div>
          <div className="w-full bg-white bg-opacity-30 rounded-full h-3">
            <div 
              className="bg-yellow-300 h-3 rounded-full transition-all duration-500"
              style={{ width: `${getProgressPercentage()}%` }}
            ></div>
          </div>
        </div>
      </header>

      <main className="p-4">
        <div className="max-w-4xl mx-auto space-y-8">
          {/* Achievements Section */}
          <section>
            <h2 className={`${textSizeClasses[textSize]} font-bold text-gray-800 mb-4 flex items-center gap-2`}>
              🏅 Επιτεύγματα
            </h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {achievements.map(achievement => (
                <div
                  key={achievement.id}
                  className={`bg-white rounded-2xl p-6 shadow-md border-2 ${
                    achievement.completed && !isClaimed(achievement.id)
                      ? 'border-green-400 bg-green-50'
                      : isClaimed(achievement.id)
                      ? 'border-blue-400 bg-blue-50'
                      : 'border-gray-200'
                  }`}
                >
                  <div className="flex items-center gap-4">
                    <div className={`text-4xl ${!achievement.completed ? 'grayscale' : ''}`}>
                      {achievement.icon}
                    </div>
                    <div className="flex-1">
                      <h3 className={`${textSizeClasses[textSize]} font-bold text-gray-800`}>
                        {achievement.name}
                      </h3>
                      <p className="text-gray-600 mt-1">{achievement.description}</p>
                      <div className="mt-2">
                        {isClaimed(achievement.id) ? (
                          <span className="text-blue-600 font-semibold flex items-center gap-1">
                            <Check className="w-4 h-4" />
                            Κερδήθηκε!
                          </span>
                        ) : achievement.completed ? (
                          <button
                            onClick={() => claimAchievement(achievement)}
                            className="bg-green-500 text-white px-4 py-2 rounded-lg font-semibold hover:bg-green-600 transition-colors"
                          >
                            Κέρδισε +{achievement.reward} πόντους!
                          </button>
                        ) : (
                          <span className="text-gray-500 flex items-center gap-1">
                            <Lock className="w-4 h-4" />
                            Κλειδωμένο
                          </span>
                        )}
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </section>

          {/* Rewards Shop */}
          <section>
            <h2 className={`${textSizeClasses[textSize]} font-bold text-gray-800 mb-4 flex items-center gap-2`}>
              🛍️ Κατάστημα Ανταμοιβών
            </h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {rewards.map(reward => {
                const affordable = canAfford(reward.cost);
                const claimed = isClaimed(reward.id);
                
                return (
                  <div
                    key={reward.id}
                    className={`bg-white rounded-2xl p-6 shadow-md transition-all ${
                      claimed
                        ? 'border-2 border-green-400 bg-green-50'
                        : affordable
                        ? 'hover:shadow-lg transform hover:scale-105'
                        : 'opacity-75'
                    }`}
                  >
                    <div className="text-center">
                      <div className="text-5xl mb-3">{reward.icon}</div>
                      <h3 className={`${textSizeClasses[textSize]} font-bold text-gray-800 mb-2`}>
                        {reward.name}
                      </h3>
                      <p className="text-gray-600 text-sm mb-4">{reward.description}</p>
                      
                      <div className="space-y-2">
                        {claimed ? (
                          <div className="text-green-600 font-bold flex items-center justify-center gap-2">
                            <Check className="w-5 h-5" />
                            Αγοράστηκε!
                          </div>
                        ) : (
                          <>
                            <div className={`font-bold text-lg ${affordable ? 'text-orange-600' : 'text-red-500'}`}>
                              💰 {reward.cost} πόντοι
                            </div>
                            <button
                              onClick={() => claimReward(reward)}
                              disabled={!affordable}
                              className={`w-full py-2 px-4 rounded-lg font-semibold transition-colors ${
                                affordable
                                  ? 'bg-orange-500 text-white hover:bg-orange-600'
                                  : 'bg-gray-300 text-gray-500 cursor-not-allowed'
                              }`}
                            >
                              {affordable ? 'Αγορά' : 'Χρειάζεστε περισσότερους πόντους'}
                            </button>
                          </>
                        )}
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          </section>

          {/* Tips Section */}
          <section className="bg-blue-100 border-2 border-blue-300 rounded-xl p-6">
            <h3 className={`${textSizeClasses[textSize]} font-bold text-blue-800 mb-3 flex items-center gap-2`}>
              💡 Πώς να κερδίσετε πόντους
            </h3>
            <div className="space-y-2 text-blue-700">
              <p>• Ολοκληρώστε μαθήματα: +10-20 πόντοι ανά μάθημα</p>
              <p>• Περάστε κουίζ με άριστα: +20 πόντοι</p>
              <p>• Κερδίστε επιτεύγματα: +20-100 πόντοι</p>
              <p>• Χρησιμοποιήστε την εφαρμογή καθημερινά: +5 πόντοι</p>
              <p>• Στείλτε μήνυμα στην οικογένεια: +2 πόντοι</p>
            </div>
          </section>
        </div>
      </main>
    </div>
  );
};

export default RewardsCenter;
