import React, { useState } from 'react';
import { ChevronLeft, Globe, Type, Bell, Shield, Moon, Volume2, Wifi, Battery, Phone, HelpCircle, HeadphonesIcon, MessageCircle } from 'lucide-react';

const Settings = ({ 
  language, 
  setLanguage, 
  textSize, 
  setTextSize, 
  setCurrentView, 
  userRole, 
  t, 
  textSizeClasses, 
  buttonSizeClasses 
}) => {
  const [notifications, setNotifications] = useState(true);
  const [darkMode, setDarkMode] = useState(false);
  const [volume, setVolume] = useState(70);
  const [autoAnswer, setAutoAnswer] = useState(false);
  const [largeButtons, setLargeButtons] = useState(true);
  
  const settingsTranslations = {
    el: {
      generalSettings: "Î“ÎµÎ½Î¹ÎºÎ­Ï‚ Î¡Ï…Î¸Î¼Î¯ÏƒÎµÎ¹Ï‚",
      accessibility: "Î ÏÎ¿ÏƒÎ²Î±ÏƒÎ¹Î¼ÏŒÏ„Î·Ï„Î±",
      privacy: "Î‘Ï€ÏŒÏÏÎ·Ï„Î¿ & Î‘ÏƒÏ†Î¬Î»ÎµÎ¹Î±",
      notifications: "Î•Î¹Î´Î¿Ï€Î¿Î¹Î®ÏƒÎµÎ¹Ï‚",
      darkMode: "Î£ÎºÎ¿Ï„ÎµÎ¹Î½Î® Î»ÎµÎ¹Ï„Î¿Ï…ÏÎ³Î¯Î±",
      volume: "ÎˆÎ½Ï„Î±ÏƒÎ· Î®Ï‡Î¿Ï…",
      autoAnswer: "Î‘Ï…Ï„ÏŒÎ¼Î±Ï„Î· Î±Ï€Î¬Î½Ï„Î·ÏƒÎ· ÎºÎ»Î®ÏƒÎµÏ‰Î½",
      largeButtons: "ÎœÎµÎ³Î¬Î»Î± ÎºÎ¿Ï…Î¼Ï€Î¹Î¬",
      networkStatus: "ÎšÎ±Ï„Î¬ÏƒÏ„Î±ÏƒÎ· Î´Î¹ÎºÏ„ÏÎ¿Ï…",
      batteryLevel: "Î•Ï€Î¯Ï€ÎµÎ´Î¿ Î¼Ï€Î±Ï„Î±ÏÎ¯Î±Ï‚",
      emergencyContacts: "Î•Ï€Î±Ï†Î­Ï‚ Î­ÎºÏ„Î±ÎºÏ„Î·Ï‚ Î±Î½Î¬Î³ÎºÎ·Ï‚",
      help: "Î’Î¿Î®Î¸ÎµÎ¹Î± & Î¥Ï€Î¿ÏƒÏ„Î®ÏÎ¹Î¾Î·",
      about: "Î£Ï‡ÎµÏ„Î¹ÎºÎ¬ Î¼Îµ Ï„Î·Î½ ÎµÏ†Î±ÏÎ¼Î¿Î³Î®",
      version: "ÎˆÎºÎ´Î¿ÏƒÎ·",
      contact: "Î•Ï€Î¹ÎºÎ¿Î¹Î½Ï‰Î½Î¯Î±",
      tutorials: "ÎŸÎ´Î·Î³Î¿Î¯ Î§ÏÎ®ÏƒÎ·Ï‚",
      liveChatSupport: "Î–Ï‰Î½Ï„Î±Î½Î® Î¥Ï€Î¿ÏƒÏ„Î®ÏÎ¹Î¾Î·"
    },
    en: {
      generalSettings: "General Settings",
      accessibility: "Accessibility",
      privacy: "Privacy & Security",
      notifications: "Notifications",
      darkMode: "Dark mode",
      volume: "Volume",
